USE `osdms`;

/*Table structure for table `t_specification_chapter` */

DROP TABLE IF EXISTS `t_specification_chapter`;

CREATE TABLE `t_specification_chapter` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `name` varchar(100) DEFAULT NULL,
  `parent_id` bigint(20) DEFAULT NULL,
  `tree_index` varchar(100) DEFAULT NULL,
  `sort` int(11) DEFAULT '0',
  `is_leaf` tinyint(1) DEFAULT '0',
  `state` tinyint(1) DEFAULT NULL,
  `content` text,
  `version` int(11) DEFAULT '0',
  `create_by` varchar(50) DEFAULT NULL,
  `create_time` datetime DEFAULT NULL,
  `last_modify_time` datetime DEFAULT NULL,
  `last_modify_by` varchar(50) DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `fk_reference_54` (`parent_id`),
  CONSTRAINT `fk_reference_54` FOREIGN KEY (`parent_id`) REFERENCES `t_specification_chapter` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=849 DEFAULT CHARSET=utf8;

/*Data for the table `t_specification_chapter` */

insert  into `t_specification_chapter`(`id`,`name`,`parent_id`,`tree_index`,`sort`,`is_leaf`,`state`,`content`,`version`,`create_by`,`create_time`,`last_modify_time`,`last_modify_by`) values (1,'全部分类',NULL,'0001',0,0,NULL,'',0,'admin','2012-06-13 17:09:06','2012-06-13 17:09:50','admin'),(2,'煤矿安全规程',1,'0001-0001',0,0,NULL,'',5,'admin','2012-06-13 17:09:06','2012-06-13 17:09:50','admin'),(3,'煤矿安全技术操作规程',1,'0001-0002',0,0,NULL,'',0,'admin','2012-06-13 17:09:06','2012-06-13 17:09:50','admin'),(4,'第一编 总则',2,'0001-0001-0001',0,0,NULL,'',14,'admin','2012-06-13 17:09:06','2012-08-10 16:20:43','系统'),(5,'第二编 井工部分',2,'0001-0001-0002',0,0,NULL,'',10,'admin','2012-06-13 17:09:06','2012-08-10 16:20:43','系统'),(6,'第三编 露天部分',2,'0001-0001-0003',0,0,NULL,'',10,'admin','2012-06-13 17:09:06','2012-08-10 16:20:43','系统'),(7,'第四编 职业危害',2,'0001-0001-0004',0,0,NULL,'',10,'admin','2012-06-13 17:09:06','2012-08-10 16:20:43','系统'),(8,'（2006）',2,'0001-0001-0005',0,1,NULL,'',10,'admin','2012-06-13 17:09:06','2012-08-10 16:20:43','系统'),(9,'（2009）',2,'0001-0001-0006',0,1,NULL,'',10,'admin','2012-06-13 17:09:06','2012-08-10 16:20:43','系统'),(10,'（2010）',2,'0001-0001-0007',0,1,NULL,'',10,'admin','2012-06-13 17:09:06','2012-08-10 16:20:43','系统'),(11,'第一条',4,'0001-0001-0001-0001',0,1,NULL,'为保障煤矿安全生产和职工人身安全，防止煤矿事故，根据《煤炭法》、《矿山安全法》和《煤矿安全监察条例》，制定本规程。',9,'admin','2012-06-13 17:09:06','2012-08-10 16:28:29','系统'),(12,'第二条',4,'0001-0001-0001-0002',0,1,NULL,'在中华人民共和国领域从事煤炭生产和煤矿建设活动，必须遵守本规程。',8,'admin','2012-06-13 17:09:06','2012-08-10 16:28:29','系统'),(13,'第三条',4,'0001-0001-0001-0003',0,1,NULL,'煤矿企业必须遵守国家有关安全生产的法律、法规、规章、规程、标准和技术规范。\r\n煤矿企业必须建立、健全各级领导安全生产责任制、职能机构安全生产责任制、岗位人员安全生产责任制。\r\n煤矿企业应建立、健全安全目标管理制度、安全奖惩制度、安全技术措施审批制度、安全隐患排查制度、安全检查制度、安全办公会议等制度。\r\n煤矿企业必须建立各种设备、设施检查维修制度，定期进行检查维修，并做好记录。\r\n',8,'admin','2012-06-13 17:09:06','2012-08-10 16:28:29','系统'),(14,'第四条',4,'0001-0001-0001-0004',0,1,NULL,'煤矿企业必须设置安全生产机构，配备适应工作需要的安全生产人员和装备。',8,'admin','2012-06-13 17:09:06','2012-08-10 16:28:29','系统'),(15,'第五条',4,'0001-0001-0001-0005',0,1,NULL,'煤矿安全工作必须实行群众监督。煤矿企业必须支持群众安全监督组织的活动，发挥职工群众安全监督作用。\r\n职工有权制止违章作业，拒绝违章指挥；当工作地点出现险情时，有权立即停止作业，撤到安全地点；当险情没有得到处理不能保证人身安全时，有权拒绝作业。\r\n',8,'admin','2012-06-13 17:09:06','2012-08-10 16:28:29','系统'),(16,'第六条',4,'0001-0001-0001-0006',0,1,NULL,'煤矿企业必须对职工进行安全培训。未经安全培训的，不得上岗作业。\r\n矿务局（公司）局长（经理）、矿长必须具备安全专业知识，具有领导安全生产和处理煤矿事故的能力，并经依法培训合格，取得安全任职资格证书。\r\n特种作业人员必须按国家有关规定培训合格，取得操作资格证书。',8,'admin','2012-06-13 17:09:06','2012-08-10 16:28:29','系统'),(17,'第七条',4,'0001-0001-0001-0007',0,1,NULL,'煤矿使用的涉及安全生产的产品，必须取得煤矿矿用产品安全标志。未取得煤矿矿用产品安全标志的，不得使用。\r\n试验涉及安全生产的新技术、新工艺、新设备、新材料前，必须经过论证、安全性能检验和鉴定，并制定安全措施。\r\n',8,'admin','2012-06-13 17:09:06','2012-08-10 16:28:29','系统'),(18,'第八条',4,'0001-0001-0001-0008',0,1,NULL,'煤矿企业在编制生产建设长远发展规划和年度生产建设计划时，必须编制安全技术发展规划和安全技术措施计划。安全技术措施所需费用、材料和设备等必须列入企业财务、供应计划。',8,'admin','2012-06-13 17:09:06','2012-08-10 16:28:29','系统'),(19,'第九条',4,'0001-0001-0001-0009',0,1,NULL,'煤矿企业必须编制年度灾害预防和处理计划，并根据具体情况及时修改。灾害预防和处理计划由矿长负责组织实施。\r\n煤矿企业每年必须至少组织1次矿井救灾演习。',8,'admin','2012-06-13 17:09:06','2012-08-10 16:28:29','系统'),(20,'第十条',4,'0001-0001-0001-0010',0,1,NULL,'入井人员必须戴安全帽、随身携带自救器和矿灯，严禁携带烟草和点火物品，严禁穿化纤衣服，入井前严禁喝酒。\r\n煤矿企业必须建立入井检身制度和出入井人员清点制度。',8,'admin','2012-06-13 17:09:06','2012-08-10 16:28:29','系统'),(21,'第十一条',4,'0001-0001-0001-0011',0,1,NULL,'煤矿企业应有创伤急救系统为其服务。创伤急救系统应配备救护车辆、急救器材、急救装备和药品等。',8,'admin','2012-06-13 17:09:06','2012-08-10 16:28:29','系统'),(22,'第十二条',4,'0001-0001-0001-0012',0,1,NULL,'井工煤矿必须及时填绘反映实际情况的下列图纸：\r\n（一）矿井地质和水文地质图。\r\n（二）井上、下对照图。\r\n（三）巷道布置图。\r\n（四）采掘工程平面图。\r\n（五）通风系统图。\r\n（六）井下运输系统图。\r\n（七）安全监测装备布置图。\r\n（八）排水、防尘、防火注浆、压风、充填、抽放瓦斯等管路系统图。\r\n（九）井下通信系统图。\r\n（十）井上、下配电系统图和井下电气设备布置图。\r\n（十一）井下避灾路线图。',8,'admin','2012-06-13 17:09:06','2012-08-10 16:28:29','系统'),(23,'第十三条',4,'0001-0001-0001-0013',0,1,NULL,'露天煤矿必须及时填绘反映实际情况的下列图纸：\r\n（一）地形地质图。\r\n（二）工程地质平面图、断面图，综合水文地质平面图。\r\n（三）采剥工程平面图、断面图。\r\n（四）排土工程平面图。\r\n（五）运输系统图。\r\n（六）输配电系统图。\r\n（七）通信系统图。\r\n（八）防排水系统及排水设备布置图。\r\n（九）边坡监测系统平面图、断面图。\r\n（十）井工老空与露天矿平面对照图。',8,'admin','2012-06-13 17:09:06','2012-08-10 16:28:29','系统'),(24,'第十四条',4,'0001-0001-0001-0014',0,1,NULL,'煤矿发生事故后，煤矿企业主要负责人和技术负责人必须立即采取措施组织抢救，矿长负责抢救指挥，并按有关规定及时上报。',8,'admin','2012-06-13 17:09:06','2012-08-10 16:28:29','系统'),(25,'第一章',5,'0001-0001-0002-0001',0,0,NULL,NULL,9,'admin','2012-06-13 17:09:06','2012-09-01 17:34:26','admin'),(26,'第一节',25,'0001-0001-0002-0001-0001',0,0,NULL,NULL,6,'admin','2012-06-13 17:09:06','2012-09-01 17:36:54','admin'),(27,'第十五条',26,'0001-0001-0002-0001-0001-0001',0,1,NULL,'单项工程、单位工程开工前，必须编制施工组织设计和作业规程，并组织每个工作人员学习。',0,'admin','2012-06-13 17:09:06','2012-06-13 17:09:50','admin'),(28,'第十六条',26,'0001-0001-0002-0001-0001-0002',0,1,NULL,'开凿平硐、斜井和立井时，自井口到坚硬岩层之间的井巷必须砌碹，并向坚硬岩层内至少延深5m。\r\n在山坡下开凿斜井和平硐时，井口顶、侧必须构筑挡墙和防洪水沟。',0,'admin','2012-06-13 17:09:06','2012-06-13 17:09:50','admin'),(29,'第十七条',26,'0001-0001-0002-0001-0001-0001',0,1,NULL,'掘进井巷和硐室时，必须采取湿式钻眼、冲洗井壁巷帮、水炮泥、爆破喷雾、装岩（煤）洒水和净化风流等综合防尘措施。\r\n冻结法凿井和在遇水膨胀的岩层中掘进不能采用湿式钻眼时，可采用干式钻眼，但必须采取捕尘措施，并使用个体防尘保护用品。',0,'admin','2012-06-13 17:09:06','2012-06-13 17:09:50','admin'),(30,'第十八条',26,'0001-0001-0002-0001-0001-0001',0,1,NULL,'每个生产矿井必须至少有2个能行人的通达地面的安全出口，各个出口间的距离不得小于30m。\r\n采用中央式通风系统的新建和改扩建矿井，设计中应规定井田边界附近的安全出口。当井田一翼走向较长、矿井发生灾害不能保证人员安全撤出时，必须掘出井田边界附近的安全出口。\r\n井下每一个水平到上一个水平和各个采区都必须至少有2个便于行人的安全出口，并与通达地面的安全出口相连接。未建成2个安全出口的水平或采区严禁生产。\r\n井巷交岔点，必须设置路标，标明所在地点，指明通往安全出口的方向。井下工作人员必须熟悉通往安全出口的路线。\r\n',0,'admin','2012-06-13 17:09:06','2012-06-13 17:09:50','admin'),(31,'第十九条',26,'0001-0001-0002-0001-0001-0001',0,1,NULL,'对于通达地面的安全出口和2个水平之间的安全出口，倾角等于或小于45°时，必须设置人行道，并根据倾角大小和实际需要设置扶手、台阶或梯道。倾角大于45°时，必须设置梯道间或梯子间，斜井梯道间必须分段错开设置，每段斜长不得大于10m；立井梯子间中的梯子角度不得大于80°，相邻2个平台的垂直距离不得大于8m。\r\n安全出口应经常清理、维护，保持畅通。',0,'admin','2012-06-13 17:09:06','2012-06-13 17:09:50','admin'),(32,'第二十条',26,'0001-0001-0002-0001-0001-0001',0,1,NULL,'主要绞车道不得兼作人行道。提升量不大，保证行车时不行人的，不受此限。',0,'admin','2012-06-13 17:09:06','2012-06-13 17:09:50','admin'),(33,'第二十一条',26,'0001-0001-0002-0001-0001-0001',0,1,NULL,'巷道净断面必须满足行人、运输、通风和安全设施及设备安装、检修、施工的需要，并符合下列要求：\r\n（一）主要运输巷和主要风巷的净高，自轨面起不得低于2m。架线电机车运输巷的净高必须符合本规程第三百五十六条和第三百五十七条的有关要求。\r\n（二）采区（包括盘区，以下各条同）内的上山、下山和平巷的净高不得低于2m，薄煤层内的不得低于1.8m。\r\n采煤工作面运输巷、回风巷及采区内的溜煤眼等的净断面或净高，由煤矿企业统一规定。\r\n巷道净断面的设计，必须按支护最大允许变形后的断面计算。\r\n',0,'admin','2012-06-13 17:09:06','2012-06-13 17:09:50','admin'),(34,'第二十二条',26,'0001-0001-0002-0001-0001-0001',0,1,NULL,'运输巷两侧（包括管、线、电缆）与运输设备最突出部分之间的距离，应符合下列要求：\r\n（一）新建矿井、生产矿井新掘运输巷的一侧，从巷道道碴面起1.6m的高度内，必须留有宽0.8m（综合机械化采煤矿井为1m）以上的人行道，管道吊挂高度不得低于1.8m；巷道另一侧的宽度不得小于0.3m（综合机械化采煤矿井为0.5m）。巷道内安设输送机时，输送机与巷帮支护的距离不得小于0.5m；输送机机头和机尾处与巷帮支护的距离应满足设备检查和维修的需要，并不得小于0.7m。巷道内移动变电站或平板车上综采设备的最突出部分，与巷帮支护的距离不得小于0.3m。\r\n（二）生产矿井已有巷道人行道的宽度不符合本条第一款第（一）项的要求时，必须在巷道的一侧设置躲避硐，2个躲避硐之间的距离不得超过40m。躲避硐宽度不得小于1.2m，深度不得小于0.7m，高度不得小于1.8m，躲避硐内严禁堆积物料。\r\n（三）在人车停车地点的巷道上下人侧，从巷道道碴面起1.6m的高度内，必须留有宽1m以上的人行道，管道吊挂高度不得低于1.8m。\r\n',0,'admin','2012-06-13 17:09:06','2012-06-13 17:09:50','admin'),(35,'第二十三条',26,'0001-0001-0002-0001-0001-0001',0,1,NULL,'在双轨运输巷中，2列列车最突出部分之间的距离，对开时不得小于0.2m，采区装载点不得小于0.7m，矿车摘挂钩地点不得小于1m。车辆最突出部分与巷道两侧距离，必须符合本规程第二十二条的要求。\r\n',0,'admin','2012-06-13 17:09:06','2012-06-13 17:09:50','admin'),(36,'第二十四条',26,'0001-0001-0002-0001-0001-0001',0,1,NULL,'采区结束回撤设备时，必须编制专门措施，加强通风、瓦斯、防火管理。',0,'admin','2012-06-13 17:09:06','2012-06-13 17:09:50','admin'),(37,'第二节',25,'0001-0001-0002-0001-0002',0,0,NULL,NULL,7,'admin','2012-06-13 17:09:06','2012-09-01 17:36:54','admin'),(38,'第二十五条',37,'0001-0001-0002-0001-0002-0001',0,1,NULL,'凿井期间，井口工作范围必须用栅栏围住，人员进出地点必须安装栅栏门；井口必须设置封口盘和井盖门，井盖门的两端必须安装栅栏，封口盘和井盖门必须坚固严密，并采用不燃性材料。\r\n',4,'admin','2012-06-13 17:09:06','2012-09-01 17:38:48','admin'),(39,'第二十六条',37,'0001-0001-0002-0001-0002-0002',0,1,NULL,'采用普通凿井法施工时，立井的永久或临时支护到井筒工作面的距离及防止片帮的措施必须根据岩性、水文地质条件和施工工艺在作业规程中明确规定。\r\n',4,'admin','2012-06-13 17:09:06','2012-09-01 17:38:48','admin'),(40,'第二十七条',37,'0001-0001-0002-0001-0002-0003',0,1,NULL,'立井井筒穿过表土层、砂层、松软岩层或煤层时，必须有专门措施。采用井圈或其他临时支护时，临时支护必须安全可靠、紧靠工作面，并及时进行永久支护。在建立永久支护前，每班应派专人观测地面沉降和临时支护后面的井帮变化情况；发现危险预兆时，必须立即停止工作，撤出人员，进行处理。\r\n',4,'admin','2012-06-13 17:09:06','2012-09-01 17:38:48','admin'),(41,'第二十八条',37,'0001-0001-0002-0001-0002-0004',0,1,NULL,'立井永久支护的质量必须符合设计要求。岩帮与支护之间必须填满灌实。井壁出水时必须采取导水或堵水等措施。\r\n',4,'admin','2012-06-13 17:09:06','2012-09-01 17:38:48','admin'),(42,'第二十九条',37,'0001-0001-0002-0001-0002-0005',0,1,NULL,'采用钻井法开凿立井井筒必须遵守下列规定：\r\n（一）钻井的设计与施工最终位置必须通过风化带，并向不透水的稳定基岩至少延深5m。\r\n（二）钻井期间，采用封口平台时，必须将井口封盖严密；采用井口梁时，必须有可靠的防坠措施。\r\n（三）钻井过程中，护壁泥浆的各项参数必须定时测定，发现问题立即调整。井筒内的泥浆面，必须保持高于地下静止水位。\r\n（四）钻井时必须测定井筒的偏斜度。偏斜超过规定时，必须及时纠正。井筒偏斜度及测点的间距必须在施工组织设计中明确规定。钻井完毕后，必须绘制井筒的纵横剖面图，井筒中心线和截面必须符合设计要求。\r\n（五）预制井壁的质量，必须逐节检查鉴定。井壁连接部位必须有可靠的防蚀、防水措施，合格后方可下沉井壁。\r\n（六）井壁下沉完成后，必须检查井壁偏斜度，只有符合要求后方可进行壁后充填，壁后充填必须密实。充填材料必须经过试验，满足强度和凝固时间的要求，并保证能够置换出泥浆。开凿沉井井壁的底部或开掘马头门之前，必须检查破壁处及其上方15～30m范围内壁后的充填质量，发现不合格时，必须采取可靠的补救措施。\r\n（七）开凿沉井井壁的底部和开掘马头门采用爆破作业时，必须制定安全措施。\r\n',4,'admin','2012-06-13 17:09:06','2012-09-01 17:38:48','admin'),(43,'第三十条',37,'0001-0001-0002-0001-0002-0006',0,1,NULL,'采用冻结法开凿立井井筒应遵守下列规定：\r\n（一）冻结深度应穿过风化带延深至稳定的基岩10m以上。基岩段涌水较大时，应加深冻结深度。\r\n（二）钻进冻结孔时，必须测定钻孔的方向和偏斜度，测斜的最大间隔不得超过30m，并绘制冻结孔实际偏斜平面位置图，偏斜度超过规定时，必须及时纠正。因钻孔偏斜影响冻结效果时，必须补孔。\r\n（三）地质检查钻孔不得打在冻结的井筒内。水文观测钻孔偏斜不得超出井筒，深度不得超过冻结段下部隔水层。\r\n（四）冻结管应采用无缝钢管焊接或螺纹连接，冻结管下入钻孔后应进行试漏，发现异常时，必须及时处理。\r\n（五）开始冻结后，必须经常观察水文观测孔的水位变化。只有在水文孔冒水7天、水量正常，确认冻结壁已交圈后，方可进行试挖。冻结和开凿过程中，要经常检查盐水温度和流量、井帮温度和位移，以及井帮和工作面渗漏盐水等情况。检查应有详细记录，发现异常，必须及时处理。\r\n（六）开凿表土层冻结段时，可以采用爆破作业，但必须制定安全技术措施。\r\n（七）掘进施工过程中，必须有防止冻结壁变形、片帮、掉石、断管等安全措施。\r\n（八）生根壁座应设在含水较少的稳定坚硬的基岩中。\r\n（九）只有在永久井壁施工全部完成后，方可停止冻结。\r\n（十）梁窝的设计和施工必须有防止漏水的措施。\r\n（十一）不论冻结管能否提拔回收，对全孔必须及时用水泥砂浆或混凝土全部充满填实。\r\n冻结站必须用不燃性材料建筑，并应有通风装置。应经常测定站内空气中氨气，氨的浓度不得超过0.004％。站内严禁烟火，并必须备有急救和消防器材。\r\n氨瓶和氨罐必须经过试验，合格后方准使用；在运输、使用和存放期间，应有安全措施。\r\n',4,'admin','2012-06-13 17:09:06','2012-09-01 17:38:48','admin'),(44,'第三十一条',37,'0001-0001-0002-0001-0002-0007',0,1,NULL,'立井井筒穿过含水岩层或破碎带，采用地面或工作面预注浆法进行堵水或加固时，应遵守下列规定：\r\n（一）注浆施工前，必须编制注浆工程设计。\r\n（二）注浆段长度必须大于注浆的含水岩层的厚度，并深入不透水岩层或硬岩层5～10m。井底的设计位置在注浆的含水岩层内时，注浆深度必须大于井深10m。\r\n（三）地面预注浆的钻孔，每钻进40m必须测斜1次，钻孔偏斜率不得超过0.5％。\r\n（四）注浆前，必须进行注浆泵和输送管路系统的耐压试验。试验压力必须达到最大注浆压力的1.5倍，试验时间不得小于15min，无异常情况后，方可使用。\r\n（五）注浆过程中，注浆压力突然上升时，必须停止注浆泵运转，卸压后方可处理。\r\n（六）每次注浆后，应至少停歇30min，方可提拔止浆塞，以防高压浆顶出钻杆。\r\n（七）冬季注浆施工时，注浆站和地面输浆管路，必须采取防冻措施。\r\n（八）井筒工作面预注浆前，在注浆的含水岩层上方，必须按设计要求设置止浆岩帽或混凝土止浆垫。含水岩层厚度大，需采用分段注浆和掘砌时，对每一注浆段，必须按设计要求设置止浆岩帽或混凝土止浆垫。岩帽和混凝土止浆垫的结构形式和厚度应根据最大注浆压力、岩石性质和工作条件确定。混凝土止浆垫由井壁支承时，应对井壁强度进行验算。\r\n（九）孔口管必须按设计孔位埋设牢固，并安设高压阀门。注浆前，必须对止浆垫和孔口管进行耐压试验，试验压力必须大于注浆压力1MPa。\r\n（十）钻注浆孔时，钻机必须安设牢固。取芯钻进时，应使用能够防止顶出钻具的钻头；无芯钻进时，可使用三翼钻头，以防承压水顶出钻具。\r\n（十一）井内应设吊泵，及时排除井底积水。当钻进注浆孔时，如井筒涌水量接近吊泵额定排水能力，必须停止钻进，提取钻具，关闭高压阀门，及时注浆。\r\n（十二）注浆站设在地面时，井上、下必须有可靠的通信联系。\r\n（十三）制浆和注浆的工作人员，应佩戴防护眼镜和口罩，水泥搅拌房内应采取防尘措施。\r\n（十四）注浆结束后，必须检查注浆效果，合格后，方可开凿井筒。\r\n',4,'admin','2012-06-13 17:09:06','2012-09-01 17:38:48','admin'),(45,'第三十二条',37,'0001-0001-0002-0001-0002-0008',0,1,NULL,'立井井筒漏水量每小时超过6m3或漏水中含砂，采用井壁注浆堵水时，必须编制施工组织设计并遵守下列规定：\r\n（一）井壁必须有承受最大注浆压力的强度。\r\n（二）井筒在流砂层部位时，注浆孔深度必须小于井壁厚度200mm。井筒采用双层井壁支护时，注浆孔应穿过内壁进入外壁100mm。当井壁破裂必须采用破壁注浆时，必须制定专门措施。\r\n（三）注浆管必须固结在井壁中，并装有阀门。钻孔可能发生涌砂时，应采取套管法或其他安全措施。采用套管法注浆时，必须对套管的固结强度进行耐压试验，只有达到注浆终压力后，方可使用。\r\n（四）在罐笼顶上进行钻孔注浆作业时，必须安设工作盘和注浆管路安全阀，作业人员必须佩带保险带，并在井口设专职值班人员。\r\n（五）井上、下都必须有可靠的通信设施，升降注浆作业吊盘或工作盘时，必须得到值班人员的允许。\r\n（六）井筒内进行钻孔注浆作业时，井底不得有人。注浆中必须观察井壁，发现问题必须停止作业，及时处理。\r\n（七）钻孔时应经常检查孔内涌水量和含砂量。涌水量较大或涌水中含砂时，必须停止钻进，及时注浆；钻孔中无水时，必须及时严密封孔。\r\n（八）注浆管露出井壁的管端与提升容器之间的间隙，必须符合本规程第三百八十七条的有关规定。\r\n',4,'admin','2012-06-13 17:09:06','2012-09-01 17:38:48','admin'),(46,'第三十三条',37,'0001-0001-0002-0001-0002-0009',0,1,NULL,'开凿或延深立井的施工组织设计中，必须有吊盘、保护盘以及凿岩、抓岩、出矸等设备的设置、运行、维修的安全措施。',4,'admin','2012-06-13 17:09:06','2012-09-01 17:38:48','admin'),(47,'第三十四条',37,'0001-0001-0002-0001-0002-0010',0,1,NULL,'开凿或延深立井时，井筒内必须设有在提升设备发生故障时专供人员出井的安全设施。',4,'admin','2012-06-13 17:09:06','2012-09-01 17:38:48','admin'),(49,'第三十六条',37,'0001-0001-0002-0001-0002-0012',0,1,NULL,'开凿或延深立井时，井筒内每个工作地点必须设置独立的信号装置。掘进和砌壁平行作业时，从吊盘和掘进工作面所发出的信号，必须有明显的区别。\r\n井内和井口的信号必须由专职信号工发送。除紧急停车外，严禁不经过井口信号工直接从井内向绞车房发送信号。井内作业人员必须熟悉并会发送信号。\r\n井口、井底信号工应在吊罐提起适当高度后，先发暂停信号，进行稳罐；待吊罐稳定，清理罐底附着物后，才能发出下降或提升信号。信号工必须目接、目送吊罐安全通过责任段。\r\n',4,'admin','2012-06-13 17:09:06','2012-09-01 17:38:48','admin'),(50,'第三十七条',37,'0001-0001-0002-0001-0002-0013',0,1,NULL,'安装井架或井架上的设备时必须盖严井口。装备井筒与安装井架及井架上的设备平行作业时，井口掩盖装置必须坚固可靠，能承受井架上坠落物的冲击。',4,'admin','2012-06-13 17:09:06','2012-09-01 17:38:48','admin'),(51,'第三十八条',37,'0001-0001-0002-0001-0002-0014',0,1,NULL,'延深立井井筒时，必须用坚固的保险盘或留保护岩柱与上部生产水平隔开。只有在井筒装备完毕、井筒与井底车场连接处的开凿和支护完成，制定安全措施后，方可拆除保险盘或掘凿保护岩柱。',4,'admin','2012-06-13 17:09:06','2012-09-01 17:38:48','admin'),(52,'第三十九条',37,'0001-0001-0002-0001-0002-0015',0,1,NULL,'采用反向凿井法掘凿暗立井或竖煤仓应遵守下列规定：\r\n（一）用木垛盘支护时，必须及时支护。爆破前最末一道木垛盘与工作面的距离不得超过1.6m。木垛盘的基墩必须牢固可靠。行人、运料眼与溜矸眼之间，必须用木板隔开。在人行眼内必须有木梯和护头板，护头板的间距最大不得超过3m，护头板上的矸石必须及时清理。爆破前，必须将人行眼和运料眼盖严。爆破后，首先通风，吹散炮烟，之后方可进入检查，检查人员不得少于2人。经过检查，确认通风、信号正常，人行间、隔板、护头板、顶板、井帮等无危险情况后，方可进行作业。\r\n（二）采用吊罐法施工时，绳孔偏斜率不得超过0.5％，绞车房与出矸水平之间，必须装设2套信号装置，其中1套必须设在吊罐内。爆破前必须摘下吊罐，放置在巷道内安全地点，将提升钢丝绳提到安全位置。爆破后必须指定专人检查提升钢丝绳和吊具，如有损坏，修复后方可使用。吊罐内有人作业时，严禁在吊罐下方进行工作或通行。\r\n（三）采用反井钻机施工时，在扩孔期间，严禁人员在孔的下方停留、通行或观察。扩孔完毕，必须在孔的外围设置栅栏，防止人员进入。\r\n（四）扩井时，必须有防止人员坠落的安全措施。爆破前必须拆除爆破孔底以下0.3m范围内的木垛盘。\r\n溜矸眼内的矸石必须经常放出，防止卡眼，但不得放空。严禁站在溜矸眼的矸石上作业。\r\n',4,'admin','2012-06-13 17:09:06','2012-09-01 17:38:48','admin'),(53,'第四十条',37,'0001-0001-0002-0001-0002-0016',0,1,NULL,'冬季或用冻结法开凿立井时，必须有防冻、清除冰凌的措施。',4,'admin','2012-06-13 17:09:06','2012-09-01 17:38:48','admin'),(54,'第四十一条',37,'0001-0001-0002-0001-0002-0017',0,1,NULL,'掘进工作面严禁空顶作业。靠近掘进工作面10m内的支护，在爆破前必须加固。爆破崩倒、崩坏的支架必须先行修复，之后方可进入工作面作业。修复支架时必须先检查顶、帮，并由外向里逐架进行。\r\n在松软的煤、岩层或流砂性地层中及地质破碎带掘进巷道时，必须采取前探支护或其他措施。\r\n在坚硬和稳定的煤、岩层中，确定巷道不设支护时，必须制定安全措施。\r\n',4,'admin','2012-06-13 17:09:06','2012-09-01 17:38:48','admin'),(55,'第四十二条',37,'0001-0001-0002-0001-0002-0018',0,1,NULL,'支架间应设牢固的撑木或拉杆。可缩性金属支架应用金属支拉杆，并用机械或力矩扳手拧紧卡缆。支架与顶帮之间的空隙必须塞紧、背实。巷道砌碹时，碹体与顶帮之间必须用不燃物充满填实；巷道冒顶空顶部分，可用支护材料接顶，但在碹拱上部必须充填不燃物垫层，其厚度不得小于0.5m。',4,'admin','2012-06-13 17:09:06','2012-09-01 17:38:48','admin'),(56,'第四十三条',37,'0001-0001-0002-0001-0002-0019',0,1,NULL,'更换巷道支护时，在拆除原有支护前，应先加固临近支护，拆除原有支护后，必须及时除掉顶帮活矸和架设永久支护，必要时还应采取临时支护措施。在倾斜巷道中，必须有防止矸石、物料滚落和支架歪倒的安全措施。',4,'admin','2012-06-13 17:09:06','2012-09-01 17:38:48','admin'),(57,'第四十四条',37,'0001-0001-0002-0001-0002-0020',0,1,NULL,'采用锚杆、锚喷等支护形式时，应遵守下列规定：\r\n（一）锚杆、锚喷等支护的端头与掘进工作面的距离，锚杆的形式、规格、安装角度，混凝土标号、喷体厚度，挂网所采用金属网的规格以及围岩涌水的处理等，必须在施工组织设计或作业规程中规定。\r\n（二）采用钻爆法掘进的岩石巷道，必须采用光面爆破。\r\n（三）打锚杆眼前，必须首先敲帮问顶，将活矸处理掉，在确保安全的条件下，方可作业。\r\n（四）使用锚固剂固定锚杆时，应将孔壁冲洗干净，砂浆锚杆必须灌满填实。\r\n（五）软岩使用锚杆支护时，必须全长锚固。\r\n（六）采用人工上料喷射机喷射混凝土、砂浆时，必须采用潮料，并使用除尘机对上料口、余气口除尘。喷射前，必须冲洗岩帮。喷射后应有养护措施。作业人员必须佩戴劳动保护用品。\r\n（七）锚杆必须按规定做拉力试验。煤巷还必须进行顶板离层监测，并用记录牌板显示。对喷体必须做厚度和强度检查，并有检查和试验记录。在井下做锚固力试验时，必须有安全措施。\r\n（八）锚杆必须用机械或力矩扳手拧紧，确保锚杆的托板紧贴巷壁。\r\n（九）岩帮的涌水地点，必须处理。\r\n（十）处理堵塞的喷射管路时，喷枪口的前方及其附近严禁有其他人员。\r\n',4,'admin','2012-06-13 17:09:06','2012-09-01 17:38:48','admin'),(59,'第二章',5,'0001-0001-0002-0002',0,1,1,'111',8,'admin','2012-09-01 16:26:34','2012-09-01 17:34:26','admin'),(60,'第一节',59,'0001-0001-0002-0002-0001',0,1,1,'高瓦斯矿井煤巷掘进工作面应安设隔（抑）爆设',4,'admin','2012-09-01 16:26:50','2012-11-14 21:59:23','admin'),(61,'第三章',5,'0001-0001-0002-0003',0,1,1,'',7,'admin','2012-09-01 17:33:23','2012-09-01 17:34:26','admin'),(62,'第四章',5,'0001-0001-0002-0004',0,1,1,'',6,'admin','2012-09-01 17:33:33','2012-09-01 17:34:26','admin'),(63,'第五章',5,'0001-0001-0002-0005',0,1,1,'',5,'admin','2012-09-01 17:33:43','2012-09-01 17:34:26','admin'),(64,'第六章',5,'0001-0001-0002-0006',0,1,1,'',5,'admin','2012-09-01 17:33:50','2012-09-01 17:34:26','admin'),(65,'第七章',5,'0001-0001-0002-0007',0,1,1,'',3,'admin','2012-09-01 17:34:00','2012-09-01 17:34:26','admin'),(66,'第八章',5,'0001-0001-0002-0008',0,1,1,'',2,'admin','2012-09-01 17:34:13','2012-09-01 17:34:26','admin'),(67,'第九章',5,'0001-0001-0002-0009',0,1,1,'',1,'admin','2012-09-01 17:34:19','2012-09-01 17:34:26','admin'),(68,'第十章',5,'0001-0001-0002-00010',0,1,1,'',0,'admin','2012-09-01 17:34:26',NULL,NULL),(69,'第一节',68,'0001-0001-0002-00010-0001',0,1,1,'',4,'admin','2012-09-01 17:34:56','2012-09-01 18:40:05','admin'),(70,'第四百九十二条',69,'0001-0001-0002-00010-0001-0001',0,1,1,'矿山救护队是处理矿井火、瓦斯、煤尘、水、顶板等灾害的专业队伍；矿山救护队员是煤矿井下一线特种作业人员。',8,'admin','2012-09-01 17:35:19','2012-09-10 23:36:40','admin'),(71,'第三节',25,'0001-0001-0002-0001-0003',0,1,1,'',5,'admin','2012-09-01 17:36:21','2012-09-01 17:36:54','admin'),(72,'第四节',25,'0001-0001-0002-0001-0004',0,1,1,'',4,'admin','2012-09-01 17:36:29','2012-09-01 17:36:54','admin'),(73,'第五节',25,'0001-0001-0002-0001-0005',0,1,1,'',3,'admin','2012-09-01 17:36:36','2012-09-01 17:36:54','admin'),(74,'第六节',25,'0001-0001-0002-0001-0006',0,1,1,'',2,'admin','2012-09-01 17:36:42','2012-09-01 17:36:54','admin'),(75,'第七节',25,'0001-0001-0002-0001-0007',0,1,1,'',1,'admin','2012-09-01 17:36:50','2012-09-01 17:36:54','admin'),(76,'第八节',25,'0001-0001-0002-0001-0008',0,1,1,'',0,'admin','2012-09-01 17:36:54',NULL,NULL),(77,'第四十五条',37,'0001-0001-0002-0001-0002-0020',0,1,1,'掘进巷道在揭露老空前，必须制定探查老空的安全措施，包括接近老空时必须预留的煤（岩）柱厚度和探明水、火、瓦斯等内容。必须根据探明的情况采取措施，进行处理。\r\n在揭露老空时，必须将人员撤至安全地点。只有经过检查，证明老空内的水、瓦斯和其他有害气体等无危险后，方可恢复工作。\r\n',2,'admin','2012-09-01 17:38:16','2012-09-01 17:38:48','admin'),(78,'第四十六条',37,'0001-0001-0002-0001-0002-0021',0,1,1,'开凿或延深斜井、下山时，必须在斜井、下山的上口设置防止跑车装置，在掘进工作面的上方设置坚固的跑车防护装置。跑车防护装置与掘进工作面的距离必须在施工组织设计或作业规程中规定。\r\n斜井（巷）施工期间兼作行人道时，必须每隔40m设置躲避硐并设红灯。设有躲避硐的一侧必须有畅通的人行道。上下人员必须走人行道。行车时红灯亮，行人立即进入躲避硐；红灯熄灭后，方可行走。\r\n',1,'admin','2012-09-01 17:38:33','2012-09-01 17:38:48','admin'),(79,'第四十七条',37,'0001-0001-0002-0001-0002-0022',0,1,1,'由下向上掘进25°以上的倾斜巷道时，必须将溜煤（矸）道与人行道分开，防止煤（矸）滑落伤人。人行道应设扶手、梯子和信号装置。斜巷与上部巷道贯通时，必须有安全措施。',0,'admin','2012-09-01 17:38:48',NULL,NULL),(80,'第四十八条',71,'0001-0001-0002-0001-0003-0001',0,1,1,'采区开采前必须按照生产布局合理的要求编制采区设计，并严格按照采区设计组织施工。\r\n一个采区内同一煤层的一翼最多只能布置1个回采工作面和2个掘进工作面同时作业。\r\n一个采区内同一煤层双翼开采或多煤层开采的，该采区最多只能布置2个回采工作面和4个掘进工作面同时作业。\r\n严禁在采煤工作面范围内再布置另一采煤工作面同时作业。\r\n采掘过程中严禁任意扩大和缩小设计规定的煤柱。采空区内不得遗留未经设计规定的煤柱。\r\n严禁破坏工业场地、矿界、防水和井巷等的安全煤柱\r\n突出矿井、高瓦斯矿井、低瓦斯矿井高瓦斯区域的采煤工作面，不得采用前进式采煤方法。\r\n',20,'admin','2012-09-01 17:39:14','2012-11-14 21:37:03','admin'),(81,'第四十九条',71,'0001-0001-0002-0001-0003-0002',0,1,1,'采煤工作面回采前必须编制作业规程。情况发生变化时，必须及时修改作业规程或补充安全措施。',19,'admin','2012-09-01 17:39:28','2012-11-14 21:37:03','admin'),(82,'第五十条',71,'0001-0001-0002-0001-0003-0003',0,1,1,'采煤工作面必须保持至少2个畅通的安全出口，一个通到回风巷道，另一个通到进风巷道。\r\n开采三角煤、残留煤柱，不能保持2个安全出口时，必须制订安全措施，报企业主要负责人审批。\r\n采煤工作面所有安全出口与巷道连接处超前压力影响范围内必须加强支护，且加强支护的巷道长度不得小于20m；综合机械化采煤工作面，此范围内的巷道高度不得低于1.8m，其他采煤工作面，此范围内的巷道高度不得低于1.6m。安全出口和与之相连接的巷道必须设专人维护，发生支架断梁折柱、巷道底鼓变形时，必须及时更换、清挖。\r\n',18,'admin','2012-09-01 17:39:44','2012-11-14 21:37:03','admin'),(83,'第五十一条',71,'0001-0001-0002-0001-0003-0004',0,1,1,'采煤工作面的伞檐不得超过作业规程的规定，不得任意丢失顶煤和底煤。工作面的浮煤应清理干净。支架、输送机和充填垛都应保持直线。',17,'admin','2012-09-01 17:39:59','2012-11-14 21:37:03','admin'),(84,'第五十二条',71,'0001-0001-0002-0001-0003-0005',0,1,1,'台阶采煤工作面必须设置安全脚手板、护身板和溜煤板。倒台阶采煤工作面，还必须在台阶的底脚加设保护台板。\r\n阶檐的宽度、台阶面长度和下部超前小眼的个数，必须在作业规程中规定。\r\n',16,'admin','2012-09-01 17:40:14','2012-11-14 21:37:03','admin'),(85,'第五十三条',71,'0001-0001-0002-0001-0003-0006',0,1,1,'采煤工作面必须经常存有一定数量的备用支护材料。使用摩擦式金属支柱或单体液压支柱的工作面，必须备有坑木，其数量、规格、存放地点和管理方法必须在作业规程中规定。\r\n采煤工作面严禁使用折损的坑木、损坏的金属顶梁、失效的摩擦式金属支柱和失效的单体液压支柱。\r\n在同一采煤工作面中，不得使用不同类型和不同性能的支柱。在地质条件复杂的采煤工作面中必须使用不同类型的支柱时，必须制定安全措施。\r\n摩擦式金属支柱和单体液压支柱入井前必须逐根进行压力试验。\r\n对摩擦式金属支柱、金属顶梁和单体液压支柱，在采煤工作面回采结束后或使用时间超过8个月后，必须进行检修。检修好的支柱，还必须进行压力试验，合格后方可使用。\r\n',15,'admin','2012-09-01 17:40:31','2012-11-14 21:37:03','admin'),(86,'第五十四条',71,'0001-0001-0002-0001-0003-0007',0,1,1,'采煤工作面必须按作业规程的规定及时支护，严禁空顶作业。所有支架必须架设牢固，并有防倒柱措施。严禁在浮煤或浮矸上架设支架。使用摩擦式金属支柱时，必须使用液压升柱器架设，初撑力不得小于50kN；单体液压支柱的初撑力，柱径为100mm的不得小于90kN，柱径为80mm的不得小于60kN。对于软岩条件下初撑力确实达不到要求的，在制定措施、满足安全的条件下，必须经企业技术负责人审批。严禁在控顶区域内提前摘柱。碰倒或损坏、失效的支柱，必须立即恢复或更换。移动输送机机头、机尾需要拆除附近的支架时，必须先架好临时支架。\r\n采煤工作面遇顶底板松软或破碎、过断层、过老空、过煤柱或冒顶区以及托伪顶开采时，必须制定安全措施。\r\n',14,'admin','2012-09-01 17:40:49','2012-11-14 21:37:03','admin'),(87,'第五十五条',71,'0001-0001-0002-0001-0003-0008',0,1,1,'严格执行敲帮问顶制度。\r\n开工前，班组长必须对工作面安全情况进行全面检查，确认无危险后，方准人员进入工作面。\r\n',13,'admin','2012-09-01 17:41:07','2012-11-14 21:37:03','admin'),(88,'第五十六条',71,'0001-0001-0002-0001-0003-0009',0,1,1,'采煤工作面必须及时回柱放顶或充填，控顶距离超过作业规程规定时，禁止采煤。用垮落法控制顶板，回柱后顶板不垮落、悬顶距离超过作业规程的规定时，必须停止采煤，采取人工强制放顶或其他措施进行处理。',12,'admin','2012-09-01 17:41:19','2012-11-14 21:37:03','admin'),(89,'第五十七条',71,'0001-0001-0002-0001-0003-00010',0,1,1,'用垮落法控制顶板时，回柱放顶的方法和安全措施，放顶与爆破、机械落煤等工序平行作业的安全距离，放顶区内支架、木柱、木垛的回收方法，必须在作业规程中明确规定。\r\n采煤工作面初次放顶及收尾时，必须制定安全措施。\r\n放顶人员必须站在支架完整，无崩绳、崩柱、甩钩、断绳抽人等危险的安全地点工作。回柱放顶前，必须对放顶的安全工作进行全面检查，清理好退路。回柱放顶时，必须指定有经验的人员观察顶板。\r\n',11,'admin','2012-09-01 17:41:33','2012-11-14 21:37:03','admin'),(90,'第五十八条',71,'0001-0001-0002-0001-0003-0011',0,1,1,'采煤工作面采用密集支柱切顶时，两段密集支柱之间必须留有宽0.5m以上的出口，出口间的距离和新密集支柱超前的距离必须在作业规程中明确规定。采煤工作面采用无密集支柱切顶时，必须有防止工作面冒顶和矸石窜入工作面的措施。',10,'admin','2012-09-01 17:42:05','2012-11-14 21:37:03','admin'),(91,'第五十九条',71,'0001-0001-0002-0001-0003-0012',0,1,1,'采用人工假顶分层垮落法开采的采煤工作面，人工假顶必须铺设好，搭接严密；采用金属网或矿用塑料网假顶时，必须把网连结好。\r\n确认垮落的顶板岩石能够胶结形成再生顶板时，可不铺设人工假顶。\r\n采用分层垮落法开采时，必须向采空区注水或注浆。注水或注浆的具体要求，应在作业规程中明确规定。\r\n',9,'admin','2012-09-01 17:42:21','2012-11-14 21:37:03','admin'),(92,'第六十条',71,'0001-0001-0002-0001-0003-0013',0,1,1,'用水砂充填法控制顶板时，采空区和三角点必须充填满。充填地点的下方，严禁人员通行或停留。注砂井和充填地点之间，应保持用电话联络，联络中断时，必须立即停止注砂。\r\n清理因跑砂堵塞的倾斜井巷前，必须制定安全措施。\r\n',8,'admin','2012-09-01 17:42:36','2012-11-14 21:37:03','admin'),(93,'第六十一条',71,'0001-0001-0002-0001-0003-0014',0,1,1,'用带状充填法控制顶板时，必须在垒砌石垛带之前清扫底板上的浮煤，石垛带必须砌接到顶，顶板下和垛墙上的缝隙应用石块塞紧。需从2个石垛中间采取矸石时，必须首先将顶板的活矸用长柄工具处理掉，设置临时支护，并与采煤工作面相接，采矸人员应在临时支护保护下进行工作，并有人观察顶板。',7,'admin','2012-09-01 17:42:50','2012-11-14 21:37:03','admin'),(94,'第六十二条',71,'0001-0001-0002-0001-0003-0015',0,1,1,'开采近距离煤层，上一煤层采用刀柱法、条带法或带状充填法控制顶板，下一煤层采用垮落法控制顶板时，必须制定控制顶板的安全措施。',6,'admin','2012-09-01 17:43:02','2012-11-14 21:37:03','admin'),(95,'第六十三条',71,'0001-0001-0002-0001-0003-0016',0,1,1,'长壁式采煤工作面分上下面同时回采时，上下面的错距应根据煤层倾角、矿山压力、支护形式、通风、瓦斯、自然发火、涌水等情况，在作业规程中明确规定。',5,'admin','2012-09-01 17:43:14','2012-11-14 21:37:03','admin'),(96,'第六十四条',71,'0001-0001-0002-0001-0003-0017',0,1,1,'采用倾斜分层垮落法回采时，下一分层的采煤工作必须在上一分层顶板垮落的稳定区域下面进行。上下分层的回采间隔时间不应过长，以防假顶腐朽。\r\n采用水平分层垮落法回采时，上一分层的采煤工作面超前下一分层采煤工作面的距离，应在作业规程中规定。\r\n',4,'admin','2012-09-01 17:43:30','2012-11-14 21:37:03','admin'),(97,'第六十五条',71,'0001-0001-0002-0001-0003-0018',0,1,1,'采用掩护支架开采急倾斜煤层时，支架的角度、结构，支架垫层数和厚度以及点柱的支设角度、排列方式和密度，必须在作业规程中规定。\r\n生产中遇有断梁、支架悬空、窜矸等情况时，必须及时处理。支架沿走向弯曲、歪斜及角度超过作业规程规定时，在下一次放架过程中，必须进行调整。应经常检查支架上的螺栓和附件，如有松动，必须及时拧紧。\r\n正倾斜掩护支架的每个回采带的两端，必须设置人行眼，并用木板隔出溜煤眼。伪倾斜掩护支架工作面上下2个出口的要求和工作面的伪倾角，超前溜煤眼的规格、间距和施工方式必须在作业规程中规定。\r\n掩护支架接近平巷时，应缩短每次下放支架的距离，并减少同时爆破的炮眼数目和装药量。掩护支架过平巷时，应加强溜煤眼与平巷连接处的支护或架设木垛。\r\n',4,'admin','2012-09-01 17:43:46','2012-11-14 21:37:03','admin'),(98,'第二节',59,'0001-0001-0002-0002-0002',0,1,1,'',1,'admin','2012-09-01 17:47:42','2012-09-01 17:47:48','admin'),(99,'第三节',59,'0001-0001-0002-0002-0003',0,1,1,'',0,'admin','2012-09-01 17:47:48',NULL,NULL),(100,'第一章',7,'0001-0001-0004-0001',0,1,1,'',1,'admin','2012-09-01 17:52:32','2012-09-01 17:52:39','admin'),(101,'第二章',7,'0001-0001-0004-0002',0,1,1,'',0,'admin','2012-09-01 17:52:39',NULL,NULL),(102,'第一章',6,'0001-0001-0003-0001',0,1,1,'',7,'admin','2012-09-01 17:54:54','2012-09-01 17:55:37','admin'),(103,'第二章',6,'0001-0001-0003-0002',0,1,1,'',6,'admin','2012-09-01 17:55:00','2012-09-01 17:55:37','admin'),(104,'第三章',6,'0001-0001-0003-0003',0,1,1,'',5,'admin','2012-09-01 17:55:07','2012-09-01 17:55:37','admin'),(105,'第四章',6,'0001-0001-0003-0004',0,1,1,'',4,'admin','2012-09-01 17:55:12','2012-09-01 17:55:37','admin'),(106,'第五章',6,'0001-0001-0003-0005',0,1,1,'',3,'admin','2012-09-01 17:55:18','2012-09-01 17:55:37','admin'),(107,'第六章',6,'0001-0001-0003-0006',0,1,1,'',2,'admin','2012-09-01 17:55:24','2012-09-01 17:55:37','admin'),(108,'第七章',6,'0001-0001-0003-0007',0,1,1,'',1,'admin','2012-09-01 17:55:31','2012-09-01 17:55:37','admin'),(109,'第八章',6,'0001-0001-0003-0008',0,1,1,'',0,'admin','2012-09-01 17:55:37',NULL,NULL),(110,'第七百三十八条',100,'0001-0001-0004-0001-0001',0,1,1,'煤矿企业必须加强职业危害的防治与管理，做好作业场所的职业卫生和劳动保护工作。采取有效措施控制尘、毒危害，保证作业场所符合国家职业卫生标准。',5,'admin','2012-09-01 17:57:49','2012-09-01 17:59:25','admin'),(111,'第七百三十九条',100,'0001-0001-0004-0001-0002',0,1,1,'作业场所空气中粉尘（总粉尘、呼吸性粉尘）浓度应符合表26要求。',4,'admin','2012-09-01 17:58:14','2012-09-01 17:59:25','admin'),(112,'第七百四十条',100,'0001-0001-0004-0001-0003',0,1,1,'煤矿企业必须按国家规定对生产性粉尘进行监测，并遵守下列规定：\r\n（一）总粉尘：\r\n1．作业场所的粉尘浓度，井下每月测定2次，地面及露天煤矿每月测定1次；\r\n2．粉尘分散度，每6个月测定1次。\r\n（二）呼吸性粉尘：\r\n1．工班个体呼吸性粉尘监测，采、掘（剥）工作面每3个月测定1次，其他工作面或作业场所每6个月测定1次。每个采样工种分2个班次连续采样，1个班次内至少采集2个有效样品，先后采集的有效样品不得少于4个；\r\n2．定点呼吸性粉尘监测每月测定1次。\r\n（三）粉尘中游离SiO2含量，每6个月测定1次，在变更工作面时也必须测定1次；各接尘作业场所每次测定的有效样品数不得少于3个。\r\n（四）开采深度大于200m的露天煤矿，在气压较低的季节应适当增加测定次数。\r\n',3,'admin','2012-09-01 17:58:38','2012-09-01 17:59:25','admin'),(113,'第七百四十一条',100,'0001-0001-0004-0001-0004',0,1,1,'作业场所的噪声，不应超过85dB（A）。大于85dB（A）时，需配备个人防护用品；大于或等于90dB（A）时，还应采取降低作业场所噪声的措施。',2,'admin','2012-09-01 17:58:50','2012-09-01 17:59:25','admin'),(114,'第七百四十二条',100,'0001-0001-0004-0001-0005',0,1,1,'矿区水源和供水工程应保证矿区工业用水量，其水质应符合国家卫生标准。',1,'admin','2012-09-01 17:59:07','2012-09-01 17:59:25','admin'),(115,'第七百四十三条',100,'0001-0001-0004-0001-0006',0,1,1,'煤矿企业必须按国家规定对生产性毒物、有害物理因素等进行定期监测，并遵守下列规定：\r\n（一）三硝基甲苯（生产车间）作业点，每月测定1次。\r\n（二）铅、苯、汞及其他有毒物质，每3个月测定1次，已达到职业卫生标准的可6个月测定1次。\r\n（三）噪声、放射线及其他物理因素每年至少测定1次。\r\n监测结果必须建档，并报有关单位。\r\n',0,'admin','2012-09-01 17:59:25',NULL,NULL),(116,'第七百四十四条',101,'0001-0001-0004-0002-0001',0,1,1,'煤矿企业必须按国家有关法律、法规的规定，对新入矿工人进行职业健康检查，并建立健康档案；对接尘工人的职业健康检查必须拍照胸大片。\r\n煤矿企业应按照国家法律、法规和卫生行政主管部门的规定定期对接触粉尘、毒物及有害物理因素等的作业人员进行职业健康检查。对检查出的职业病患者，煤矿企业必须按国家规定及时给以治疗、疗养和调离有害作业岗位，并做好健康监护及职业病报告工作。\r\n查体时间间隔必须符合下列要求：\r\n（一）对在岗接触粉尘作业工人，岩石掘进工种每2～3年拍片检查1次；混合工种每3～4年拍片检查1次；纯采煤工种每4～5年拍片检查1次。\r\n（二）对离岗工人必须进行离岗的职业性健康检查。\r\n（三）对接触毒物、放射线的人员每年检查1次。\r\n职业性健康检查、职业病诊断、职业病治疗应由取得相应资格的职业卫生机构承担。',6,'admin','2012-09-01 17:59:45','2012-09-01 18:01:07','admin'),(117,'第七百四十五条',101,'0001-0001-0004-0002-0002',0,1,1,'Ⅰ期尘肺患者每年复查1次。\r\n疑似尘肺患者（0+）：岩石掘进工种每年拍片复查1次、混合工种每2年拍片复查1次、纯采煤工种每3年拍片复查1次。\r\n',5,'admin','2012-09-01 18:00:00','2012-09-01 18:01:07','admin'),(118,'第七百四十六条',101,'0001-0001-0004-0002-0003',0,1,1,'有下列病症之一的，不得从事接尘作业：\r\n（一）活动性肺结核病及肺外结核病。\r\n（二）严重的上呼吸道或支气管疾病。\r\n（三）显著影响肺功能的肺脏或胸膜病变。\r\n（四）心、血管器质性疾病。\r\n（五）经医疗鉴定，不适于从事粉尘作业的其他疾病。\r\n',4,'admin','2012-09-01 18:00:13','2012-09-01 18:01:07','admin'),(119,'第七百四十七条',101,'0001-0001-0004-0002-0004',0,1,1,'有下列病症之一的，不得从事井下工作：\r\n（一）本规程第七百四十六条所列病症之一的。\r\n（二）风湿病（反复活动）。\r\n（三）严重的皮肤病。\r\n（四）经医疗鉴定，不适于从事井下工作的其他疾病。',3,'admin','2012-09-01 18:00:27','2012-09-01 18:01:07','admin'),(120,'第七百四十八条',101,'0001-0001-0004-0002-0005',0,1,1,'癫痫病和精神分裂症患者严禁从事煤矿生产工作。',2,'admin','2012-09-01 18:00:39','2012-09-01 18:01:07','admin'),(121,'第七百四十九条',101,'0001-0001-0004-0002-0006',0,1,1,'患有高血压、心脏病、深度近视等病症以及其他不适应高空（2m以上）作业者，不得从事高空作业。',1,'admin','2012-09-01 18:00:51','2012-09-01 18:01:07','admin'),(122,'第七百五十条',101,'0001-0001-0004-0002-0007',0,1,1,'粉尘、毒物及有害物理因素超过国家职业卫生标准的作业场所，除采取防治措施外，作业人员必须佩戴防尘或防毒等个体劳动防护用品。',0,'admin','2012-09-01 18:01:07',NULL,NULL),(123,'第一节',103,'0001-0001-0003-0002-0001',0,1,1,'',3,'admin','2012-09-01 18:03:04','2012-09-01 18:03:30','admin'),(124,'第二节',103,'0001-0001-0003-0002-0002',0,1,1,'',2,'admin','2012-09-01 18:03:18','2012-09-01 18:03:30','admin'),(125,'第三节',103,'0001-0001-0003-0002-0003',0,1,1,'',1,'admin','2012-09-01 18:03:23','2012-09-01 18:03:30','admin'),(126,'第四节',103,'0001-0001-0003-0002-0004',0,1,1,'',0,'admin','2012-09-01 18:03:30',NULL,NULL),(127,'第一节',104,'0001-0001-0003-0003-0001',0,1,1,'',2,'admin','2012-09-01 18:03:42','2012-09-01 18:03:55','admin'),(128,'第二节',104,'0001-0001-0003-0003-0002',0,1,1,'',1,'admin','2012-09-01 18:03:50','2012-09-01 18:03:55','admin'),(129,'第三节',104,'0001-0001-0003-0003-0003',0,1,1,'',0,'admin','2012-09-01 18:03:55',NULL,NULL),(130,'第一节',107,'0001-0001-0003-0006-0001',0,1,1,'',1,'admin','2012-09-01 18:04:08','2012-09-01 18:04:14','admin'),(131,'第二节',107,'0001-0001-0003-0006-0002',0,1,1,'',0,'admin','2012-09-01 18:04:14',NULL,NULL),(132,'第一节',108,'0001-0001-0003-0007-0001',0,1,1,'',7,'admin','2012-09-01 18:05:05','2012-09-01 18:05:39','admin'),(133,'第二节',108,'0001-0001-0003-0007-0002',0,1,1,'',6,'admin','2012-09-01 18:05:09','2012-09-01 18:05:39','admin'),(134,'第三节',108,'0001-0001-0003-0007-0003',0,1,1,'',5,'admin','2012-09-01 18:05:15','2012-09-01 18:05:39','admin'),(135,'第四节',108,'0001-0001-0003-0007-0004',0,1,1,'',4,'admin','2012-09-01 18:05:19','2012-09-01 18:05:39','admin'),(136,'第五节',108,'0001-0001-0003-0007-0005',0,1,1,'',3,'admin','2012-09-01 18:05:23','2012-09-01 18:05:39','admin'),(137,'第六节',108,'0001-0001-0003-0007-0006',0,1,1,'',2,'admin','2012-09-01 18:05:28','2012-09-01 18:05:39','admin'),(138,'第七节',108,'0001-0001-0003-0007-0007',0,1,1,'',1,'admin','2012-09-01 18:05:34','2012-09-01 18:05:39','admin'),(139,'第八节',108,'0001-0001-0003-0007-0008',0,1,1,'',0,'admin','2012-09-01 18:05:39',NULL,NULL),(140,'第一节',61,'0001-0001-0002-0003-0001',0,1,1,'',2,'admin','2012-09-01 18:35:38','2012-09-01 18:35:51','admin'),(141,'第二节',61,'0001-0001-0002-0003-0002',0,1,1,'',1,'admin','2012-09-01 18:35:44','2012-09-01 18:35:51','admin'),(142,'第三节',61,'0001-0001-0002-0003-0003',0,1,1,'',0,'admin','2012-09-01 18:35:51',NULL,NULL),(143,'第一节',62,'0001-0001-0002-0004-0001',0,1,1,'',4,'admin','2012-09-01 18:36:09','2012-09-01 18:36:39','admin'),(144,'第二节',62,'0001-0001-0002-0004-0002',0,1,1,'',3,'admin','2012-09-01 18:36:16','2012-09-01 18:36:39','admin'),(145,'第三节',62,'0001-0001-0002-0004-0003',0,1,1,'',2,'admin','2012-09-01 18:36:27','2012-09-01 18:36:39','admin'),(146,'第四节',62,'0001-0001-0002-0004-0004',0,1,1,'',1,'admin','2012-09-01 18:36:33','2012-09-01 18:36:39','admin'),(147,'第五节',62,'0001-0001-0002-0004-0005',0,1,1,'',0,'admin','2012-09-01 18:36:39',NULL,NULL),(148,'第一节',63,'0001-0001-0002-0005-0001',0,1,1,'',2,'admin','2012-09-01 18:36:51','2012-09-01 18:37:03','admin'),(149,'第二节',63,'0001-0001-0002-0005-0002',0,1,1,'',1,'admin','2012-09-01 18:36:58','2012-09-01 18:37:03','admin'),(150,'第三节',63,'0001-0001-0002-0005-0003',0,1,1,'',0,'admin','2012-09-01 18:37:03',NULL,NULL),(151,'第一节',64,'0001-0001-0002-0006-0001',0,1,1,'',6,'admin','2012-09-01 18:37:18','2012-09-01 18:37:45','admin'),(152,'第二节',64,'0001-0001-0002-0006-0002',0,1,1,'',5,'admin','2012-09-01 18:37:23','2012-09-01 18:37:45','admin'),(153,'第三节',64,'0001-0001-0002-0006-0003',0,1,1,'',4,'admin','2012-09-01 18:37:27','2012-09-01 18:37:45','admin'),(154,'第四节',64,'0001-0001-0002-0006-0004',0,1,1,'',3,'admin','2012-09-01 18:37:31','2012-09-01 18:37:45','admin'),(155,'第五节',64,'0001-0001-0002-0006-0005',0,1,1,'',2,'admin','2012-09-01 18:37:35','2012-09-01 18:37:45','admin'),(157,'第一节',65,'0001-0001-0002-0007-0001',0,1,1,'',2,'admin','2012-09-01 18:38:00','2012-09-01 18:38:11','admin'),(158,'第二节',65,'0001-0001-0002-0007-0002',0,1,1,'',1,'admin','2012-09-01 18:38:07','2012-09-01 18:38:11','admin'),(159,'第三节',65,'0001-0001-0002-0007-0003',0,1,1,'',0,'admin','2012-09-01 18:38:11',NULL,NULL),(160,'第一节',66,'0001-0001-0002-0008-0001',0,1,1,'',4,'admin','2012-09-01 18:38:24','2012-09-01 18:38:46','admin'),(161,'第二节',66,'0001-0001-0002-0008-0002',0,1,1,'',3,'admin','2012-09-01 18:38:30','2012-09-01 18:38:46','admin'),(162,'第三节',66,'0001-0001-0002-0008-0003',0,1,1,'',2,'admin','2012-09-01 18:38:35','2012-09-01 18:38:46','admin'),(163,'第四节',66,'0001-0001-0002-0008-0004',0,1,1,'',1,'admin','2012-09-01 18:38:42','2012-09-01 18:38:46','admin'),(164,'第五节',66,'0001-0001-0002-0008-0005',0,1,1,'',0,'admin','2012-09-01 18:38:46',NULL,NULL),(165,'第一节',67,'0001-0001-0002-0009-0001',0,1,1,'',6,'admin','2012-09-01 18:39:02','2012-09-01 18:39:29','admin'),(166,'第二节',67,'0001-0001-0002-0009-0002',0,1,1,'',5,'admin','2012-09-01 18:39:07','2012-09-01 18:39:29','admin'),(167,'第三节',67,'0001-0001-0002-0009-0003',0,1,1,'',4,'admin','2012-09-01 18:39:11','2012-09-01 18:39:29','admin'),(168,'第四节',67,'0001-0001-0002-0009-0004',0,1,1,'',3,'admin','2012-09-01 18:39:15','2012-09-01 18:39:29','admin'),(169,'第五节',67,'0001-0001-0002-0009-0005',0,1,1,'',2,'admin','2012-09-01 18:39:19','2012-09-01 18:39:29','admin'),(170,'第六节',67,'0001-0001-0002-0009-0006',0,1,1,'',1,'admin','2012-09-01 18:39:23','2012-09-01 18:39:29','admin'),(171,'第七节',67,'0001-0001-0002-0009-0007',0,1,1,'',0,'admin','2012-09-01 18:39:29',NULL,NULL),(172,'第二节',68,'0001-0001-0002-00010-0002',0,1,1,'',3,'admin','2012-09-01 18:39:47','2012-09-01 18:40:05','admin'),(173,'第三节',68,'0001-0001-0002-00010-0003',0,1,1,'',2,'admin','2012-09-01 18:39:55','2012-09-01 18:40:05','admin'),(174,'第四节',68,'0001-0001-0002-00010-0004',0,1,1,'',1,'admin','2012-09-01 18:40:01','2012-09-01 18:40:05','admin'),(175,'第五节',68,'0001-0001-0002-00010-0005',0,1,1,'',0,'admin','2012-09-01 18:40:05',NULL,NULL),(176,'第五百三十四条',102,'0001-0001-0003-0001-0001',0,1,1,'煤矿企业应根据本矿采用的开采工艺和设备制定作业规程和各工种安全技术操作规程。',13,'admin','2012-09-01 18:40:53','2012-09-01 18:44:04','admin'),(177,'第五百三十五条',102,'0001-0001-0003-0001-0002',0,1,1,'露天采场主要区段的上下平盘之间应设人行通路或梯子，并按有关规定在梯子两侧设置安全护栏。',12,'admin','2012-09-01 18:41:04','2012-09-01 18:44:04','admin'),(178,'第五百三十六条',102,'0001-0001-0003-0001-0003',0,1,1,'在露天煤矿内行走的人员必须遵守下列规定：\r\n（一）必须走人行通路或梯子。\r\n（二）因工作需要沿铁路线和矿山道路行走的人员，必须时刻注意前后方向来车。躲车时，必须躲到安全地点。\r\n（三）横过铁道线路或矿山公路时，必须止步瞭望。\r\n（四）横过带式输送机时，必须沿着装有栏杆的栈桥通过。\r\n（五）严禁在有塌落危险的坡顶、坡底行走或逗留。',11,'admin','2012-09-01 18:41:24','2012-09-01 18:44:04','admin'),(179,'第五百三十七条',102,'0001-0001-0003-0001-0004',0,1,1,'未经煤矿企业允许，闲杂人员和车辆严禁进入作业区。',10,'admin','2012-09-01 18:41:36','2012-09-01 18:44:04','admin'),(180,'第五百三十八条',102,'0001-0001-0003-0001-0005',0,1,1,'移动设备应在平盘安全区内走行或停留；否则必须采取安全措施。',9,'admin','2012-09-01 18:41:58','2012-09-01 18:44:04','admin'),(181,'第五百三十九条',102,'0001-0001-0003-0001-0006',0,1,1,'采场内有危险的火区、老空等地点，应充填或设置栅栏，并设置警示标志；地面、采场及排土场内临时设置变压器时应设围栏，配电柜、箱、盘应加锁，并应设置明显的防触电标志；设备停放场、炸药厂、爆炸材料库、油库、加油站和物资仓库等易燃易爆场所，必须设置防爆、防火和危险警示标志；矿山道路必须设置限速、道口等路标，特殊路段设警示标；汽车运输为左侧通行的，在过渡区段内必须设置醒目的换向标志。\r\n严禁擅自移动和毁坏各种安全标志。',8,'admin','2012-09-01 18:42:13','2012-09-01 18:44:04','admin'),(182,'第五百四十条',102,'0001-0001-0003-0001-0007',0,1,1,'在运输线路两侧堆放物料时，不得影响行车安全。',7,'admin','2012-09-01 18:42:26','2012-09-01 18:44:04','admin'),(183,'第五百四十一条',102,'0001-0001-0003-0001-0008',0,1,1,'在爆破区域、岩体变形区域、滑坡危险区域内不得建永久性建（构）筑物。',6,'admin','2012-09-01 18:42:36','2012-09-01 18:44:04','admin'),(184,'第五百四十二条',102,'0001-0001-0003-0001-0009',0,1,1,'机械设备的供电电缆必须绝缘良好，电缆横过铁路、公路时，必须采取防护措施。\r\n机械设备内必须备有完好的绝缘防护用品和工具，并定期进行电气绝缘性能试验，不合格的及时更换。\r\n',6,'admin','2012-09-01 18:42:46','2012-09-01 18:44:04','admin'),(185,'第五百四十三条',102,'0001-0001-0003-0001-00010',0,1,1,'电气设备应安设过流、过压、漏电、接地等保护装置，并灵敏可靠。',4,'admin','2012-09-01 18:43:17','2012-09-01 18:44:04','admin'),(186,'第五百四十四条',102,'0001-0001-0003-0001-0011',0,1,1,'采掘、运输、排土等机械设备作业时，严禁人员上下设备；在危及人身安全的作业范围内，严禁人员停留或通过。',3,'admin','2012-09-01 18:43:30','2012-09-01 18:44:04','admin'),(187,'第五百四十五条',102,'0001-0001-0003-0001-0012',0,1,1,'在大雾、烟尘等能见度低的情况下作业时，应制定安全技术措施。',2,'admin','2012-09-01 18:43:41','2012-09-01 18:44:04','admin'),(188,'第五百四十六条',102,'0001-0001-0003-0001-0013',0,1,1,'作业人员在超过２m的高空作业时，应佩带安全带或设置安全网。在露天进行起重和高空作业时，遇有六级以上大风必须制定安全措施。',1,'admin','2012-09-01 18:43:54','2012-09-01 18:44:04','admin'),(189,'第五百四十七条',102,'0001-0001-0003-0001-0014',0,1,1,'井工开采形成的老空威胁露天煤矿安全时，应制定安全措施。',0,'admin','2012-09-01 18:44:04',NULL,NULL),(190,'第一百七十六条',143,'0001-0001-0002-0004-0001-0001',0,1,1,'1',10,'admin','2012-09-10 22:10:11','2012-09-10 22:13:46','admin'),(191,'第一百七十七条',143,'0001-0001-0002-0004-0001-0002',0,1,1,'突出矿井在编制年度、季度、月生产建设计划的同时，必须编制防治突出措施计划。\r\n开采突出煤层时，必须采取突出危险性预测、防治突出措施、防治突出措施的效果检验、安全防护措施等综合防治突出措施。\r\n',8,'admin','2012-09-10 22:11:34','2012-09-10 22:13:46','admin'),(192,'第一百七十八条',143,'0001-0001-0002-0004-0001-0003',0,1,1,'煤矿企业应掌握突出动态和规律、填写突出卡片、积累资料、总结经验教训、制定防治突出措施，在每年第一季度内，将上年度的突出资料报省（自治区、直辖市）负责煤炭行业管理的部门和全国突出档案室。',7,'admin','2012-09-10 22:12:03','2012-09-10 22:13:46','admin'),(193,'第一百七十九条',143,'0001-0001-0002-0004-0001-0004',0,1,1,'对有突出危险的新建矿井，突出矿井的新水平、新采区，必须编制防治突出煤层突出的设计。设计应包括开拓方式、煤层开采程序、采煤方法、通风方式、支护形式、突出危险性预测方法、保护层的选择、抽放瓦斯和局部防治突出措施等内容。',6,'admin','2012-09-10 22:12:15','2012-09-10 22:13:46','admin'),(194,'第一百八十条',143,'0001-0001-0002-0004-0001-0005',0,1,1,'（一）主要巷道应布置在岩层或非突出煤层中。应尽可能减少突出煤层中的掘进工作量。开采保护层的采区，应充分利用保护层的保护范围。\r\n（二）应尽可能减少石门揭穿突出煤层的次数，揭穿突出煤层地点应避开地质构造带。如果条件许可，应尽量将石门布置在被保护区，或先掘出揭煤地点的煤层巷道，然后再与石门贯通。石门与突出煤层中已掘巷道贯通时，被贯通巷道应超过石门贯通位置5m以上、并保持正常通风。\r\n（三）在同一突出煤层的同一区段的集中应力影响范围内，不得布置2个工作面相向回采或掘进。突出煤层的掘进工作面，应避开本煤层或邻近煤层采煤工作面的应力集中范围。\r\n',5,'admin','2012-09-10 22:12:38','2012-09-10 22:13:46','admin'),(195,'第一百八十一条',143,'0001-0001-0002-0004-0001-0006',0,1,1,'突出矿井必须及时编制矿井瓦斯地质图，图中应标明采掘进度、被保护范围、煤层赋存条件、地质构造、突出点的位置、突出强度、瓦斯基本参数等，作为突出危险性区域预测和制定防治突出措施的依据。\r\n在突出煤层顶底板掘进岩巷时，必须定期验证地质资料，及时掌握施工动态和围岩变化情况，防止误穿突出煤层。\r\n',4,'admin','2012-09-10 22:12:53','2012-09-10 22:13:46','admin'),(196,'第一百八十二条',143,'0001-0001-0002-0004-0001-0007',0,1,1,'开采突出煤层时，每个采掘工作面的专职瓦斯检查工必须随时检查瓦斯，掌握突出预兆。当发现有突出预兆时，瓦斯检查工有权停止工作面作业，并协助班组长立即组织人员按避灾路线撤出、报告矿调度室。',3,'admin','2012-09-10 22:13:06','2012-09-10 22:13:46','admin'),(197,'第一百八十三条',143,'0001-0001-0002-0004-0001-0008',0,1,1,'突出煤层中的突出危险区、突出威胁区，严禁采用放顶煤采煤法、水力采煤法、非正规采煤法采煤。\r\n突出煤层中的突出危险区、突出威胁区的采掘工作面严禁使用风镐作业。\r\n',2,'admin','2012-09-10 22:13:19','2012-09-10 22:13:46','admin'),(198,'第一百八十四条',143,'0001-0001-0002-0004-0001-0009',0,1,1,'有突出危险的采掘工作面爆破落煤前，所有不装药的眼、孔都应用不燃性材料充填，充填深度应不小于爆破孔深度的1.5倍。\r\n对采用直径大于120mm的钻孔、水力冲刷或水力冲孔等措施在煤体中形成的孔洞，在爆破前应严密封闭孔口，孔内注满水、砂或填土。\r\n',1,'admin','2012-09-10 22:13:32','2012-09-10 22:13:46','admin'),(199,'第一百八十五条',143,'0001-0001-0002-0004-0001-00010',0,1,1,'煤与二氧化碳突出、岩石与二氧化碳突出的管理和防治措施参照本章规定执行。',0,'admin','2012-09-10 22:13:46',NULL,NULL),(200,'第一百八十六条',144,'0001-0001-0002-0004-0002-0001',0,1,1,'突出矿井必须对突出煤层进行区域突出危险性预测（简称区域预测）和工作面突出危险性预测（简称工作面预测）。\r\n突出煤层经区域预测可划分为突出危险区、突出威胁区和无突出危险区。采掘工作面经工作面预测可划分为突出危险工作面和无突出危险工作面。\r\n对采掘工作面实施防治突出措施后，应按工作面预测方法进行措施效果检验。措施效果检验指标都在该煤层突出危险临界值以下的，认为措施有效。\r\n',5,'admin','2012-09-10 22:14:05','2012-09-10 22:15:37','admin'),(201,'第一百八十七条',144,'0001-0001-0002-0004-0002-0002',0,1,1,'在突出威胁区内，根据煤层突出危险程度，采掘工作面每推进30～100m应用工作面预测方法连续进行不少于2次的区域性预测验证，其中任何1次验证为有突出危险时，该区域应改划为突出危险区。\r\n在无突出危险区内，可不采取防治突出措施。\r\n',4,'admin','2012-09-10 22:14:19','2012-09-10 22:15:37','admin'),(202,'第一百八十八条',144,'0001-0001-0002-0004-0002-0003',0,1,1,'在突出危险区内进行采掘作业时，必须采取综合防治突出措施。当预测为突出危险工作面时，应采取防治突出措施，只有经措施效果检验证实措施有效后，方可在采取安全防护措施的情况下进行采掘作业。\r\n每执行1次防治突出措施作业循环后，应再进行工作面预测，如预测为无突出危险，仍必须再采取防治突出措施，只有连续2次预测为无突出危险，该工作面方可视为无突出危险工作面。\r\n预测为无突出危险工作面，每预测循环应留有不小于2m的预测超前距。\r\n在无突出危险工作面进行采掘作业时，可不采取防治突出措施，但必须采取安全防护措施。\r\n',3,'admin','2012-09-10 22:14:55','2012-09-10 22:15:37','admin'),(203,'第一百八十九条',144,'0001-0001-0002-0004-0002-0004',0,1,1,'保护层的开采厚度等于或小于0.5m、上保护层与突出煤层间距大于50m或下保护层与突出煤层间距大于80m时，必须对保护层的保护效果进行检验。\r\n矿井首次开采保护层时，必须进行保护效果及保护范围的实际考察，并不断积累、补充和完善资料，以便得出保护效果及保护范围的参数。\r\n',2,'admin','2012-09-10 22:15:12','2012-09-10 22:15:37','admin'),(204,'第一百九十条',144,'0001-0001-0002-0004-0002-0005',0,1,1,'预抽煤层瓦斯后，必须对预抽瓦斯防治突出效果进行检验，其有效性指标应根据矿井实测资料确定。如无实测数据，可依据下列指标之一确定:\r\n（一）预抽煤层瓦斯后，突出煤层的残存瓦斯含量小于该煤层始突深度的原始瓦斯含量。\r\n（二）煤层瓦斯预抽率大于30%。\r\n采用煤层瓦斯预抽率作为有效性指标的突出煤层，在进行采掘作业时，必须采用工作面预测方法对预抽效果进行经常复验。\r\n',1,'admin','2012-09-10 22:15:22','2012-09-10 22:15:37','admin'),(205,'第一百九十一条',144,'0001-0001-0002-0004-0002-0006',0,1,1,'掘进工作面防治突出措施效果检验有效时，允许的进尺量必须同时保证在巷道轴线方向留有不少于5m的措施孔超前距和不少于2m的检验孔超前距。\r\n采煤工作面防治突出措施效果检验有效时，允许的推进进度必须同时满足留有不少于3m的措施孔超前距和不少于2m的检验孔超前距。\r\n当防突措施无效时，不论措施孔还留有多少超前距，都必须采取防治突出的补充措施，只有经措施效果检验有效后，方可在采取安全防护措施的前提下进行采掘作业。\r\n',0,'admin','2012-09-10 22:15:37',NULL,NULL),(206,'第一百九十二条',145,'0001-0001-0002-0004-0003-0001',0,1,1,'对于有突出危险煤层，应采取开采保护层或预抽煤层瓦斯等区域性防治突出措施。',6,'admin','2012-09-10 22:15:56','2012-09-10 22:17:14','admin'),(207,'第一百九十三条',145,'0001-0001-0002-0004-0003-0002',0,1,1,'在突出矿井开采煤层群时，应优先选择开采保护层防治突出措施。开采保护层后，在被保护层中受到保护的区域可按无突出危险区进行采掘作业；在未受到保护的区域，必须采取综合防治突出措施。',5,'admin','2012-09-10 22:16:12','2012-09-10 22:17:14','admin'),(208,'第一百九十四条',145,'0001-0001-0002-0004-0003-0003',0,1,1,'选择保护层应遵循下列原则：\r\n（一）优先选择无突出危险的煤层作为保护层。矿井中所有煤层都有突出危险时，应选择突出危险程度较小的煤层作保护层。\r\n（二）应优先选择上保护层；选择下保护层开采时，不得破坏被保护层的开采条件。\r\n',4,'admin','2012-09-10 22:16:27','2012-09-10 22:17:14','admin'),(209,'第一百九十五条',145,'0001-0001-0002-0004-0003-0004',0,1,1,'被保护范围的划定方法及有关参数，应根据对矿井实际考察的结果确定。正在开采的保护层采煤工作面，必须超前于被保护层的掘进工作面，其超前距离不得小于保护层与被保护层之间法线距离的2倍，并不得小于30m。',3,'admin','2012-09-10 22:16:39','2012-09-10 22:17:14','admin'),(210,'第一百九十六条',145,'0001-0001-0002-0004-0003-0005',0,1,1,'开采突出厚煤层时，可利用上分层或上区段开采后形成的卸压作用保护下分层或下阶段，但应依据对上分层或上区段卸压范围的考察结果确定其保护范围，必须将下分层或下区段的采掘工作面布置在保护范围内。',2,'admin','2012-09-10 22:16:50','2012-09-10 22:17:14','admin'),(211,'第一百九十七条',145,'0001-0001-0002-0004-0003-0006',0,1,1,'开采保护层时采空区内不得留有煤（岩）柱；特殊情况需留煤（岩）柱时，必须将煤（岩）柱的位置和尺寸准确地标在采掘平面图上。每个被保护层的瓦斯地质图上，应标出煤（岩）柱的影响范围，在这个范围内进行采掘工作时，必须采取综合防治突出措施。',1,'admin','2012-09-10 22:17:03','2012-09-10 22:17:14','admin'),(212,'第一百九十八条',145,'0001-0001-0002-0004-0003-0007',0,1,1,'开采保护层时，应同时抽放被保护层的瓦斯。开采近距离保护层时，必须采取措施严防被保护层初期卸压的瓦斯突然涌入保护层采掘工作面和误穿突出煤层。',0,'admin','2012-09-10 22:17:14',NULL,NULL),(213,'第二百条',146,'0001-0001-0002-0004-0004-0001',0,1,1,'',9,'admin','2012-09-10 22:17:41','2012-09-10 22:20:03','admin'),(214,'第二百零一条',146,'0001-0001-0002-0004-0004-0002',0,1,1,'石门揭穿（开）突出煤层前，当预测为突出危险工作面时，必须采取防治突出措施，经检验措施有效后，方可用远距离爆破揭穿（开）煤层；若经检验措施无效，必须采取补充防治突出措施直至有效。当预测为无突出危险工作面时，方可直接采用远距离爆破或震动爆破揭穿（开）煤层。”\r\n厚度小于0.3m的突出煤层，可直接采用震动爆破或远距离爆破揭穿。\r\n',8,'admin','2012-09-10 22:17:56','2012-09-10 22:20:03','admin'),(215,'第二百零二条',146,'0001-0001-0002-0004-0004-0003',0,1,1,'防治石门突出措施可选用抽放瓦斯、水力冲孔、排放钻孔、水力冲刷或金属骨架等措施。',6,'admin','2012-09-10 22:18:35','2012-09-10 22:20:03','admin'),(216,'第二百零三条',146,'0001-0001-0002-0004-0004-0004',0,1,1,'有突出危险的新建矿井或突出矿井开拓的新水平的井巷第一次揭穿（开）各煤层时，必须测定煤层瓦斯压力、瓦斯含量及其他与突出危险性相关的参数。',5,'admin','2012-09-10 22:18:54','2012-09-10 22:20:03','admin'),(217,'第二百零四条',146,'0001-0001-0002-0004-0004-0005',0,1,1,'突出煤层采掘工作面局部防治突出措施参数，应根据矿井实际测定的结果或参照有关资料确定。',4,'admin','2012-09-10 22:19:07','2012-09-10 22:20:03','admin'),(218,'第二百零五条',146,'0001-0001-0002-0004-0004-0006',0,1,1,'突出煤层的采掘工作面，应根据煤层实际情况选用防治突出措施，并遵守下列规定：\r\n（一）掘进上山时不应采取松动爆破、水力冲孔、水力疏松等措施。\r\n（二）在急倾斜煤层中掘进上山时，应采用双上山、伪倾斜上山或直径在300mm以上的钻孔等掘进方式，并加强支护。\r\n（三）采煤工作面应尽量采用刨煤机或浅截深采煤机采煤。\r\n（四）急倾斜突出煤层厚度大于0.8m时，应优先采用伪倾斜正台阶、掩护支架采煤法等。对于急倾斜突出煤层倒台阶采煤工作面，应尽量加大各个台阶高度，尽量缩小台阶宽度，每个台阶的底脚必须背紧背严，落煤后，必须及时紧贴煤壁支护。\r\n在过突出孔洞及在其附近30m范围内进行采掘作业时，必须加强支护。\r\n',3,'admin','2012-09-10 22:19:21','2012-09-10 22:20:03','admin'),(219,'第二百零六条',146,'0001-0001-0002-0004-0004-0007',0,1,1,'在煤巷掘进工作面第一次执行局部防治突出措施或无措施超前距时，必须采取小直径浅孔排放等防治突出措施，只有在工作面前方形成5m的安全屏障后，方可进入正常防突措施循环。\r\n在掘进工作面执行上述措施时，钻孔终孔位置应控制到巷道轮廓线外2m以上。\r\n',2,'admin','2012-09-10 22:19:36','2012-09-10 22:20:03','admin'),(220,'第二百零七条',146,'0001-0001-0002-0004-0004-0008',0,1,1,'在急倾斜突出煤层中采用双上山掘进时，2个上山之间应开联络巷，联络巷间距不得大于10m，上山与联络巷只准1个工作面作业。\r\n急倾斜突出煤层上山掘进工作面，应采用阻燃抗静电的硬质风筒通风。\r\n突出煤层上山掘进工作面采用爆破作业时，应采用深度不大于1.0m的炮眼远距离全断面一次爆破。\r\n',1,'admin','2012-09-10 22:19:50','2012-09-10 22:20:03','admin'),(221,'第二百零八条',146,'0001-0001-0002-0004-0004-0009',0,1,1,'在突出煤层的煤巷中，更换、维修或回收支架时，必须采取预防煤体冒落引起突出的措施。',0,'admin','2012-09-10 22:20:03',NULL,NULL),(222,'第二百零九条',147,'0001-0001-0002-0004-0005-0001',0,1,1,'井巷揭穿突出煤层和在突出煤层中进行采掘作业时，必须采取远距离爆破、避难硐室、反向风门、压风自救系统等安全防护措施。突出矿井的入井人员必须携带隔离式自救器；采掘工作业时，隔离式自救器应当悬挂或存放在其3m的范围内。\r\n',5,'admin','2012-09-10 22:20:38','2012-09-10 22:22:00','admin'),(223,'第二百一十条',147,'0001-0001-0002-0004-0005-0002',0,1,1,'',4,'admin','2012-09-10 22:20:53','2012-09-10 22:22:00','admin'),(224,'第二百一十一条',147,'0001-0001-0002-0004-0005-0003',0,1,1,'在突出矿井的突出危险区，掘进工作面进风侧必须设置至少2道牢固可靠的反向风门。反向风门距工作面的距离，应根据掘进工作面的通风系统和预计的突出强度确定。',3,'admin','2012-09-10 22:21:06','2012-09-10 22:22:00','admin'),(225,'第二百一十二条',147,'0001-0001-0002-0004-0005-0004',0,1,1,'石门揭煤采用远距离爆破时，必须制定包括爆破地点，避灾路线及停电、撤人和警戒范围等的专门措施。\r\n煤巷掘进工作面采用远距离爆破时，爆破地点必须设在进风侧反向风门之外的全风压通风的新鲜风流中或避难硐室内，爆破地点距工作面的距离必须在措施中明确规定。\r\n远距离爆破时，回风系统必须停电撤人。爆破后，进入工作面检查的时间应在措施中明确规定，但不得小于30min。\r\n',2,'admin','2012-09-10 22:21:34','2012-09-10 22:22:00','admin'),(226,'第二百一十三条',147,'0001-0001-0002-0004-0005-0005',0,1,1,'在突出煤层采掘工作面附近、爆破时撤离人员集中地点必须设有直通矿调度室的电话，并设置有供给压缩空气设施的避难硐室或压风自救系统。工作面回风系统中有人作业的地点，也应设置压风自救系统。',1,'admin','2012-09-10 22:21:46','2012-09-10 22:22:00','admin'),(227,'第二百一十四条',147,'0001-0001-0002-0004-0005-0006',0,1,1,'突出的煤必须及时清理，以防自燃引起瓦斯煤尘爆炸。清理突出的煤时，必须制定防煤尘、片帮、冒顶以及瓦斯超限、出现火源、再次发生事故的安全措施。',0,'admin','2012-09-10 22:22:00',NULL,NULL),(228,'第二百一十五条',148,'0001-0001-0002-0005-0001-0001',0,1,1,'生产和在建矿井必须制定井上、下防火措施。矿井的所有地面建筑物、煤堆、矸石山、木料场等处的防火措施和制度，必须符合国家有关防火的规定。',12,'admin','2012-09-10 22:28:59','2012-09-10 22:31:37','admin'),(229,'第二百一十六条',148,'0001-0001-0002-0005-0001-0002',0,1,1,'木料场、矸石山、炉灰场距离进风井不得小于80m。木料场距离矸石山不得小于50m。\r\n不得将矸石山或炉灰场设在进风井的主导风向上风侧，也不得设在表土10m以内有煤层的地面上和设在有漏风的采空区上方的塌陷范围内。\r\n',11,'admin','2012-09-10 22:29:10','2012-09-10 22:31:37','admin'),(230,'第二百一十七条',148,'0001-0001-0002-0005-0001-0003',0,1,1,'新建矿井的永久井架和井口房、以井口为中心的联合建筑，必须用不燃性材料建筑。\r\n对现有生产矿井用可燃性材料建筑的井架和井口房，必须制定防火措施。\r\n',10,'admin','2012-09-10 22:29:21','2012-09-10 22:31:37','admin'),(231,'第二百一十八条',148,'0001-0001-0002-0005-0001-0004',0,1,1,'矿井必须设地面消防水池和井下消防管路系统。井下消防管路系统应每隔100m设置支管和阀门，但在带式输送机巷道中应每隔50m设置支管和阀门。地面的消防水池必须经常保持不少于200m3的水量。如果消防用水同生产、生活用水共用同一水池，应有确保消防用水的措施。\r\n开采下部水平的矿井，除地面消防水池外，可利用上部水平或生产水平的水仓作为消防水池。\r\n',9,'admin','2012-09-10 22:29:32','2012-09-10 22:31:37','admin'),(232,'第二百一十九条',148,'0001-0001-0002-0005-0001-0005',0,1,1,'进风井口应装设防火铁门，防火铁门必须严密并易于关闭，打开时不妨碍提升、运输和人员通行，并应定期维修；如果不设防火铁门，必须有防止烟火进入矿井的安全措施。',8,'admin','2012-09-10 22:29:42','2012-09-10 22:31:37','admin'),(233,'第二百二十条',148,'0001-0001-0002-0005-0001-0006',0,1,1,'井口房和通风机房附近20m内，不得有烟火或用火炉取暖。通风机房位于工业广场以外时，除开采有瓦斯喷出区域的矿井和煤（岩）与瓦斯突出矿井外，可用隔焰式火炉或防爆式电热器取暖。\r\n暖风道和压入式通风的风硐必须用不燃性材料砌筑，并应至少装设2道防火门。\r\n',7,'admin','2012-09-10 22:29:53','2012-09-10 22:31:37','admin'),(234,'第二百二十一条',148,'0001-0001-0002-0005-0001-0007',0,1,1,'井筒、平硐与各水平的连接处及井底车场，主要绞车道与主要运输巷、回风巷的连接处，井下机电设备硐室，主要巷道内带式输送机机头前后两端各20m范围内，都必须用不燃性材料支护。\r\n在井下和井口房，严禁采用可燃性材料搭设临时操作间、休息间。\r\n',6,'admin','2012-09-10 22:30:04','2012-09-10 22:31:37','admin'),(235,'第二百二十二条',148,'0001-0001-0002-0005-0001-0008',0,1,1,'井下严禁使用灯泡取暖和使用电炉。',5,'admin','2012-09-10 22:30:18','2012-09-10 22:31:37','admin'),(236,'第二百二十三条',148,'0001-0001-0002-0005-0001-0009',0,1,1,'',4,'admin','2012-09-10 22:30:36','2012-09-10 22:31:37','admin'),(237,'第二百二十四条',148,'0001-0001-0002-0005-0001-00010',0,1,1,'井下使用的汽油、煤油和变压器油必须装入盖严的铁桶内，由专人押运送至使用地点，剩余的汽油、煤油和变压器油必须运回地面，严禁在井下存放。\r\n井下使用的润滑油、棉纱、布头和纸等，必须存放在盖严的铁桶内。用过的棉纱、布头和纸，也必须放在盖严的铁桶内，并由专人定期送到地面处理，不得乱放乱扔。严禁将剩油、废油泼洒在井巷或硐室内。\r\n井下清洗风动工具时，必须在专用硐室进行，并必须使用不燃性和无毒性洗涤剂。\r\n',3,'admin','2012-09-10 22:30:49','2012-09-10 22:31:37','admin'),(238,'第二百二十五条',148,'0001-0001-0002-0005-0001-0011',0,1,1,'第二百二十五条',2,'admin','2012-09-10 22:31:02','2012-09-10 22:31:37','admin'),(239,'第二百二十六条',148,'0001-0001-0002-0005-0001-0012',0,1,1,'井下爆炸材料库、机电设备硐室、检修硐室、材料库、井底车场、使用带式输送机或液力偶合器的巷道以及采掘工作面附近的巷道中，应备有灭火器材，其数量、规格和存放地点，应在灾害预防和处理计划中确定。\r\n井下工作人员必须熟悉灭火器材的使用方法，并熟悉本职工作区域内灭火器材的存放地点。\r\n',1,'admin','2012-09-10 22:31:17','2012-09-10 22:31:37','admin'),(240,'第二百二十七条',148,'0001-0001-0002-0005-0001-0013',0,1,1,'每季度应对井上、下消防管路系统，防火门，消防材料库和消防器材的设置情况进行1次检查，发现问题，及时解决。',0,'admin','2012-09-10 22:31:37',NULL,NULL),(241,'第二百二十八条',149,'0001-0001-0002-0005-0002-0001',0,1,1,'煤的自燃倾向性分为容易自燃、自燃、不易自燃三类。\r\n新建矿井的所有煤层的自燃倾向性由地质勘探部门提供煤样和资料，送国家授权单位作出鉴定，鉴定结果报省级煤矿安全监察机构及省（自治区、直辖市）负责煤炭行业管理的部门备案。\r\n生产矿井延深新水平时，必须对所有煤层的自燃倾向性进行鉴定。\r\n开采容易自燃和自燃煤层的矿井，必须采取综合预防煤层自然发火的措施。\r\n',17,'admin','2012-09-10 22:31:56','2012-09-10 22:34:24','admin'),(242,'第二百二十九条',149,'0001-0001-0002-0005-0002-0002',0,1,1,'对开采容易自燃和自燃的单一厚煤层或煤层群的矿井，集中运输大巷和总回风巷应布置在岩层内或不易自燃的煤层内；如果布置在容易自燃和自燃的煤层内，必须砌碹或锚喷，碹后的空隙和冒落处必须用不燃性材料充填密实，或用无腐蚀性、无毒性的材料进行处理。',16,'admin','2012-09-10 22:32:21','2012-09-10 22:34:24','admin'),(243,'第二百三十条',149,'0001-0001-0002-0005-0002-0003',0,1,1,'开采容易自燃和自燃的煤层（薄煤层除外）时，采煤工作面必须采用后退式开采，并根据采取防火措施后的煤层自然发火期确定采区开采期限。在地质构造复杂、断层带、残留煤柱等区域开采时，应根据矿山地质和开采技术条件，在作业规程中另行确定采区开采方式和开采期限。回采过程中不得任意留设设计外煤柱和顶煤。采煤工作面采到停采线时，必须采取措施使顶板冒落严实。',15,'admin','2012-09-10 22:32:32','2012-09-10 22:34:24','admin'),(244,'第二百三十一条',149,'0001-0001-0002-0005-0002-0004',0,1,1,'开采容易自燃和自燃的急倾斜煤层用垮落法控制顶板时，在主石门和采区运输石门上方，必须留有煤柱。禁止采掘留在主石门上方的煤柱。留在采区运输石门上方的煤柱，在采区结束后可回收，但必须采取防止自然发火措施。',14,'admin','2012-09-10 22:32:43','2012-09-10 22:34:24','admin'),(245,'第二百三十二条',149,'0001-0001-0002-0005-0002-0005',0,1,1,'开采容易自燃和自燃的煤层时，必须对采空区、突出和冒落孔洞等空隙采取预防性灌浆或全部充填、喷洒阻化剂、注阻化泥浆、注凝胶、注惰性气体、均压等措施，编制相应的防灭火设计，防止自然发火。\r\n在自然发火期内能采完、并能及时予以封闭的工作面和采区，可不采取上述防止自然发火的措施。\r\n',13,'admin','2012-09-10 22:32:55','2012-09-10 22:34:24','admin'),(246,'第二百三十三条',149,'0001-0001-0002-0005-0002-0006',0,1,1,'采用灌浆防灭火时，应遵守下列规定：\r\n（一）采区设计必须明确规定巷道布置方式、隔离煤柱尺寸、灌浆系统、疏水系统、预筑防火墙的位置以及采掘顺序。\r\n（二）安排生产计划时，必须同时安排防火灌浆计划，落实灌浆地点、时间、进度、灌浆浓度和灌浆量。\r\n（三）对采区开采线、停采线、上下煤柱线内的采空区，应加强防火灌浆。\r\n（四）应有灌浆前疏水和灌浆后防止溃浆、透水的措施。\r\n',12,'admin','2012-09-10 22:33:06','2012-09-10 22:34:24','admin'),(247,'第二百三十四条',149,'0001-0001-0002-0005-0002-0007',0,1,1,'在灌浆区下部进行采掘前，必须查明灌浆区内的浆水积存情况。发现积存浆水，必须在采掘之前放出；在未放出前，严禁在灌浆区下部进行采掘工作。',12,'admin','2012-09-10 22:33:16','2012-11-14 22:09:31','admin'),(248,'第二百三十五条',149,'0001-0001-0002-0005-0002-0008',0,1,1,'采用阻化剂防灭火时，应遵守下列规定：\r\n（一）选用的阻化剂材料不得污染井下空气和危害人体健康。\r\n（二）必须在设计中对阻化剂的种类和数量、阻化效果等主要参数作出明确规定。\r\n（三）应采取防止阻化剂腐蚀机械设备、支架等金属构件的措施。\r\n',11,'admin','2012-09-10 22:33:22','2012-11-14 22:09:09','admin'),(249,'第二百三十六条',149,'0001-0001-0002-0005-0002-0009',0,1,1,'采用凝胶防灭火时，应遵守下列规定：\r\n（一）选用的凝胶和促凝剂材料，不得污染井下空气和危害人体健康，使用时井巷空气成分必须符合本规程第一百条的有关规定。\r\n（二）编制的设计中应明确规定凝胶的配方、促凝时间和压注量等参数。\r\n（三）压注的凝胶必须充填满全部空间，其外表面应予喷浆封闭，并定期观测，发现老化、干裂时，应予重新压注。\r\n',10,'admin','2012-09-10 22:33:28','2012-11-14 22:09:01','admin'),(250,'第二百三十七条',149,'0001-0001-0002-0005-0002-00010',0,1,1,'采用均压技术防灭火时，应遵守下列规定：\r\n（一）应有完整的区域风压和风阻资料以及完善的检测手段。\r\n（二）必须有专人定期观测与分析采空区和火区的漏风量、漏风方向、空气温度、防火墙内外空气压差等的状况，并记录在专用的防火记录簿内。\r\n（三）改变矿井通风方式、主要通风机工况以及井下通风系统时，对均压地点的均压状况必须及时进行调整，保证均压状态的稳定。\r\n（四）应经常检查均压区域内的巷道中风流流动状态，应有防止瓦斯积聚的安全措施。\r\n',9,'admin','2012-09-10 22:33:33','2012-11-14 22:08:53','admin'),(251,'第二百三十八条',149,'0001-0001-0002-0005-0002-0011',0,1,1,'采用氮气防灭火时，必须遵守下列规定：\r\n（一）氮气源稳定可靠。\r\n（二）注入的氮气浓度不小于97％。\r\n（三）至少有1套专用的氮气输送管路系统及其附属安全设施。\r\n（四）有能连续监测采空区气体成分变化的监测系统。\r\n（五）有固定或移动的温度观测站（点）和监测手段。\r\n（六）有专人定期进行检测、分析和整理有关记录、发现问题及时报告处理等规章制度。\r\n',8,'admin','2012-09-10 22:33:40','2012-11-14 22:08:41','admin'),(252,'第二百三十九条',149,'0001-0001-0002-0005-0002-0012',0,1,1,'开采容易自燃和自燃的煤层、采用全部充填采煤法时，不得采用可燃物作充填材料，采空区和三角点必须充满。',7,'admin','2012-09-10 22:33:46','2012-11-14 22:08:27','admin'),(253,'第二百四十条',149,'0001-0001-0002-0005-0002-0013',0,1,1,'开采容易自燃和自燃的煤层时，在采区开采设计中，必须预先选定构筑防火门的位置。当采煤工作面投产和通风系统形成后，必须按设计选定的防火门位置构筑好防火门墙，并储备足够数量的封闭防火门的材料。\r\n采煤工作面回采结束后，必须在45天内进行永久性封闭。\r\n',6,'admin','2012-09-10 22:33:53','2012-11-14 22:08:19','admin'),(254,'第二百四十一条',149,'0001-0001-0002-0005-0002-0014',0,1,1,'开采容易自燃和自燃的煤层时，在采区开采设计中，必须明确选定自然发火观测站或观测点的位置并建立监测系统、确定煤层自然发火的标志气体和建立自然发火预测预报制度。所有检测分析结果必须记录在专用的防火记录簿内，并定期检查、分析整理，发现自然发火指标超过或达到临界值等异常变化时，立即发出自然发火预报，采取措施进行处理。',5,'admin','2012-09-10 22:33:59','2012-11-14 22:08:10','admin'),(255,'第二百四十二条',149,'0001-0001-0002-0005-0002-0015',0,1,1,'采用放顶煤采煤法开采容易自燃和自燃的厚及特厚煤层时，必须编制防止采空区自然发火的设计，并遵守下列规定：\r\n（一）根据防火要求和现场条件，应选用注入惰性气体、灌注泥浆（包括粉煤灰泥浆）、压注阻化剂、喷浆堵漏及均压等综合防火措施。\r\n（二）有可靠的防止漏风和有害气体泄漏的措施。\r\n（三）建立完善的火灾监测系统。\r\n',4,'admin','2012-09-10 22:34:04','2012-11-14 22:07:53','admin'),(256,'第二百四十三条',149,'0001-0001-0002-0005-0002-0016',0,1,1,'在容易自燃和自燃的煤层中掘进巷道时，对巷道中出现的冒顶区必须及时进行防火处理，并定期检查。',4,'admin','2012-09-10 22:34:14','2012-11-14 22:08:03','admin'),(257,'第二百四十四条',149,'0001-0001-0002-0005-0002-0017',0,1,1,'任何人发现井下火灾时，应视火灾性质、灾区通风和瓦斯情况，立即采取一切可能的方法直接灭火，控制火势，并迅速报告矿调度室。矿调度室在接到井下火灾报告后，应立即按灾害预防和处理计划通知有关人员组织抢救灾区人员和实施灭火工作。\r\n矿值班调度和在现场的区、队、班组长应依照灾害预防和处理计划的规定，将所有可能受火灾威胁地区中的人员撤离，并组织人员灭火。电气设备着火时，应首先切断其电源；在切断电源前，只准使用不导电的灭火器材进行灭火。\r\n抢救人员和灭火过程中，必须指定专人检查瓦斯、一氧化碳、煤尘、其他有害气体和风向、风量的变化，还必须采取防止瓦斯、煤尘爆炸和人员中毒的安全措施。\r\n',2,'admin','2012-09-10 22:34:19','2012-11-14 22:07:28','admin'),(258,'第二百四十五条',149,'0001-0001-0002-0005-0002-0018',0,1,1,'封闭火区灭火时，应尽量缩小封闭范围，并必须指定专人检查瓦斯、氧气、一氧化碳、煤尘以及其他有害气体和风向、风量的变化，还必须采取防止瓦斯、煤尘爆炸和人员中毒的安全措施。',1,'admin','2012-09-10 22:34:24','2012-11-14 22:07:17','admin'),(259,'第二百四十六条',150,'0001-0001-0002-0005-0003-0001',0,1,1,'煤矿企业必须绘制火区位置关系图，注明所有火区和曾经发火的地点。每一处火区都要按形成的先后顺序进行编号，并建立火区管理卡片。火区位置关系图和火区管理卡片必须永久保存。',5,'admin','2012-09-10 22:34:38','2012-11-14 22:09:52','admin'),(260,'第二百四十七条',150,'0001-0001-0002-0005-0003-0002',0,1,1,'永久性防火墙的管理应遵守下列规定：',4,'admin','2012-09-10 22:34:44','2012-11-14 22:10:00','admin'),(261,'第二百四十八条',150,'0001-0001-0002-0005-0003-0003',0,1,1,'封闭的火区，只有经取样化验证实火已熄灭后，方可启封或注销。\r\n火区同时具备下列条件时，方可认为火已熄灭：\r\n（一）火区内的空气温度下降到30℃以下，或与火灾发生前该区的日常空气温度相同。\r\n（二）火区内空气中的氧气浓度降到5.0%以下。\r\n（三）火区内空气中不含有乙烯、乙炔，一氧化碳浓度在封闭期间内逐渐下降，并稳定在0.001%以下。\r\n（四）火区的出水温度低于25℃，或与火灾发生前该区的日常出水温度相同。\r\n（五）上述4项指标持续稳定的时间在１个月以上。\r\n',3,'admin','2012-09-10 22:34:51','2012-11-14 22:10:09','admin'),(262,'第二百四十九条',150,'0001-0001-0002-0005-0003-0004',0,1,1,'启封已熄灭的火区前，必须制定安全措施。\r\n启封火区时，应逐段恢复通风，同时测定回风流中有无一氧化碳。发现复燃征兆时，必须立即停止向火区送风，并重新封闭火区。\r\n启封火区和恢复火区初期通风等工作，必须由矿山救护队负责进行，火区回风风流所经过巷道中的人员必须全部撤出。\r\n在启封火区工作完毕后的3天内，每班必须由矿山救护队检查通风工作，并测定水温、空气温度和空气成分。只有在确认火区完全熄灭、通风等情况良好后，方可进行生产工作。\r\n',2,'admin','2012-09-10 22:35:00','2012-11-14 22:10:18','admin'),(263,'第二百五十条',150,'0001-0001-0002-0005-0003-0005',0,1,1,'不得在火区的同一煤层的周围进行采掘工作。\r\n在同一煤层同一水平的火区两侧、煤层倾角小于35°的火区下部区段、火区下方邻近煤层进行采掘时，必须编制设计，并遵守下列规定：\r\n（一）必须留有足够宽（厚）度的煤（岩）柱隔离火区，回采时及回采后能有效隔离火区，不影响火区的灭火工作。\r\n（二）掘进巷道时，必须有防止误冒、透火区的安全措施。\r\n煤层倾角在35°以上的火区下部区段严禁进行采掘工作。\r\n',1,'admin','2012-09-10 22:35:08','2012-11-14 22:10:25','admin'),(264,'第二百五十一条',151,'0001-0001-0002-0006-0001-0001',0,1,1,'不得在火区的同一煤层的周围进行采掘工作。\r\n在同一煤层同一水平的火区两侧、煤层倾角小于35°的火区下部区段、火区下方邻近煤层进行采掘时，必须编制设计，并遵守下列规定：\r\n（一）必须留有足够宽（厚）度的煤（岩）柱隔离火区，回采时及回采后能有效隔离火区，不影响火区的灭火工作。\r\n（二）掘进巷道时，必须有防止误冒、透火区的安全措施。\r\n煤层倾角在35°以上的火区下部区段严禁进行采掘工作。\r\n',3,'admin','2012-09-10 22:36:21','2012-11-14 22:10:38','admin'),(265,'第二百五十二条',151,'0001-0001-0002-0006-0001-0002',0,1,1,'水文地质条件复杂的矿井，必须针对主要含水层（段）建立地下水动态观测系统，进行地下水动态观测、水害预测分析，并制定相应的“探、防、堵、截、排”等综合防治措施。',2,'admin','2012-09-10 22:36:28','2012-11-14 22:10:45','admin'),(266,'第二百五十三条',151,'0001-0001-0002-0006-0001-0003',0,1,1,'煤矿企业每年雨季前必须对防治水工作进行全面检查。\r\n雨季受水威胁的矿井，应当制定雨季防治水措施，建立雨季巡视制度并组织抢险队伍，储备足够的防洪抢险物资。当暴雨威胁矿井安全时，必须立即停产撤出井下全部人员，只有在确认暴雨洪水隐患彻底消除后方可恢复生产。\r\n',1,'admin','2012-09-10 22:36:35','2012-11-14 22:10:53','admin'),(267,'第二百五十四条',152,'0001-0001-0002-0006-0002-0001',0,1,1,'煤矿企业必须查清矿区及其附近地面水流系统的汇水、渗漏情况，疏水能力和有关水利工程情况，掌握当地历年降水量和最高洪水位资料，建立疏水、防水和排水系统。',5,'admin','2012-09-10 22:36:47','2012-11-14 22:11:03','admin'),(268,'第二百五十五条',152,'0001-0001-0002-0006-0002-0002',0,1,1,'井口和工业场地内建筑物的高程必须高于当地历年最高洪水位；在山区还必须避开可能发生泥石流、滑坡的地段。\r\n井口及工业场地内建筑物的高程低于当地历年最高洪水位时，必须修筑堤坝、沟渠或采取其他防排水措施。\r\n',4,'admin','2012-09-10 22:36:58','2012-11-14 22:11:10','admin'),(269,'第二百五十六条',152,'0001-0001-0002-0006-0002-0003',0,1,1,'井口附近或塌陷区内外的地表水体可能溃入井下时，必须采取措施，并遵守下列规定：\r\n（一）严禁开采煤层露头的防水煤柱。\r\n（二）容易积水的地点应修筑沟渠，排泄积水。修筑沟渠时，应避开露头、裂隙和导水岩层。特别低洼地点不能修筑沟渠排水时，应填平压实；如果范围太大无法填平时，可建排洪站排水，防止积水渗入井下。\r\n（三）矿井受河流、山洪和滑坡威胁时，必须采取修筑堤坝、泄洪渠和防止滑坡的措施。\r\n（四）排到地面的矿井水，必须妥善处理，避免再渗入井下。\r\n（五）对漏水的沟渠和河床，应及时堵漏或改道。地面裂缝和塌陷地点必须填塞，填塞工作必须有安全措施，防止人员陷入塌陷坑内。\r\n（六）每次降大到暴雨时和降雨后，必须派专人检查矿区及其附近地面有无裂缝、老窑陷落和岩溶塌陷等现象。发现漏水情况，必须及时处理。\r\n',3,'admin','2012-09-10 22:37:04','2012-11-14 22:11:19','admin'),(270,'第二百五十七条',152,'0001-0001-0002-0006-0002-0004',0,1,1,'严禁将矸石、炉灰、垃圾等杂物堆放在山洪、河流可能冲刷到的地段。',2,'admin','2012-09-10 22:37:11','2012-11-14 22:11:28','admin'),(271,'第二百五十八条',152,'0001-0001-0002-0006-0002-0005',0,1,1,'使用中的钻孔，必须安装孔口盖。报废的钻孔必须及时封孔。',1,'admin','2012-09-10 22:37:17','2012-11-14 22:11:35','admin'),(272,'第二百五十九条',153,'0001-0001-0002-0006-0003-0001',0,1,1,'相邻矿井的分界处，必须留防水煤柱。矿井以断层分界时，必须在断层两侧留有防水煤柱。\r\n防水煤柱的尺寸，应根据相邻矿井的地质构造、水文地质条件、煤层赋存条件、围岩性质、开采方法以及岩层移动规律等因素，在矿井设计中规定。\r\n严禁在各种防隔水煤柱中采掘。\r\n',19,'admin','2012-09-10 22:37:28','2012-11-14 22:11:47','admin'),(273,'第二百六十条',153,'0001-0001-0002-0006-0003-0002',0,1,1,'井巷出水点的位置及其水量，有积水的井巷及采空区的积水范围、标高和积水量，必须绘在采掘工程平面图上。\r\n在水淹区域应标出探水线的位置。采掘到探水线位置时，必须探水前进。\r\n',18,'admin','2012-09-10 22:37:35','2012-11-14 22:11:53','admin'),(274,'第二百六十一条',153,'0001-0001-0002-0006-0003-0003',0,1,1,'每次降大到暴雨时和降雨后，应及时观测井下水文变化情况，并向矿调度室报告。',17,'admin','2012-09-10 22:37:43','2012-11-14 22:12:01','admin'),(275,'第二百六十二条',153,'0001-0001-0002-0006-0003-0004',0,1,1,'水淹区积水面以下的煤岩层中的采掘工作，应在排除积水以后进行；如果无法排除积水，必须编制设计，由企业主要负责人审批后，方可进行。',16,'admin','2012-09-10 22:37:49','2012-11-14 22:12:08','admin'),(276,'第二百六十三条',153,'0001-0001-0002-0006-0003-0005',0,1,1,'在有水或未固结的灌浆区、有淤泥的废弃井巷、岩石洞穴附近采掘时，必须执行本规程第二百六十条、第二百六十二条的规定。',15,'admin','2012-09-10 22:37:55','2012-11-14 22:12:14','admin'),(277,'第二百六十四条',153,'0001-0001-0002-0006-0003-0006',0,1,1,'开采水淹区域下的废弃防水煤柱时，必须制订安全措施，报企业技术负责人审批。',14,'admin','2012-09-10 22:38:00','2012-11-14 22:12:20','admin'),(278,'第二百六十五条',153,'0001-0001-0002-0006-0003-0007',0,1,1,'井田内有与河流、湖泊、溶洞、含水层等有水力联系的导水断层、裂隙（带）、陷落柱时，必须查出其确切位置，并按规定留设防水煤（岩）柱。\r\n巷道必须穿过上述构造时，必须探水前进。如果前方有水，应超前预注浆封堵加固，必要时预先建筑防水闸门或采取其他防治水措施。\r\n',13,'admin','2012-09-10 22:38:06','2012-11-14 22:12:26','admin'),(279,'第二百六十六条',153,'0001-0001-0002-0006-0003-0008',0,1,1,'采掘工作面或其他地点发现有挂红、挂汗、空气变冷、出现雾气、水叫、顶板淋水加大、顶板来压、底板鼓起或产生裂隙出现渗水、水色发浑、有臭味等突水预兆时，必须停止作业，采取措施，立即报告矿调度室，发出警报，撤出所有受水威胁地点的人员。',12,'admin','2012-09-10 22:38:17','2012-11-14 22:12:33','admin'),(280,'第二百六十七条',153,'0001-0001-0002-0006-0003-0009',0,1,1,'采掘工作面或其他地点发现有挂红、挂汗、空气变冷、出现雾气、水叫、顶板淋水加大、顶板来压、底板鼓起或产生裂隙出现渗水、水色发浑、有臭味等突水预兆时，必须停止作业，采取措施，立即报告矿调度室，发出警报，撤出所有受水威胁地点的人员。',11,'admin','2012-09-10 22:38:23','2012-11-14 22:12:40','admin'),(281,'第二百六十八条',153,'0001-0001-0002-0006-0003-00010',0,1,1,'煤层顶板有含水层和水体存在时，应当观测“三带”发育高度。当导水裂隙带范围内的含水层或老空积水影响安全开采时，必须超前探放水并建立疏排水系统。',10,'admin','2012-09-10 22:38:29','2012-11-14 22:12:47','admin'),(282,'第二百六十九条',153,'0001-0001-0002-0006-0003-0011',0,1,1,'承压含水层与开采煤层之间的隔水层能承受的水头值大于实际水头值时，可以“带水压开采”，但必须制订安全措施，报企业主要负责人审批。',9,'admin','2012-09-10 22:38:34','2012-11-14 22:12:53','admin'),(283,'第二百七十条',153,'0001-0001-0002-0006-0003-0012',0,1,1,'承压含水层与开采煤层之间的隔水层能承受的水头值小于实际水头值时，开采前必须采取下列措施，由企业主要负责人审批：\r\n（一）采取疏水降压的方法，把承压含水层的水头值降到隔水层能承受的安全水头值以下，并制订安全措施。\r\n（二）承压含水层不具备疏水降压条件时，必须采取建筑防水闸门、注浆加固底板、留设防水煤柱、增加抗灾强排能力等防水措施。',8,'admin','2012-09-10 22:38:39','2012-11-14 22:13:02','admin'),(284,'第二百七十一条',153,'0001-0001-0002-0006-0003-0013',0,1,1,'水文地质条件复杂的矿井，当开拓到设计水平，只有在建成防、排水系统后，方可开始向有突水危险地区开拓掘进。',7,'admin','2012-09-10 22:38:45','2012-11-14 22:13:09','admin'),(285,'第二百七十二条',153,'0001-0001-0002-0006-0003-0014',0,1,1,'煤系底部有强岩溶承压含水层时，主要运输巷和主要回风巷必须布置在不受水威胁的层位中，并以石门分区隔离开采。',6,'admin','2012-09-10 22:38:54','2012-11-14 22:13:17','admin'),(286,'第二百七十三条',153,'0001-0001-0002-0006-0003-0015',0,1,1,'',4,'admin','2012-09-10 22:38:59','2012-09-10 22:39:23','admin'),(287,'第二百七十四条',153,'0001-0001-0002-0006-0003-0016',0,1,1,'井下防水闸墙的设置应当根据矿井水文地质情况决定，防水闸墙的设计经煤矿企业技术负责人批准后方可施工，投入使用前应当由煤矿企业技术负责人组织竣工验收。',4,'admin','2012-09-10 22:39:05','2012-11-14 22:13:39','admin'),(288,'第二百七十五条',153,'0001-0001-0002-0006-0003-0017',0,1,1,'井筒穿过含水层段的井壁结构应采用防水混凝土或设置隔水层。',3,'admin','2012-09-10 22:39:11','2012-11-14 22:13:46','admin'),(289,'第二百七十六条',153,'0001-0001-0002-0006-0003-0018',0,1,1,'井巷揭穿含水层、地质构造带前，必须编制探放水和注浆堵水设计。\r\n井巷揭露的主要出水点或地段，必须进行水温、水量、水质等地下水动态和松散含水层涌水含砂量综合观测和分析，防止滞后突水。\r\n',2,'admin','2012-09-10 22:39:15','2012-11-14 22:13:55','admin'),(290,'第二百七十七条',153,'0001-0001-0002-0006-0003-0019',0,1,1,'立井基岩段施工应遵循快速、打干井的原则，并遵守下列规定：\r\n（一）单层涌水量小于10m3/h的含水层段，应强行穿过。\r\n（二）单层涌水量大于10m3/h的含水层段，应预注浆堵水。\r\n（三）单层涌水量大于10m3/h，且含水层层数多，层段又较集中的地段，应进行地面预注浆。\r\n（四）单层涌水量大于10m3/h，但含水层层数少，或层段分散的地段，应进行工作面预注浆或短探、短注、短掘。\r\n',1,'admin','2012-09-10 22:39:23','2012-11-14 22:14:03','admin'),(291,'第二百七十八条',154,'0001-0001-0002-0006-0004-0001',0,1,1,'主要排水设备应符合下列要求：',7,'admin','2012-09-10 22:40:04','2012-11-14 22:14:18','admin'),(292,'第二百七十九条',154,'0001-0001-0002-0006-0004-0002',0,1,1,'主要泵房至少有2个出口，一个出口用斜巷通到井筒，并应高出泵房底板7m以上；另一个出口通到井底车场，在此出口通路内，应设置易于关闭的既能防水又能防火的密闭门。泵房和水仓的连接通道，应设置可靠的控制闸门。',6,'admin','2012-09-10 22:40:11','2012-11-14 22:14:26','admin'),(293,'第二百八十条',154,'0001-0001-0002-0006-0004-0003',0,1,1,'主要水仓必须有主仓和副仓，当一个水仓清理时，另一个水仓能正常使用。\r\n新建、改扩建矿井或生产矿井的新水平，正常涌水量在1000m3/h以下时，主要水仓的有效容量应能容纳8h的正常涌水量。\r\n正常涌水量大于1000m3/h的矿井，主要水仓有效容量可按下式计算：\r\nＶ＝2（Q+3000）\r\n式中  V--主要水仓的有效容量，m3；\r\n     Q--矿井每小时正常涌水量，m3。\r\n但主要水仓的总有效容量不得小于4h的矿井正常涌水量。\r\n采区水仓的有效容量应能容纳4h的采区正常涌水量。\r\n矿井最大涌水量和正常涌水量相差特大的矿井，对排水能力、水仓容量应编制专门设计。\r\n水仓进口处应设置箅子。对水砂充填、水力采煤和其他涌水中带有大量杂质的矿井，还应设置沉淀池。水仓的空仓容量必须经常保持在总容量的50%以上。\r\n',5,'admin','2012-09-10 22:40:16','2012-11-14 22:14:33','admin'),(294,'第二百八十一条',154,'0001-0001-0002-0006-0004-0004',0,1,1,'水泵、水管、闸阀、排水用的配电设备和输电线路，必须经常检查和维护。在每年雨季以前，必须全面检修1次，并对全部工作水泵和备用水泵进行1次联合排水试验，发现问题，及时处理。\r\n水仓、沉淀池和水沟中的淤泥，应及时清理，每年雨季前必须清理1次。',4,'admin','2012-09-10 22:40:23','2012-11-14 22:14:43','admin'),(295,'第二百八十二条',154,'0001-0001-0002-0006-0004-0005',0,1,1,'对基岩段富水性较强的深井，应在井筒中部设置相应排水能力的转水站。',3,'admin','2012-09-10 22:40:28','2012-11-14 22:14:50','admin'),(296,'第二百八十三条',154,'0001-0001-0002-0006-0004-0006',0,1,1,'井筒开凿到底后，井底附近必须设置具有一定能力的临时排水设施，保证临时变电所、临时水仓形成之前的施工安全。',2,'admin','2012-09-10 22:40:34','2012-11-14 22:14:57','admin'),(297,'第二百八十四条',154,'0001-0001-0002-0006-0004-0007',0,1,1,'在建矿井在永久排水系统形成之前，各施工区必须设置临时排水系统，并保证有足够的排水能力。',1,'admin','2012-09-10 22:40:40','2012-11-14 22:15:03','admin'),(298,'第二百八十五条',155,'0001-0001-0002-0006-0005-0001',0,1,1,'矿井必须做好水害分析预报和充水条件分析，坚持预测预报、有疑必探、先探后掘、先治后采的防治水原则。\r\n探水或接近积水地区掘进前或排放被淹井巷的积水前，必须编制探放水设计，并采取防止瓦斯和其他有害气体危害等安全措施。\r\n探水孔的布置和超前距离，应当根据水头高低、煤（岩）层厚度和硬度以及安全措施等在探放水设计中具体规定。\r\n',10,'admin','2012-09-10 22:40:54','2012-11-14 22:15:13','admin'),(299,'第二百八十六条',155,'0001-0001-0002-0006-0005-0002',0,1,1,'采掘工作面遇到下列情况之一时，必须确定探水线进行探水：\r\n（一）接近水淹或可能积水的井巷、老空或相邻煤矿时。\r\n（二）接近含水层、导水断层、溶洞和导水陷落柱时。\r\n（三）打开隔离煤柱放水时。\r\n（四）接近可能与河流、湖泊、水库、蓄水池、水井等相通的断层破碎带时。\r\n（五）接近有出水可能的钻孔时。\r\n（六）接近有水的灌浆区时。\r\n（七）接近其他可能出水地区时。\r\n经探水确认无突水危险后，方可前进。\r\n',9,'admin','2012-09-10 22:41:00','2012-11-14 22:15:20','admin'),(300,'第二百八十七条',155,'0001-0001-0002-0006-0005-0003',0,1,1,'煤系底部有强承压含水层并有突水危险的工作面，在开采前，必须编制探放水设计，明确安全措施。',8,'admin','2012-09-10 22:41:05','2012-11-14 22:15:27','admin'),(301,'第二百八十八条',155,'0001-0001-0002-0006-0005-0004',0,1,1,'安装钻机探水前，必须遵守下列规定：',7,'admin','2012-09-10 22:41:10','2012-11-14 22:15:35','admin'),(302,'第二百八十九条',155,'0001-0001-0002-0006-0005-0005',0,1,1,'预计水压较大的地区，探水钻进之前，必须先安好孔口管和控制闸阀，进行耐压试验，达到设计承受的水压后，方准继续钻进。特别危险的地区，应有躲避场所，并规定避灾路线。',6,'admin','2012-09-10 22:41:16','2012-11-14 22:15:41','admin'),(303,'第二百九十条',155,'0001-0001-0002-0006-0005-0006',0,1,1,'钻孔内水压过大时，应采用反压和有防喷装置的方法钻进，并有防止孔口管和煤（岩）壁突然鼓出的措施。',5,'admin','2012-09-10 22:41:22','2012-11-14 22:15:48','admin'),(304,'第二百九十一条',155,'0001-0001-0002-0006-0005-0007',0,1,1,'钻进时，发现煤岩松软、片帮、来压或钻孔中的水压、水量突然增大，以及有顶钻等异状时，必须停止钻进，但不得拔出钻杆，现场负责人员应立即向矿调度室报告，并派人监测水情。如果发现情况危急时，必须立即撤出所有受水威胁地区的人员，然后采取措施，进行处理。',4,'admin','2012-09-10 22:41:27','2012-11-14 22:15:55','admin'),(305,'第二百九十二条',155,'0001-0001-0002-0006-0005-0008',0,1,1,'探放老空水前，首先要分析查明老空水体的空间位置、积水量和水压。老空积水区高于探放水点位置时，只准打钻孔探放水;探放水时，必须撤出探放水点以下部位受水害威胁区域内的所有人员。探放水孔必须打中老空水体，并要监视放水全过程，核对放水量，直到老空水放完为止。\r\n钻孔接近老空，预计可能有瓦斯或其他有害气体涌出时，必须有瓦斯检查工或矿山救护队员在现场值班，检查空气成分。如果瓦斯或其他有害气体浓度超过本规程规定时，必须立即停止钻进，切断电源，撤出人员，并报告矿调度室，及时处理。\r\n',3,'admin','2012-09-10 22:41:33','2012-11-14 22:16:02','admin'),(306,'第二百九十三条',155,'0001-0001-0002-0006-0005-0009',0,1,1,'钻孔放水前，必须估计积水量，根据矿井排水能力和水仓容量，控制放水流量；放水时，必须设专人监测钻孔出水情况，测定水量、水压，做好记录。若水量突然变化，必须及时处理，并立即报告矿调度室。',2,'admin','2012-09-10 22:41:39','2012-11-14 22:16:08','admin'),(307,'第二百九十四条',155,'0001-0001-0002-0006-0005-00010',0,1,1,'排除井筒和下山的积水以及恢复被淹井巷前，必须有矿山救护队检查水面上的空气成分，发现有害气体，必须及时处理。\r\n排水过程中，如有被水封住的有害气体突然涌出的可能，必须制定安全措施。\r\n',1,'admin','2012-09-10 22:41:44','2012-11-14 22:16:15','admin'),(308,'第二百九十五条',157,'0001-0001-0002-0007-0001-0001',0,1,1,'爆炸材料的贮存，永久性地面爆炸材料库建筑结构（包括永久性埋入式库房）及各种防护措施，总库区的内、外部安全距离等，必须符合国家有关规定。\r\n井上、下接触爆炸材料的人员，必须穿棉布或抗静电衣服。\r\n',16,'admin','2012-09-10 22:42:06','2012-11-15 20:29:28','admin'),(309,'第二百九十六条',157,'0001-0001-0002-0007-0001-0002',0,1,1,'建有爆炸材料制造厂的矿区总库，所有库房贮存各种炸药的总容量不得超过该厂1个月生产量，雷管的总容量不得超过3个月生产量。没有爆炸材料制造厂的矿区总库，所有库房贮存各种炸药的总容量不得超过由该库所供应的矿井2个月的计划需要量，雷管的总容量不得超过6个月的计划需要量。单个库房的最大容量：炸药不得超过200ｔ，雷管不得超过500万发。\r\n地面分库所有库房贮存爆炸材料的总容量：炸药不得超过75ｔ，雷管不得超过25万发。单个库房的炸药最大容量不得超过25ｔ。地面分库贮存各种爆炸材料的数量，还不得超过由该库所供应的矿井３个月的计划需要量。\r\n',14,'admin','2012-09-10 22:42:12','2012-11-15 20:29:19','admin'),(310,'第二百九十七条',157,'0001-0001-0002-0007-0001-0003',0,1,1,'建有爆炸材料制造厂的矿区总库，所有库房贮存各种炸药的总容量不得超过该厂1个月生产量，雷管的总容量不得超过3个月生产量。没有爆炸材料制造厂的矿区总库，所有库房贮存各种炸药的总容量不得超过由该库所供应的矿井2个月的计划需要量，雷管的总容量不得超过6个月的计划需要量。单个库房的最大容量：炸药不得超过200ｔ，雷管不得超过500万发。\r\n地面分库所有库房贮存爆炸材料的总容量：炸药不得超过75ｔ，雷管不得超过25万发。单个库房的炸药最大容量不得超过25ｔ。地面分库贮存各种爆炸材料的数量，还不得超过由该库所供应的矿井３个月的计划需要量。\r\n',13,'admin','2012-09-10 22:42:18','2012-11-15 20:29:37','admin'),(311,'第二百九十八条',157,'0001-0001-0002-0007-0001-0004',0,1,1,'各种爆炸材料的每一品种都应专库贮存；但当条件限制时，可按国家的有关同库贮存的规定贮存。\r\n存放爆炸材料的木架每格只准放1层爆炸材料箱。\r\n',12,'admin','2012-09-10 22:42:24','2012-11-15 20:30:07','admin'),(312,'第二百九十九条',157,'0001-0001-0002-0007-0001-0005',0,1,1,'地面爆炸材料库必须有发放爆炸材料的专用套间或单独房间。分库的炸药发放套间内，可临时保存爆破工的空爆炸材料箱与发爆器。在分库的雷管发放套间内发放雷管时，必须在铺有导电的软质垫层并有边缘突起的桌子上进行。',11,'admin','2012-09-10 22:42:29','2012-11-15 20:30:15','admin'),(313,'第三百条',157,'0001-0001-0002-0007-0001-0006',0,1,1,'使用年限在2年以下的地面临时性爆炸材料库的最大容量：炸药不得超过3ｔ，雷管不得超过１万发，并不得超过该库所供应单位10天的需要量。\r\n库房必须选择在干燥的地方，并应有良好的通风和防潮措施。\r\n地面临时性爆炸材料库周围，必须设围墙或铁刺网，其高度不得低于2m，围墙或铁刺网距库房的距离不应小于5m。\r\n地面临时性爆炸材料库的内外部安全距离、照明、防火和防雷电措施、管理制度与永久性地面爆炸材料库相同。\r\n',10,'admin','2012-09-10 22:42:35','2012-11-15 20:30:22','admin'),(314,'第三百零一条',157,'0001-0001-0002-0007-0001-0007',0,1,1,'在地面临时保管当天使用的爆炸材料时，可存放在棚子、帐篷、洞穴内或其他地点，但应遵守下列规定：',9,'admin','2012-09-10 22:42:41','2012-11-15 20:30:30','admin'),(315,'第三百零二条',157,'0001-0001-0002-0007-0001-0008',0,1,1,'开凿井筒或平硐时，可在距井筒或平硐口以及周围主要建筑物50m以外加设横堤，或250m以外不加横堤的专用房屋或硐室内贮存1天使用的爆炸材料，但最大炸药贮存量不得超过500kg。',8,'admin','2012-09-10 22:42:49','2012-11-15 20:30:39','admin'),(316,'第三百零三条',157,'0001-0001-0002-0007-0001-0009',0,1,1,'井下爆炸材料库应采用硐室式或壁槽式。\r\n爆炸材料必须贮存在硐室或壁槽内，硐室之间或壁槽之间的距离，必须符合爆炸材料安全距离的规定。\r\n井下爆炸材料库应包括库房、辅助硐室和通向库房的巷道。辅助硐室中，应有检查电雷管全电阻、发放炸药、电雷管编号以及保存爆破工的空爆炸材料箱和发爆器等专用硐室。\r\n',7,'admin','2012-09-10 22:42:57','2012-11-15 20:30:47','admin'),(317,'第三百零四条',157,'0001-0001-0002-0007-0001-00010',0,1,1,'井下爆炸材料库的布置必须符合下列要求：',6,'admin','2012-09-10 22:43:03','2012-11-15 20:30:54','admin'),(318,'第三百零五条',157,'0001-0001-0002-0007-0001-0011',0,1,1,'井下爆炸材料库必须砌碹或用非金属不燃性材料支护，不得渗漏水，并应采取防潮措施。爆炸材料库出口两旁的巷道，必须砌碹或用不燃性材料支护，支护长度不得小于5m。库房必须备有足够数量的消防器材。',5,'admin','2012-09-10 22:43:08','2012-11-15 20:31:04','admin'),(319,'第三百零六条',157,'0001-0001-0002-0007-0001-0012',0,1,1,'井下爆炸材料库的最大贮存量，不得超过该矿井3天的炸药需要量和10天的电雷管需要量。\r\n井下爆炸材料库的炸药和电雷管必须分开贮存。\r\n每个硐室贮存的炸药量不得超过2t，电雷管不得超过10天的需要量；每个壁槽贮存的炸药量不得超过400kg，电雷管不得超过2天的需要量。\r\n库房的发放爆炸材料硐室允许存放当班待发的炸药，但其最大存放量不得超过3箱。\r\n',4,'admin','2012-09-10 22:43:13','2012-11-15 20:31:11','admin'),(320,'第三百零七条',157,'0001-0001-0002-0007-0001-0013',0,1,1,'在多水平生产的矿井内、井下爆炸材料库距爆破工作地点超过2.5km的矿井内、井下无爆炸材料库的矿井内可设立爆炸材料发放硐室，但必须遵守下列规定：',3,'admin','2012-09-10 22:43:19','2012-11-15 20:31:19','admin'),(321,'第三百零八条',157,'0001-0001-0002-0007-0001-0014',0,1,1,'井下爆炸材料库必须采用矿用防爆型（矿用增安型除外）的照明设备，照明线必须使用阻燃电缆，电压不得超过127V。严禁在贮存爆炸材料的硐室或壁槽内装灯。\r\n不设固定式照明设备的爆炸材料库，可使用带绝缘套的矿灯。\r\n任何人员不得携带矿灯进入井下爆炸材料库房内。库内照明设备或线路发生路障时，在库房管理人员的监护下检修人员可使用带绝缘套的矿灯进入库内工作。',2,'admin','2012-09-10 22:43:26','2012-11-15 20:31:26','admin'),(322,'第三百零九条',157,'0001-0001-0002-0007-0001-0015',0,1,1,'煤矿企业必须建立爆炸材料领退制度、电雷管编号制度和爆炸材料丢失处理办法。\r\n电雷管（包括清退入库的电雷管）在发给爆破工前，必须用电雷管检测仪逐个做全电阻检查，并将脚线扭结成短路。严禁发放电阻不合格的电雷管。\r\n煤矿企业必须按民用爆炸物品管理条例的规定，建立爆炸材料销毁制度。\r\n',1,'admin','2012-09-10 22:43:33','2012-11-15 20:31:35','admin'),(323,'第三百一十条',158,'0001-0001-0002-0007-0002-0001',0,1,1,'',4,'admin','2012-09-10 22:43:44','2012-09-10 22:44:09','admin'),(324,'第三百一十一条',158,'0001-0001-0002-0007-0002-0002',0,1,1,'',3,'admin','2012-09-10 22:43:49','2012-09-10 22:44:09','admin'),(325,'第三百一十二条',158,'0001-0001-0002-0007-0002-0003',0,1,1,'井下用机车运送爆炸材料时，应遵守下列规定：\r\n（一）炸药和电雷管不得在同一列车内运输。如用同一列车运输，装有炸药与装有电雷管的车辆之间，以及装有炸药或电雷管的车辆与机车之间，必须用空车分别隔开，隔开长度不得小于3m。\r\n（二）硝化甘油类炸药和电雷管必须装在专用的、带盖的有木质隔板的车厢内，车厢内部应铺有胶皮或麻袋等软质垫层，并只准放1层爆炸材料箱。其他类炸药箱可以装在矿车内，但堆放高度不得超过矿车上缘。\r\n（三）爆炸材料必须由井下爆炸材料库负责人或经过专门训练的专人护送。跟车人员、护送人员和装卸人员应坐在尾车内，严禁其他人员乘车。\r\n（四）列车的行驶速度不得超过2m/s。\r\n（五）装有爆炸材料的列车不得同时运送其他物品或工具。\r\n',3,'admin','2012-09-10 22:43:58','2012-11-15 20:32:08','admin'),(326,'第三百一十三条',158,'0001-0001-0002-0007-0002-0004',0,1,1,'水平巷道和倾斜巷道内有可靠的信号装置时，可用钢丝绳牵引的车辆运送爆炸材料，但炸药和电雷管必须分开运输，运输速度不得超过1m/s。运输电雷管的车辆必须加盖、加垫，车厢内以软质垫物塞紧，防止震动和撞击。\r\n严禁用刮板输送机、带式输送机等运输爆炸材料。\r\n',2,'admin','2012-09-10 22:44:04','2012-11-15 20:32:15','admin'),(327,'第三百一十四条',158,'0001-0001-0002-0007-0002-0005',0,1,1,'由爆炸材料库直接向工作地点用人力运送爆炸材料时，应遵守下列规定：\r\n（一）电雷管必须由爆破工亲自运送，炸药应由爆破工或在爆破工监护下由其他人员运送。\r\n（二）爆炸材料必须装在耐压和抗撞冲、防震、防静电的非金属容器内。电雷管和炸药严禁装在同一容器内。严禁将爆炸材料装在衣袋内。领到爆炸材料后，应直接送到工作地点，严禁中途逗留。\r\n（三）携带爆炸材料上、下井时，在每层罐笼内搭乘的携带爆炸材料的人员不得超过4人，其他人员不得同罐上下。\r\n（四）在交接班、人员上下井的时间内严禁携带爆炸材料人员沿井筒上下。\r\n',1,'admin','2012-09-10 22:44:09','2012-11-15 20:52:48','admin'),(328,'第三百一十五条',159,'0001-0001-0002-0007-0003-0001',0,1,1,'所有爆破人员，包括爆破、送药、装药人员，必须熟悉爆炸材料性能和本规程规定。',32,'admin','2012-09-10 22:44:20','2012-11-15 20:53:03','admin'),(329,'第三百一十六条',159,'0001-0001-0002-0007-0003-0002',0,1,1,'井下爆破工作必须由专职爆破工担任。在煤（岩）与瓦斯（二氧化碳）突出煤层中，专职爆破工必须固定在同一工作面工作。\r\n爆破作业必须执行“一炮三检制”。\r\n',31,'admin','2012-09-10 22:44:25','2012-11-15 20:53:11','admin'),(330,'第三百一十七条',159,'0001-0001-0002-0007-0003-0003',0,1,1,'爆破作业必须编制爆破作业说明书，说明书必须符合下列要求：\r\n（一）炮眼布置图必须标明采煤工作面的高度和打眼范围或掘进工作面的巷道断面尺寸，炮眼的位置、个数、深度、角度及炮眼编号，并用正面图、平面图和剖面图表示。\r\n（二）炮眼说明表必须说明炮眼的名称、深度、角度，使用炸药、雷管的品种，装药量，封泥长度，连线方法和起爆顺序。\r\n（三）必须编入采掘作业规程，并及时修改补充。\r\n爆破工必须依照说明书进行爆破作业。\r\n',30,'admin','2012-09-10 22:44:30','2012-11-15 20:53:24','admin'),(331,'第三百一十八条',159,'0001-0001-0002-0007-0003-0004',0,1,1,'不得使用过期或严重变质的爆炸材料。不能使用的爆炸材料必须交回爆炸材料库。',29,'admin','2012-09-10 22:44:35','2012-11-15 20:53:32','admin'),(332,'第三百一十九条',159,'0001-0001-0002-0007-0003-0005',0,1,1,'爆炸材料新产品，经国家授权的检验机构检验合格，并取得煤矿矿用产品安全标志后，方可在井下试用。',28,'admin','2012-09-10 22:44:40','2012-11-15 20:53:40','admin'),(333,'第三百二十条',159,'0001-0001-0002-0007-0003-0006',0,1,1,'井下爆破作业，必须使用煤矿许用炸药和煤矿许用电雷管。煤矿许用炸药的选用应遵守下列规定：\r\n（一）低瓦斯矿井的岩石掘进工作面必须使用安全等级不低于一级的煤矿许用炸药。\r\n（二）低瓦斯矿井的煤层采掘工作面、半煤岩掘进工作面必须使用安全等级不低于二级的煤矿许用炸药。\r\n（三）高瓦斯矿井、低瓦斯矿井的高瓦斯区域，必须使用安全等级不低于三级的煤矿许用炸药。有煤（岩）与瓦斯突出危险的工作面，必须使用安全等级不低于三级的煤矿许用含水炸药。\r\n严禁使用黑火药和冻结或半冻结的硝化甘油类炸药。同一工作面不得使用2种不同品种的炸药。\r\n在采掘工作面，必须使用煤矿许用瞬发电雷管或煤矿许用毫秒延期电雷管。使用煤矿许用毫秒延期电雷管时，最后一段的延期时间不得超过130ms。不同厂家生产的或不同品种的电雷管，不得掺混使用。不得使用导爆管或普通导爆索，严禁使用火雷管。\r\n',27,'admin','2012-09-10 22:44:46','2012-11-15 20:53:49','admin'),(334,'第三百二十一条',159,'0001-0001-0002-0007-0003-0007',0,1,1,'在有瓦斯或有煤尘爆炸危险的采掘工作面，应采用毫秒爆破。在掘进工作面应全断面一次起爆，不能全断面一次起爆的，必须采取安全措施；在采煤工作面，可分组装药，但一组装药必须一次起爆。\r\n严禁在1个采煤工作面使用2台发爆器同时进行爆破。\r\n',26,'admin','2012-09-10 22:44:53','2012-11-15 20:53:58','admin'),(335,'第三百二十二条',159,'0001-0001-0002-0007-0003-0008',0,1,1,'在高瓦斯矿井、低瓦斯矿井的高瓦斯区域的采掘工作面采用毫秒爆破时，若采用反向起爆，必须制定安全技术措施。',25,'admin','2012-09-10 22:44:59','2012-11-15 20:54:05','admin'),(336,'第三百二十三条',159,'0001-0001-0002-0007-0003-0009',0,1,1,'在高瓦斯矿井和有煤（岩）与瓦斯突出危险的采掘工作面的实体煤中，为增加煤体裂隙、松动煤体而进行的10m以上的深孔预裂控制爆破，可使用二级煤矿许用炸药，但必须制定安全措施。',24,'admin','2012-09-10 22:45:04','2012-11-15 20:54:13','admin'),(337,'第三百二十四条',159,'0001-0001-0002-0007-0003-00010',0,1,1,'爆破工必须把炸药、电雷管分开存放在专用的爆炸材料箱内，并加锁；严禁乱扔、乱放。爆炸材料箱必须放在顶板完好、支架完整，避开机械、电气设备的地点。爆破时必须把爆炸材料箱放到警戒线以外的安全地点。',23,'admin','2012-09-10 22:45:10','2012-11-15 20:54:22','admin'),(338,'第三百二十五条',159,'0001-0001-0002-0007-0003-0011',0,1,1,'从成束的电雷管中抽取单个电雷管时，不得手拉脚线硬拽管体，也不得手拉管体硬拽脚线，应将成束的电雷管顺好，拉住前端脚线将电雷管抽出。抽出单个电雷管后，必须将其脚线扭结成短路。',22,'admin','2012-09-10 22:45:15','2012-11-15 20:54:30','admin'),(339,'第三百二十六条',159,'0001-0001-0002-0007-0003-0012',0,1,1,'装配起爆药卷时，必须遵守下列规定：\r\n（一）必须在顶板完好、支架完整、避开电气设备和导电体的爆破工作地点附近进行。严禁坐在爆炸材料箱上装配起爆药卷。装配起爆药卷数量，以当时当地需要的数量为限。\r\n（二）装配起爆药卷必须防止电雷管受震动、冲击，折断脚线和损坏脚线绝缘层。\r\n（三）电雷管必须由药卷的顶部装入，严禁用电雷管代替竹、木棍扎眼。电雷管必须全部插入药卷内。严禁将电雷管斜插在药卷的中部或捆在药卷上。\r\n（四）电雷管插入药卷后，必须用脚线将药卷缠住，并将电雷管脚线扭结成短路。\r\n',21,'admin','2012-09-10 22:45:22','2012-11-15 20:54:39','admin'),(340,'第三百二十七条',159,'0001-0001-0002-0007-0003-0013',0,1,1,'装药前，首先必须清除炮眼内的煤粉或岩粉，再用木质或竹质炮棍将药卷轻轻推入，不得冲撞或捣实。炮眼内的各药卷必须彼此密接。\r\n有水的炮眼，应使用抗水型炸药。\r\n装药后，必须把电雷管脚线悬空，严禁电雷管脚线、爆破母线与运输设备、电气设备以及采掘机械等导电体相接触。\r\n',20,'admin','2012-09-10 22:45:28','2012-11-15 20:54:49','admin'),(341,'第三百二十八条',159,'0001-0001-0002-0007-0003-0014',0,1,1,'炮眼封泥应用水炮泥，水炮泥外剩余的炮眼部分应用粘土炮泥或用不燃性的、可塑性松散材料制成的炮泥封实。严禁用煤粉、块状材料或其他可燃性材料作炮眼封泥。\r\n无封泥、封泥不足或不实的炮眼严禁爆破。\r\n严禁裸露爆破。\r\n',19,'admin','2012-09-10 22:45:37','2012-11-15 20:54:59','admin'),(342,'第三百二十九条',159,'0001-0001-0002-0007-0003-0015',0,1,1,'炮眼深度和炮眼的封泥长度应符合下列要求：\r\n（一）炮眼深度小于0.6m时，不得装药、爆破；在特殊条件下，如挖底、刷帮、挑顶确需浅眼爆破时，必须制定安全措施，炮眼深度可以小于0.6m，但必须封满炮泥。\r\n（二）炮眼深度为0.6～1m时，封泥长度不得小于炮眼深度的1/2。\r\n（三）炮眼深度超过1m时，封泥长度不得小于0.5m。\r\n（四）炮眼深度超过2.5m时，封泥长度不得小于1m。\r\n（五）光面爆破时，周边光爆炮眼应用炮泥封实，且封泥长度不得小于0.3m。\r\n（六）工作面有2个或2个以上自由面时，在煤层中最小抵抗线不得小于0.5m，在岩层中最小抵抗线不得小于0.3m。浅眼装药爆破大岩块时，最小抵抗线和封泥长度都不得小于0.3m。\r\n',18,'admin','2012-09-10 22:45:43','2012-11-15 20:55:09','admin'),(343,'第三百三十条',159,'0001-0001-0002-0007-0003-0016',0,1,1,'处理卡在溜煤（矸）眼中的煤、矸时，如果确无爆破以外的办法，可爆破处理，但必须遵守下列规定：\r\n（一）必须采用取得煤矿矿用产品安全标志的用于溜煤（矸）眼的煤矿许用刚性被筒炸药或不低于该安全等级的煤矿许用炸药。\r\n（二）每次爆破只准使用1个煤矿许用电雷管，最大装药量不得超过450g。\r\n（三）爆破前必须检查溜煤（矸）眼内堵塞部位的上部和下部空间的瓦斯。\r\n（四）爆破前必须洒水。\r\n',17,'admin','2012-09-10 22:45:50','2012-11-15 20:55:18','admin'),(344,'第三百三十一条',159,'0001-0001-0002-0007-0003-0017',0,1,1,'装药前和爆破前有下列情况之一的，严禁装药、爆破：\r\n（一）采掘工作面的控顶距离不符合作业规程的规定，或者支架有损坏，或者伞檐超过规定。\r\n（二）爆破地点附近20m以内风流中瓦斯浓度达到1.0％。\r\n（三）在爆破地点20m以内，矿车，未清除的煤、矸或其他物体堵塞巷道断面1/3以上。\r\n（四）炮眼内发现异状、温度骤高骤低、有显著瓦斯涌出、煤岩松散、透老空等情况。\r\n（五）采掘工作面风量不足。\r\n',16,'admin','2012-09-10 22:45:58','2012-11-15 20:55:26','admin'),(345,'第三百三十二条',159,'0001-0001-0002-0007-0003-0018',0,1,1,'在有煤尘爆炸危险的煤层中，掘进工作面爆破前后，附近20m的巷道内，必须洒水降尘。',15,'admin','2012-09-10 22:46:05','2012-11-15 20:55:36','admin'),(346,'第三百三十三条',159,'0001-0001-0002-0007-0003-0019',0,1,1,'爆破前，必须加强对机器、液压支架和电缆等的保护或将其移出工作面。\r\n爆破前，班组长必须亲自布置专人在警戒线和可能进入爆破地点的所有通路上担任警戒工作。警戒人员必须在安全地点警戒。警戒线处应设置警戒牌、栏杆或拉绳。\r\n',14,'admin','2012-09-10 22:46:11','2012-11-15 20:55:45','admin'),(347,'第三百三十四条',159,'0001-0001-0002-0007-0003-0020',0,1,1,'爆破母线和连接线应符合下列要求：\r\n（一）煤矿井下爆破母线必须符合标准。\r\n（二）爆破母线和连接线、电雷管脚线和连接线、脚线和脚线之间的接头必须相互扭紧并悬挂，不得与轨道、金属管、金属网、钢丝绳、刮板输送机等导电体相接触。\r\n（三）巷道掘进时，爆破母线应随用随挂。不得使用固定爆破母线，特殊情况下，在采取安全措施后，可不受此限。\r\n（四）爆破母线与电缆、电线、信号线应分别挂在巷道的两侧。如果必须挂在同一侧，爆破母线必须挂在电缆的下方，并应保持0.3m以上的距离。\r\n（五）只准采用绝缘母线单回路爆破，严禁用轨道、金属管、金属网、水或大地等当作回路。\r\n（六）爆破前，爆破母线必须扭结成短路。\r\n',13,'admin','2012-09-10 22:46:16','2012-11-15 20:55:56','admin'),(348,'第三百三十五条',159,'0001-0001-0002-0007-0003-0021',0,1,1,'爆破母线和连接线应符合下列要求：\r\n（一）煤矿井下爆破母线必须符合标准。\r\n（二）爆破母线和连接线、电雷管脚线和连接线、脚线和脚线之间的接头必须相互扭紧并悬挂，不得与轨道、金属管、金属网、钢丝绳、刮板输送机等导电体相接触。\r\n（三）巷道掘进时，爆破母线应随用随挂。不得使用固定爆破母线，特殊情况下，在采取安全措施后，可不受此限。\r\n（四）爆破母线与电缆、电线、信号线应分别挂在巷道的两侧。如果必须挂在同一侧，爆破母线必须挂在电缆的下方，并应保持0.3m以上的距离。\r\n（五）只准采用绝缘母线单回路爆破，严禁用轨道、金属管、金属网、水或大地等当作回路。\r\n（六）爆破前，爆破母线必须扭结成短路。\r\n',12,'admin','2012-09-10 22:47:24','2012-11-15 20:56:07','admin'),(349,'第三百三十六条',159,'0001-0001-0002-0007-0003-0022',0,1,1,'每次爆破作业前，爆破工必须做电爆网路全电阻检查。严禁用发爆器打火放电检测电爆网路是否导通。\r\n发爆器必须统一管理、发放。必须定期校验发爆器的各项性能参数，并进行防爆性能检查，不符合规定的严禁使用。\r\n',11,'admin','2012-09-10 22:47:33','2012-11-15 20:56:16','admin'),(350,'第三百三十七条',159,'0001-0001-0002-0007-0003-0023',0,1,1,'爆破工必须最后离开爆破地点，并必须在安全地点起爆。起爆地点到爆破地点的距离必须在作业规程中具体规定。',10,'admin','2012-09-10 22:47:40','2012-11-15 20:56:24','admin'),(351,'第三百三十八条',159,'0001-0001-0002-0007-0003-0024',0,1,1,'发爆器的把手、钥匙或电力起爆接线盒的钥匙，必须由爆破工随身携带，严禁转交他人。不到爆破通电时，不得将把手或钥匙插入发爆器或电力起爆接线盒内。爆破后，必须立即将把手或钥匙拔出，摘掉母线并扭结成短路。',9,'admin','2012-09-10 22:47:47','2012-11-15 20:56:34','admin'),(352,'第三百三十九条',159,'0001-0001-0002-0007-0003-0025',0,1,1,'爆破前，脚线的连接工作可由经过专门训练的班组长协助爆破工进行。爆破母线连接脚线、检查线路和通电工作，只准爆破工一人操作。\r\n爆破前，班组长必须清点人数，确认无误后，方准下达起爆命令。\r\n爆破工接到起爆命令后，必须先发出爆破警号，至少再等5s，方可起爆。\r\n装药的炮眼应当班爆破完毕。特殊情况下，当班留有尚未爆破的装药的炮眼时，当班爆破工必须在现场向下一班爆破工交接清楚。\r\n',8,'admin','2012-09-10 22:47:54','2012-11-15 20:56:44','admin'),(353,'第三百四十条',159,'0001-0001-0002-0007-0003-0026',0,1,1,'爆破前，脚线的连接工作可由经过专门训练的班组长协助爆破工进行。爆破母线连接脚线、检查线路和通电工作，只准爆破工一人操作。\r\n爆破前，班组长必须清点人数，确认无误后，方准下达起爆命令。\r\n爆破工接到起爆命令后，必须先发出爆破警号，至少再等5s，方可起爆。\r\n装药的炮眼应当班爆破完毕。特殊情况下，当班留有尚未爆破的装药的炮眼时，当班爆破工必须在现场向下一班爆破工交接清楚。\r\n',7,'admin','2012-09-10 22:48:02','2012-11-15 20:56:54','admin'),(354,'第三百四十一条',159,'0001-0001-0002-0007-0003-0027',0,1,1,'通电以后拒爆时，爆破工必须先取下把手或钥匙，并将爆破母线从电源上摘下，扭结成短路，再等一定时间（使用瞬发电雷管时，至少等5min；使用延期电雷管时，至少等15min），才可沿线路检查，找出拒爆的原因。',6,'admin','2012-09-10 22:48:07','2012-11-15 20:57:03','admin'),(355,'第三百四十二条',159,'0001-0001-0002-0007-0003-0028',0,1,1,'处理拒爆、残爆时，必须在班组长指导下进行，并应在当班处理完毕。如果当班未能处理完毕，当班爆破工必须在现场向下一班爆破工交接清楚。\r\n处理拒爆时，必须遵守下列规定：\r\n（一）由于连线不良造成的拒爆，可重新连线起爆。\r\n（二）在距拒爆炮眼0.3m以外另打与拒爆炮眼平行的新炮眼，重新装药起爆。\r\n（三）严禁用镐刨或从炮眼中取出原放置的起爆药卷或从起爆药卷中拉出电雷管。不论有无残余炸药严禁将炮眼残底继续加深；严禁用打眼的方法往外掏药；严禁用压风吹拒爆（残爆）炮眼。\r\n（四）处理拒爆的炮眼爆炸后，爆破工必须详细检查炸落的煤、矸，收集未爆的电雷管。\r\n（五）在拒爆处理完毕以前，严禁在该地点进行与处理拒爆无关的工作。\r\n',5,'admin','2012-09-10 22:48:12','2012-11-15 20:57:15','admin'),(356,'第三百四十三条',159,'0001-0001-0002-0007-0003-0029',0,1,1,'爆炸材料库和爆炸材料发放硐室附近30m范围内，严禁爆破。',4,'admin','2012-09-10 22:48:22','2012-11-15 20:57:22','admin'),(357,'第三百四十四条',159,'0001-0001-0002-0007-0003-0030',0,1,1,'开凿或延深立井井筒向井底工作面运送爆炸材料和在井筒内装药时，除负责装药爆破的人员、信号工、看盘工和水泵司机外，其他人员必须撤到地面或上水平巷道中。',3,'admin','2012-09-10 22:48:27','2012-11-15 20:57:31','admin'),(358,'第三百四十五条',159,'0001-0001-0002-0007-0003-0031',0,1,1,'开凿或延深立井井筒的装配起爆药卷工作，可在地面专用的房间内进行。\r\n专用房间距井筒、厂房、建筑物和主要通路的安全距离必须符合国家有关规定，且距离井筒不得小于50m。\r\n严禁将起爆药卷与炸药装在同一爆炸材料容器内运往井底工作面。\r\n',2,'admin','2012-09-10 22:48:32','2012-11-15 20:57:40','admin'),(359,'第三百四十六条',159,'0001-0001-0002-0007-0003-0032',0,1,1,'在开凿或延深立井井筒时，必须在地面或在生产水平巷道内进行爆破。\r\n在爆破母线与电力起爆接线盒引线接通之前，井筒内所有电气设备必须断电。\r\n只有在爆破人员完成装药和连线工作，将所有井盖门打开，井筒、井口房内的人员全部撤出，设备、工具提升到安全高度以后，方可爆破。\r\n爆破通风后，必须仔细检查井筒，清除崩落在井圈上、吊盘上或其他设备上的矸石。\r\n爆破后乘吊桶检查井底工作面时，吊桶不得蹾撞工作面。\r\n',1,'admin','2012-09-10 22:48:37','2012-11-15 20:57:50','admin'),(360,'第三百四十七条',160,'0001-0001-0002-0008-0001-0001',0,1,1,'瓦斯矿井中使用机车运输时，应遵守下列规定：',33,'admin','2012-09-10 23:09:08','2012-11-15 20:58:25','admin'),(361,'第三百四十八条',160,'0001-0001-0002-0008-0001-0002',0,1,1,'机车司机必须按信号指令行车，在开车前必须发出开车信号。机车运行中，严禁将头或身体探出车外。司机离开座位时，必须切断电动机电源，将控制手把取下，扳紧车闸，但不得关闭车灯。',32,'admin','2012-09-10 23:09:14','2012-11-15 20:58:36','admin'),(362,'第三百四十九条',160,'0001-0001-0002-0008-0001-0003',0,1,1,'必须定期检修机车和矿车，并经常检查，发现隐患，及时处理。\r\n机车的闸、灯、警铃（喇叭）、连接装置和撒砂装置，任何一项不正常或防爆部分失去防爆性能时，都不得使用该机车。\r\n',31,'admin','2012-09-10 23:09:20','2012-11-15 20:58:45','admin'),(363,'第三百五十条',160,'0001-0001-0002-0008-0001-0004',0,1,1,'采用矿用防爆型柴油动力装置时，应遵守下列规定：',30,'admin','2012-09-10 23:09:26','2012-11-15 20:58:52','admin'),(364,'第三百五十一条',160,'0001-0001-0002-0008-0001-0005',0,1,1,'采用机车运输时，应遵守下列规定：',29,'admin','2012-09-10 23:09:32','2012-11-15 20:59:00','admin'),(365,'第三百五十二条',160,'0001-0001-0002-0008-0001-0006',0,1,1,'新建或改扩建的矿井中，对运行7t及其以上机车或3t及其以上矿车的轨道，应采用不低于30kg/m的钢轨。',28,'admin','2012-09-10 23:09:41','2012-11-15 20:59:09','admin'),(366,'第三百五十三条',160,'0001-0001-0002-0008-0001-0007',0,1,1,'矿井轨道必须按标准铺设。主要运输巷道轨道的铺设质量应符合下列要求：',27,'admin','2012-09-10 23:09:46','2012-11-15 20:59:21','admin'),(367,'第三百五十四条',160,'0001-0001-0002-0008-0001-0008',0,1,1,'架线电机车运行的轨道应符合下列要求：',26,'admin','2012-09-10 23:09:52','2012-11-15 20:59:29','admin'),(368,'第三百五十五条',160,'0001-0001-0002-0008-0001-0009',0,1,1,'架线电机车使用的直流电压，不得超过600V。',25,'admin','2012-09-10 23:10:00','2012-11-15 20:59:37','admin'),(369,'第三百五十六条',160,'0001-0001-0002-0008-0001-00010',0,1,1,'自轨面算起，电机车架空线的悬挂高度应符合下列要求：',24,'admin','2012-09-10 23:10:06','2012-11-15 20:59:45','admin'),(370,'第三百五十七条',160,'0001-0001-0002-0008-0001-0011',0,1,1,'电机车架空线与巷道顶或棚梁之间的距离不得小于0.2m。悬吊绝缘子距电机车架空线的距离，每侧不得超过0.25m。电机车架空线悬挂点的间距，在直线段内不得超过5m，在曲线段内不得超过表4规定值。\r\n表4  电机车架空线曲线段悬挂点间距最大值\r\n曲率半径/m	25～22	21～19	18～16	15～13	12～11	10～8\r\n悬挂点间距/m	4.5	4	3.5	3	2.5	2\r\n',23,'admin','2012-09-10 23:10:12','2012-11-15 21:00:04','admin'),(371,'第三百五十八条',160,'0001-0001-0002-0008-0001-0012',0,1,1,'长度超过1.5km的主要运输平巷，上下班时应采用机械运送人员。\r\n严禁使用固定车厢式矿车、翻转车厢式矿车、底卸式矿车、材料车和平板车等运送人员。\r\n',22,'admin','2012-09-10 23:10:18','2012-11-15 21:00:16','admin'),(372,'第三百五十九条',160,'0001-0001-0002-0008-0001-0013',0,1,1,'用人车运送人员时，应遵守下列规定：',21,'admin','2012-09-10 23:10:24','2012-11-15 21:00:24','admin'),(373,'第三百六十条',160,'0001-0001-0002-0008-0001-0014',0,1,1,'乘车人员必须遵守下列规定：',20,'admin','2012-09-10 23:10:30','2012-11-15 21:00:32','admin'),(374,'第三百六十一条',160,'0001-0001-0002-0008-0001-0015',0,1,1,'井下蓄电池充电室内必须采用矿用防爆型电气设备。测定电压时，可使用普通型电压表，但必须在揭开电池盖10min以后进行。\r\n井下矿用防爆型蓄电池电机车的电气设备，必须在车库内打开检修。\r\n',19,'admin','2012-09-10 23:11:00','2012-11-15 21:00:41','admin'),(375,'第三百六十二条',160,'0001-0001-0002-0008-0001-0016',0,1,1,'人力推车时，必须遵守下列规定：',18,'admin','2012-09-10 23:13:13','2012-11-15 21:00:48','admin'),(376,'第三百六十三条',160,'0001-0001-0002-0008-0001-0017',0,1,1,'各种车辆的两端必须装置碰头，每端突出的长度不得小于100mm。',17,'admin','2012-09-10 23:13:30','2012-11-15 21:00:59','admin'),(377,'第三百六十四条',160,'0001-0001-0002-0008-0001-0018',0,1,1,'不得在能自动滑行的坡道上停放车辆。确需停放时，必须用可靠的制动器将车辆稳住。',16,'admin','2012-09-10 23:13:35','2012-11-15 21:01:09','admin'),(378,'第三百六十五条',160,'0001-0001-0002-0008-0001-0019',0,1,1,'人员上下的主要倾斜井巷，垂深超过50m时，应采用机械运送人员。',15,'admin','2012-09-10 23:13:42','2012-11-15 21:01:20','admin'),(379,'第三百六十六条',160,'0001-0001-0002-0008-0001-0020',0,1,1,'倾斜井巷运送人员的人车必须有顶盖，车辆上必须装有可靠的防坠器。当断绳时，防坠器能自动发生作用，也能人工操纵。',14,'admin','2012-09-10 23:13:48','2012-11-15 21:01:29','admin'),(380,'第三百六十七条',160,'0001-0001-0002-0008-0001-0021',0,1,1,'倾斜井巷运送人员的人车必须有跟车人，跟车人必须坐在设有手动防坠器把手或制动器把手的位置上。\r\n每班运送人员前，必须检查人车的连接装置、保险链和防坠器，并必须先放1次空车。\r\n',13,'admin','2012-09-10 23:13:54','2012-11-15 21:01:42','admin'),(381,'第三百六十八条',160,'0001-0001-0002-0008-0001-0022',0,1,1,'用架空乘人装置运送人员时应遵守下列规定：',12,'admin','2012-09-10 23:13:59','2012-11-15 21:01:53','admin'),(382,'第三百六十九条',160,'0001-0001-0002-0008-0001-0023',0,1,1,'斜井人车必须设置使跟车人在运行途中任何地点都能向司机发送紧急停车信号的装置。\r\n多水平运输时，从各水平发出的信号必须有区别。人员上、下地点应悬挂信号牌。任一区段行车时，各水平必须有信号显示。\r\n',11,'admin','2012-09-10 23:14:04','2012-11-15 21:02:03','admin'),(383,'第三百七十条',160,'0001-0001-0002-0008-0001-0024',0,1,1,'倾斜井巷内使用串车提升时必须遵守下列规定：',10,'admin','2012-09-10 23:14:10','2012-11-15 21:02:13','admin'),(384,'第三百七十一条',160,'0001-0001-0002-0008-0001-0025',0,1,1,'倾斜井巷使用绞车提升时必须遵守下列规定：\r\n（一）轨道的铺设质量符合本规程第三百五十三条的规定，并采取轨道防滑措施。\r\n（二）托绳轮（辊）按设计要求设置，并保持转动灵活。\r\n（三）倾斜井巷上端有足够的过卷距离。过卷距离根据巷道倾角、设计载荷、最大提升速度和实际制动力等参量计算确定，并有1.5倍的备用系数。\r\n（四）串车提升的各车场设有信号硐室及躲避硐；运人斜井各车场设有信号和候车硐室，候车硐室具有足够的空间。\r\n',9,'admin','2012-09-10 23:14:15','2012-11-15 21:02:24','admin'),(385,'第三百七十二条',160,'0001-0001-0002-0008-0001-0026',0,1,1,'斜井提升时，严禁蹬钩、行人。\r\n运送物料时，开车前把钩工必须检查牵引车数、各车的连接和装载情况。牵引车数超过规定，连接不良或装载物料超重、超高、超宽或偏载严重有翻车危险时，严禁发出开车信号。\r\n',8,'admin','2012-09-10 23:14:20','2012-11-15 21:02:35','admin'),(386,'第三百七十三条',160,'0001-0001-0002-0008-0001-0027',0,1,1,'采用滚筒驱动带式输送机运输时，应遵守下列规定：',7,'admin','2012-09-10 23:14:25','2012-11-15 21:02:48','admin'),(387,'第三百七十四条',160,'0001-0001-0002-0008-0001-0028',0,1,1,'采用钢丝绳牵引带式输送机运输时，必须遵守下列规定：',6,'admin','2012-09-10 23:14:30','2012-11-15 21:02:57','admin'),(388,'第三百七十五条',160,'0001-0001-0002-0008-0001-0029',0,1,1,'井巷中采用钢丝绳牵引带式输送机或钢丝绳芯带式输送机运送人员时，应遵守下列规定：',5,'admin','2012-09-10 23:14:35','2012-11-15 21:03:07','admin'),(389,'第三百七十六条',160,'0001-0001-0002-0008-0001-0030',0,1,1,'单轨吊车、卡轨车、齿轨车和胶套轮车的运行坡度、运行速度和载荷重量不得超过设计规定的数值，胶套轮材料和钢轨的摩擦系数不得小于0.4。设备最突出部分与巷道之间以及对开列车最突出部分之间的间隙，必须符合本规程第二十二条和第二十三条的规定。',4,'admin','2012-09-10 23:14:53','2012-11-15 21:03:17','admin'),(390,'第三百七十七条',160,'0001-0001-0002-0008-0001-0031',0,1,1,'卡轨车、齿轨车和胶套轮车运行的轨道，应采用不小于22kg/m的钢轨。轨道的铺设质量应符合本规程第三百五十三条的规定。',3,'admin','2012-09-10 23:14:58','2012-11-15 21:03:28','admin'),(391,'第三百七十八条',160,'0001-0001-0002-0008-0001-0032',0,1,1,'单轨吊车、卡轨车、齿轨车和胶套轮车的牵引机车和驱动绞车，应具有可靠的制动系统，并满足以下要求：\r\n（一）保险制动和停车制动的制动力应为额定牵引力的1.5～2倍。\r\n（二）必须设有既可手动又能自动的保险闸。保险闸应具备以下性能：\r\n1．运行速度超过额定速度15%时能自动施闸；\r\n2．施闸时的空动时间不大于0.7s；\r\n3．在最大载荷最大坡度上以最大设计速度向下运行时，制动距离应不超过相当于在这一速度下6s的行程；\r\n4．在最小载荷最大坡度上向上运行时，制动减速度不大于5m/s2。\r\n（三）保险制动和停车制动装置，应设计成失效安全型。\r\n',2,'admin','2012-09-10 23:15:07','2012-11-15 21:03:37','admin'),(392,'第三百七十九条',160,'0001-0001-0002-0008-0001-0033',0,1,1,'在单轨吊车、卡轨车、齿轨车和胶套轮车的牵引机车或头车上，必须装设车灯和喇叭，列车的尾部设有红灯。在钢丝绳牵引的单轨吊车和卡轨车的运输系统内，必须备有列车司机与牵引绞车司机联络用的信号和通信装置。',1,'admin','2012-09-10 23:15:14','2012-11-15 21:03:46','admin'),(393,'第三百八十条',161,'0001-0001-0002-0008-0002-0001',0,1,1,'立井中升降人员，应使用罐笼或带乘人间的箕斗。在井筒内作业或因其他原因，需要使用普通箕斗或救急罐升降人员时，必须制定安全措施。\r\n凿井期间，立井中升降人员可采用吊桶，并遵守下列规定：\r\n',18,'admin','2012-09-10 23:16:50','2012-11-15 21:07:54','admin'),(394,'第三百八十一条',161,'0001-0001-0002-0008-0002-0002',0,1,1,'专为升降人员和升降人员与物料的罐笼（包括有乘人间的箕斗）应符合下列要求：',17,'admin','2012-09-10 23:16:55','2012-11-15 21:07:54','admin'),(395,'第三百八十二条',161,'0001-0001-0002-0008-0002-0003',0,1,1,'提升装置的最大载重量和最大载重差，应在井口公布，严禁超载和超载重差运行。箕斗提升必须采用定重装载。',16,'admin','2012-09-10 23:17:00','2012-11-15 21:07:54','admin'),(396,'第三百八十三条',161,'0001-0001-0002-0008-0002-0004',0,1,1,'升降人员或升降人员和物料的单绳提升罐笼、带乘人间的箕斗，必须装设可靠的防坠器。',15,'admin','2012-09-10 23:17:04','2012-11-15 21:07:54','admin'),(397,'第三百八十四条',161,'0001-0001-0002-0008-0002-0005',0,1,1,'立井使用罐笼提升时，井口、井底和中间运输巷的安全门必须与罐位和提升信号联锁：罐笼到位并发出停车信号后安全门才能打开；安全门未关闭，只能发出调平和换层信号，但发不出开车信号；安全门关闭后才能发出开车信号；发出开车信号后，安全门打不开。井口、井底和中间运输巷都应设置摇台，并与罐笼停止位置、阻车器和提升信号系统联锁：罐笼未到位，放不下摇台，打不开阻车器；摇台未抬起，阻车器未关闭，发不出开车信号。立井井口和井底使用罐座时，必须对罐座设置闭锁装置，罐座未打开，发不出开车信号。升降人员时，严禁使用罐座。',14,'admin','2012-09-10 23:17:09','2012-11-15 21:07:54','admin'),(398,'第三百八十五条',161,'0001-0001-0002-0008-0002-0006',0,1,1,'提升容器的罐耳在安装时与罐道之间所留的间隙：使用滑动罐耳的刚性罐道每侧不得超过5mm，木罐道每侧不得超过10mm；钢丝绳罐道的罐耳滑套直径与钢丝绳直径之差不得大于5mm；采用滚轮罐耳的组合钢罐道的辅助滑动罐耳，每侧间隙应保持10～15mm。',13,'admin','2012-09-10 23:17:13','2012-11-15 21:07:54','admin'),(399,'第三百八十六条',161,'0001-0001-0002-0008-0002-0007',0,1,1,'罐道和罐耳的磨损达到下列程度时，必须更换：\r\n（一）木罐道任一侧磨损量超过15mm或其总间隙超过40mm。\r\n（二）钢轨罐道轨头任一侧磨损量超过8mm，或轨腰磨损量超过原有厚度的25%；罐耳的任一侧磨损量超过8mm，或在同一侧罐耳和罐道的总磨损量超过10mm，或者罐耳与罐道的总间隙超过20mm。\r\n（三）组合钢罐道任一侧的磨损量超过原有厚度的50%。\r\n（四）钢丝绳罐道与滑套的总间隙超过15mm。\r\n',12,'admin','2012-09-10 23:17:18','2012-11-15 21:07:54','admin'),(400,'第三百八十七条',161,'0001-0001-0002-0008-0002-0008',0,1,1,'立井提升容器间及提升容器与井壁、罐道梁、井梁之间的最小间隙，必须符合表5规定。\r\n表5  立井提升容器间及提升容器与井壁、罐道梁、井梁间的最小间隙值\r\n最小间隙值/mm 间隙类别\r\n\r\n罐道和井梁布置	容器与容器之间	容器与井壁之间	容器与罐道梁之间	容器与井梁之间	备  注\r\n罐道布置在容器一侧	200	150	40	150	罐耳与罐道卡子之间为20\r\n罐道布置在容器两侧	木罐道\r\n钢罐道		200\r\n150	50\r\n40	200\r\n150	有卸载滑轮的容器，滑轮与罐道梁间隙增加25\r\n罐道布置在容器正面	木罐道\r\n钢罐道	200\r\n200	200\r\n150	50\r\n40	200\r\n150	\r\n钢丝绳罐道	500	350		350	设防撞绳时，容器之间最小间隙为200\r\n提升容器在安装或检修后，第1次开车前必须检查各个间隙，不符合规定时，不得开车。\r\n采用钢丝绳罐道，当提升容器之间的间隙小于表5规定时，必须设防撞绳。\r\n凿井时，2个提升容器的导向装置最突出部分之间的间隙，不得小于0.2+H/3000m（H--提升高度，m）；井筒深度小于300m时，上述间隙不得小于300mm。\r\n',11,'admin','2012-09-10 23:17:23','2012-11-15 21:07:54','admin'),(401,'第三百八十八条',161,'0001-0001-0002-0008-0002-0009',0,1,1,'钢丝绳罐道应优先选用密封式钢丝绳。每个提升容器（或平衡锤）设有4根罐道绳时，每根罐道绳的最小刚性系数不得小于500N/m，各罐道绳张紧力之差不得小于平均张紧力的5%，内侧张紧力大，外侧张紧力小。1个提升容器（或平衡锤）只有2根罐道绳时，每根罐道绳的刚性系数不得小于1000N/m，各罐道绳的张紧力应相等。单绳提升的2根主提升钢丝绳必须采用同一捻向或不旋转钢丝绳。',10,'admin','2012-09-10 23:17:28','2012-11-15 21:07:54','admin'),(402,'第三百八十九条',161,'0001-0001-0002-0008-0002-00010',0,1,1,'对金属井架、井筒罐道梁和其他装备的固定和锈蚀情况，应每年检查1次。发现松动，应采取加固或其他措施；发现防腐层剥落，应补刷防腐剂。检查和处理结果应留有记录。\r\n建井用金属井架，每次移设后都应涂防腐剂。\r\n',9,'admin','2012-09-10 23:17:33','2012-11-15 21:07:54','admin'),(403,'第三百九十条',161,'0001-0001-0002-0008-0002-0011',0,1,1,'检修人员站在罐笼或箕斗顶上工作时，必须遵守下列规定：\r\n（一）在罐笼或箕斗顶上，必须装设保险伞和栏杆。\r\n（二）必须佩带保险带。\r\n（三）提升容器的速度，一般为0.3～0.5m/s，最大不得超过2m/s。\r\n（四）检修用信号必须安全可靠。\r\n',8,'admin','2012-09-10 23:17:37','2012-11-15 21:07:54','admin'),(404,'第三百九十一条',161,'0001-0001-0002-0008-0002-0012',0,1,1,'提升装置的各部分，包括提升容器、连接装置、防坠器、罐耳、罐道、阻车器、罐座、摇台、装卸设备、天轮和钢丝绳，以及提升绞车各部分，包括滚筒、制动装置、深度指示器、防过卷装置、限速器、调绳装置、传动装置、电动机和控制设备以及各种保护和闭锁装置等，每天必须由专职人员检查1次，每月还必须组织有关人员检查1次。发现问题，必须立即处理，检查和处理结果都应留有记录。',7,'admin','2012-09-10 23:17:42','2012-11-15 21:07:54','admin'),(405,'第三百九十二条',161,'0001-0001-0002-0008-0002-0013',0,1,1,'井口和井底车场必须有把钩工。\r\n人员上下井时，必须遵守乘罐制度，听从把钩工指挥。开车信号发出后严禁进出罐笼。\r\n严禁在同一层罐笼内人员和物料混合提升。\r\n',6,'admin','2012-09-10 23:17:47','2012-11-15 21:07:54','admin'),(406,'第三百九十三条',161,'0001-0001-0002-0008-0002-0014',0,1,1,'每一提升装置，必须装有从井底信号工发给井口信号工和从井口信号工发给绞车司机的信号装置。井口信号装置必须与绞车的控制回路相闭锁，只有在井口信号工发出信号后，绞车才能启动。除常用的信号装置外，还必须有备用信号装置。井底车场与井口之间，井口与绞车司机台之间，除有上述信号装置外，还必须装设直通电话。\r\n1套提升装置服务几个水平使用时，从各水平发出的信号必须有区别。\r\n',5,'admin','2012-09-10 23:17:51','2012-11-15 21:07:54','admin'),(407,'第三百九十四条',161,'0001-0001-0002-0008-0002-0015',0,1,1,'井底车场的信号必须经由井口信号工转发，不得越过井口信号工直接向绞车司机发信号；但有下列情况之一时，不受此限：\r\n（一）发送紧急停车信号。\r\n（二）箕斗提升（不包括带乘人间的箕斗的人员提升）。\r\n（三）单容器提升。\r\n（四）井上下信号联锁的自动化提升系统。\r\n',4,'admin','2012-09-10 23:17:57','2012-11-15 21:07:54','admin'),(408,'第三百九十五条',161,'0001-0001-0002-0008-0002-0016',0,1,1,'用多层罐笼升降人员或物料时，井上、下各层出车平台都必须设有信号工。各信号工发送信号时，必须遵守下列规定：',3,'admin','2012-09-10 23:18:07','2012-11-15 21:07:54','admin'),(409,'第三百九十八条',162,'0001-0001-0002-0008-0003-0001',0,1,1,'使用和保管提升钢丝绳时，必须遵守下列规定：\r\n（一）新绳到货后，应由检验单位进行验收检验。合格后应妥善保管备用，防止损坏或锈蚀。\r\n（二）对每卷钢丝绳必须保存有包括出厂厂家合格证、验收证书等完整的原始资料。\r\n（三）保管超过１年的钢丝绳，在悬挂前必须再进行１次检验，合格后方可使用。\r\n（四）直径为18mm及其以下的专为提升物料用的钢丝绳（立井提升用绳除外），有厂家合格证书，外观检查无锈蚀和损伤，可以不进行本条第一款第（一）项、第（三）项所要求的检验。\r\n',18,'admin','2012-09-10 23:19:38','2012-11-15 21:08:33','admin'),(410,'第三百九十九条',162,'0001-0001-0002-0008-0003-0002',0,1,1,'提升钢丝绳的检验应使用符合条件的设备和方法进行，检验周期应符合下列要求：\r\n（一）升降人员或升降人员和物料用的钢丝绳，自悬挂时起每隔６个月检验１次；悬挂吊盘的钢丝绳，每隔12个月检验1次。\r\n（二）升降物料用的钢丝绳，自悬挂时起12个月时进行第１次检验，以后每隔６个月检验１次。\r\n摩擦轮式绞车用的钢丝绳、平衡钢丝绳以及直径为18mm及其以下的专为升降物料用的钢丝绳（立井提升用绳除外），不受此限。\r\n',17,'admin','2012-09-10 23:19:50','2012-11-15 21:08:41','admin'),(411,'第四百条',162,'0001-0001-0002-0008-0003-0003',0,1,1,'提升钢丝绳的检验应使用符合条件的设备和方法进行，检验周期应符合下列要求：\r\n（一）升降人员或升降人员和物料用的钢丝绳，自悬挂时起每隔６个月检验１次；悬挂吊盘的钢丝绳，每隔12个月检验1次。\r\n（二）升降物料用的钢丝绳，自悬挂时起12个月时进行第１次检验，以后每隔６个月检验１次。\r\n摩擦轮式绞车用的钢丝绳、平衡钢丝绳以及直径为18mm及其以下的专为升降物料用的钢丝绳（立井提升用绳除外），不受此限。\r\n',16,'admin','2012-09-10 23:23:15','2012-11-15 21:08:51','admin'),(412,'第四百零一条',162,'0001-0001-0002-0008-0003-0004',0,1,1,'提升装置使用中的钢丝绳做定期检验时，安全系数有下列情况之一的，必须更换：\r\n（一）专为升降人员用的小于7。\r\n（二）升降人员和物料用的钢丝绳：升降人员时小于7；升降物料时小于6。\r\n（三）专为升降物料用和悬挂吊盘用的小于5。\r\n',15,'admin','2012-09-10 23:23:20','2012-11-15 21:09:01','admin'),(413,'第四百零二条',162,'0001-0001-0002-0008-0003-0005',0,1,1,'新钢丝绳悬挂前的检验（包括验收检验）和在用绳的定期检验，必须按下列规定执行：',14,'admin','2012-09-10 23:23:24','2012-11-15 21:09:10','admin'),(414,'第四百零三条',162,'0001-0001-0002-0008-0003-0006',0,1,1,'摩擦轮式提升钢丝绳的使用期限应不超过2年，平衡钢丝绳的使用期限应不超过4年。如果钢丝绳的断丝、直径缩小和锈蚀程度不超过本规程第四百零五条、第四百零六条、第四百零八条的规定，可继续使用，但不得超过１年。\r\n井筒中悬挂水泵、抓岩机的钢丝绳，使用期限一般为１年；悬挂水管、风管、输料管、安全梯和电缆的钢丝绳，使用期限一般为2年。到期后经检查鉴定，锈蚀程度不超过本规程第四百零八条的规定，可以继续使用。\r\n',13,'admin','2012-09-10 23:23:29','2012-11-15 21:09:20','admin'),(415,'第四百零四条',162,'0001-0001-0002-0008-0003-0007',0,1,1,'提升钢丝绳、罐道绳必须每天检查１次，平衡钢丝绳、防坠器制动绳（包括缓冲绳）、架空乘人装置钢丝绳、钢丝绳牵引带式输送机钢丝绳和井筒悬吊钢丝绳必须至少每周检查１次。对易损坏和断丝或锈蚀较多的一段应停车详细检查。断丝的突出部分应在检查时剪下。检查结果应记入钢丝绳检查记录簿。',12,'admin','2012-09-10 23:23:35','2012-11-15 21:09:29','admin'),(416,'第四百零五条',162,'0001-0001-0002-0008-0003-0008',0,1,1,'各种股捻钢丝绳在1个捻距内断丝断面积与钢丝总断面积之比，达到下列数值时，必须更换：\r\n（一）升降人员或升降人员和物料用的钢丝绳为5%。\r\n（二）专为升降物料用的钢丝绳、平衡钢丝绳、防坠器的制动钢丝绳（包括缓冲绳）和兼作运人的钢丝绳牵引带式输送机的钢丝绳为10%。\r\n（三）罐道钢丝绳为15%。\r\n（四）架空乘人装置、专为无极绳运输用的和专为运物料的钢丝绳牵引带式输送机用的钢丝绳为25%。\r\n',11,'admin','2012-09-10 23:23:39','2012-11-15 21:09:39','admin'),(417,'第四百零六条',162,'0001-0001-0002-0008-0003-0009',0,1,1,'以钢丝绳标称直径为准计算的直径减小量达到下列数值时，必须更换：\r\n（一）提升钢丝绳或制动钢丝绳为10%。\r\n（二）罐道钢丝绳为15%。\r\n使用密封钢丝绳外层钢丝厚度磨损量达到50%时，必须更换。\r\n',10,'admin','2012-09-10 23:23:44','2012-11-15 21:09:50','admin'),(418,'第四百零七条',162,'0001-0001-0002-0008-0003-00010',0,1,1,'钢丝绳在运行中遭受到卡罐、突然停车等猛烈拉力时，必须立即停车检查，发现下列情况之一者，必须将受力段剁掉或更换全绳：\r\n（一）钢丝绳产生严重扭曲或变形。\r\n（二）断丝超过本规程第四百零五条的规定。\r\n（三）直径减小量超过本规程第四百零六条的规定。\r\n（四）遭受猛烈拉力的一段的长度伸长0.5%以上。\r\n在钢丝绳使用期间，断丝数突然增加或伸长突然加快，必须立即更换。\r\n',9,'admin','2012-09-10 23:23:48','2012-11-15 21:09:59','admin'),(419,'第四百零八条',162,'0001-0001-0002-0008-0003-0011',0,1,1,'钢丝绳的钢丝有变黑、锈皮、点蚀麻坑等损伤时，不得用作升降人员。\r\n钢丝绳锈蚀严重，或点蚀麻坑形成沟纹，或外层钢丝松动时，不论断丝数多少或绳径是否变化，必须立即更换。\r\n',8,'admin','2012-09-10 23:23:54','2012-11-15 21:10:07','admin'),(420,'第四百零九条',162,'0001-0001-0002-0008-0003-0012',0,1,1,'使用有接头的钢丝绳时，必须遵守下列规定：\r\n（一）有接头的钢丝绳，只可在下列设备中使用：\r\n1．平巷运输设备；\r\n2．30°以下倾斜井巷中专为升降物料的绞车；\r\n3．斜巷无极绳绞车；\r\n4．斜巷架空乘人装置；\r\n5．斜巷钢丝绳牵引带式输送机。\r\n（二）在倾斜井巷中使用的钢丝绳，其插接长度不得小于钢丝绳直径的1000倍。\r\n',7,'admin','2012-09-10 23:24:01','2012-11-15 21:10:17','admin'),(421,'第四百一十条',162,'0001-0001-0002-0008-0003-0013',0,1,1,'平衡钢丝绳的长度必须与提升容器过卷高度相适应，防止过卷时损坏平衡钢丝绳。使用圆形平衡钢丝绳时，必须有避免平衡钢丝绳扭结的装置。',7,'admin','2012-09-10 23:24:06','2012-11-15 21:10:26','admin'),(422,'第四百一十一条',162,'0001-0001-0002-0008-0003-0014',0,1,1,'主要提升装置必须备有检验合格的备用钢丝绳。\r\n对使用中的钢丝绳，应根据井巷条件及锈蚀情况，至少每月涂油1次。\r\n摩擦轮式提升装置的提升钢丝绳，只准涂、浸专用的钢丝绳油（增磨脂）；但对不绕过摩擦轮部分的钢丝绳，必须涂防腐油。\r\n',5,'admin','2012-09-10 23:25:14','2012-11-15 21:10:34','admin'),(423,'第四百一十二条',162,'0001-0001-0002-0008-0003-0015',0,1,1,'立井提升容器与提升钢丝绳的连接，应采用楔形连接装置。每次更换钢丝绳时，必须对连接装置的主要受力部件进行探伤检验，合格后方可继续使用。楔形连接装置的累计使用期限：单绳提升不得超过10年；多绳提升不得超过15年。\r\n倾斜井巷运输时，矿车之间的连接、矿车与钢丝绳之间的连接，必须使用不能自行脱落的连接装置，并加装保险绳。\r\n倾斜井巷运输用的钢丝绳连接装置，在每次换钢丝绳时，必须用２倍于其最大静荷重的拉力进行试验。\r\n倾斜井巷运输用的矿车连接装置，必须至少每年进行１次２倍于其最大静荷重的拉力试验。\r\n',4,'admin','2012-09-10 23:25:21','2012-11-15 21:10:43','admin'),(424,'第四百一十三条',162,'0001-0001-0002-0008-0003-0016',0,1,1,'新安装或大修后的防坠器，必须进行脱钩试验，合格后方可使用。对使用中的立井罐笼防坠器，应每6个月进行1次不脱钩试验，每年进行1 次脱钩试验。对使用中的斜井人车防坠器，应每班进行1次手动落闸试验、每月进行1次静止松绳落闸试验、每年进行1次重载全速脱钩试验。防坠器的各个连接和传动部分，必须经常处于灵活状态。',3,'admin','2012-09-10 23:25:27','2012-11-15 21:10:52','admin'),(425,'第四百一十四条',162,'0001-0001-0002-0008-0003-0017',0,1,1,'立井和斜井使用的连接装置的性能指标和投用前的试验，必须符合下列要求：',2,'admin','2012-09-10 23:25:32','2012-11-15 21:11:00','admin'),(426,'第四百一十五条',162,'0001-0001-0002-0008-0003-0018',0,1,1,'开凿立井和倾斜井巷时，升降人员和物料的提升装置的连接装置，不得作其他用途。',1,'admin','2012-09-10 23:25:38','2012-11-15 21:11:10','admin'),(427,'第四百一十六条',163,'0001-0001-0002-0008-0004-0001',0,1,1,'除移动式的或辅助性的绞车外，提升装置的天轮、滚筒、摩擦轮、导向轮和导向滚等的最小直径与钢丝绳直径之比值，应符合下列要求：',21,'admin','2012-09-10 23:26:06','2012-11-15 21:16:55','admin'),(428,'第四百一十七条',163,'0001-0001-0002-0008-0004-0002',0,1,1,'立井的天轮、主动摩擦轮、导向轮的直径或滚筒上绕绳部分的最小直径与钢丝绳中最粗钢丝的直径之比值，必须符合下列要求：\r\n（一）井上的提升装置，不小于1200。\r\n（二）井下和凿井用的提升装置，不小于900。\r\n（三）凿井期间升降物料的绞车和悬挂水泵、吊盘用的提升装置，不小于300。\r\n',20,'admin','2012-09-10 23:26:12','2012-11-15 21:16:55','admin'),(429,'第四百一十八条',163,'0001-0001-0002-0008-0004-0003',0,1,1,'天轮到滚筒上的钢丝绳的最大内、外偏角都不得超过1°30′。单层缠绕时，内偏角应保证不咬绳。',19,'admin','2012-09-10 23:26:17','2012-11-15 21:16:55','admin'),(430,'第四百一十九条',163,'0001-0001-0002-0008-0004-0004',0,1,1,'各种提升装置的滚筒上缠绕的钢丝绳层数严禁超过下列规定：\r\n（一）立井中升降人员或升降人员和升降物料的，1层；专为升降物料的，2层。\r\n（二）倾斜井巷中升降人员或升降人员和物料的，2层；升降物料的，3层。\r\n（三）建井期间升降人员和物料的，2层。\r\n（四）现有生产矿井在用的绞车，如果在滚筒上装设过渡绳楔，滚筒强度满足要求且滚筒边缘高度符合本规程第四百二十条规定，可按本条第一款第（一）项、第（二）项所规定的层数增加1层。\r\n移动式的或辅助性的专为升降物料的（包括矸石山和向天桥上提升等）以及凿井时期专为升降物料的，准许多层缠绕。\r\n',18,'admin','2012-09-10 23:26:23','2012-11-15 21:16:55','admin'),(431,'第四百二十条',163,'0001-0001-0002-0008-0004-0005',0,1,1,'滚筒上缠绕2层或2层以上钢丝绳时，必须符合下列要求：\r\n（一）滚筒边缘高出最外1层钢丝绳的高度，至少为钢丝绳直径的2.5倍。\r\n（二）滚筒上必须设有带绳槽的衬垫。\r\n（三）钢丝绳由下层转到上层的临界段（相当于绳圈1/4长的部分）必须经常检查，并应在每季度将钢丝绳移动1/4绳圈的位置。\r\n对现有不带绳槽衬垫的在用绞车，只要在滚筒板上刻有绳槽或用1层钢丝绳作底绳，可继续使用。\r\n',17,'admin','2012-09-10 23:26:28','2012-11-15 21:16:55','admin'),(432,'第四百二十一条',163,'0001-0001-0002-0008-0004-0006',0,1,1,'钢丝绳绳头固定在滚筒上时，应符合下列要求：\r\n（一）必须有特备的容绳或卡绳装置，严禁系在滚筒轴上。\r\n（二）绳孔不得有锐利的边缘，钢丝绳的弯曲不得形成锐角。\r\n（三）滚筒上应经常缠留3圈绳，用以减轻固定处的张力，还必须留有作定期检验用的补充绳。\r\n',16,'admin','2012-09-10 23:26:48','2012-11-15 21:16:55','admin'),(433,'第四百二十二条',163,'0001-0001-0002-0008-0004-0007',0,1,1,'通过天轮的钢丝绳必须低于天轮的边缘，其高差：提升用天轮不得小于钢丝绳直径的1.5倍；悬吊用天轮不得小于钢丝绳直径的1倍。天轮的各段衬垫磨损达到1根钢丝绳直径的深度时，或沿侧面磨损达到钢丝绳直径的1/2时，必须更换。',15,'admin','2012-09-10 23:26:53','2012-11-15 21:16:55','admin'),(434,'第四百二十三条',163,'0001-0001-0002-0008-0004-0008',0,1,1,'摩擦提升装置的绳槽衬垫磨损剩余厚度不得小于钢丝绳直径，绳槽磨损深度不得超过70mm，任一根提升钢丝绳的张力与平均张力之差不得超过±10%。更换钢丝绳时，必须同时更换全部钢丝绳。\r\n第四百二十四条 立井中用罐笼升降人员时的加速度和减速度，都不得超过0.75m/s2，其最大速度，不得超过用下列公式所求得的数值，且最大不得超过12m/s。\r\n式中 v--最大提升速度，m/s；\r\nH--提升高度，m。\r\n立井中用吊桶升降人员时的最大速度：在使用钢丝绳罐道时，不得超过上述公式求得数值的1/2；无罐道时，不得超过1m/s。\r\n',14,'admin','2012-09-10 23:26:59','2012-11-15 21:16:55','admin'),(435,'第四百二十四条',163,'0001-0001-0002-0008-0004-0009',0,1,1,'立井中用罐笼升降人员时的加速度和减速度，都不得超过0.75m/s2，其最大速度，不得超过用下列公式所求得的数值，且最大不得超过12m/s。\r\n式中 v--最大提升速度，m/s；\r\nH--提升高度，m。\r\n立井中用吊桶升降人员时的最大速度：在使用钢丝绳罐道时，不得超过上述公式求得数值的1/2；无罐道时，不得超过1m/s。\r\n',13,'admin','2012-09-10 23:27:05','2012-11-15 21:16:55','admin'),(436,'第四百二十五条',163,'0001-0001-0002-0008-0004-00010',0,1,1,'立井升降物料时，提升容器的最大速度，不得超过用下列公式所求得的数值：\r\n式中 v--最大提升速度，m/s；\r\nH--提升高度，m。\r\n立井中用吊桶升降物料时的最大速度：在使用钢丝绳罐道时，不得超过用上述公式求得数值的2/3；无罐道时，不得超过2m/s。\r\n',12,'admin','2012-09-10 23:27:11','2012-11-15 21:16:55','admin'),(437,'第四百二十六条',163,'0001-0001-0002-0008-0004-0011',0,1,1,'斜井提升容器的最大速度和最大加、减速度应符合下列要求：\r\n（一）升降人员时的速度，不得超过5m/s，并不得超过人车设计的最大允许速度。升降人员时的加速度和减速度，不得超过0.5m/s2。\r\n（二）用矿车升降物料时，速度不得超过5m/s。\r\n（三）用箕斗升降物料时，速度不得超过7m/s；当铺设固定道床并采用大于或等于38kg/m钢轨时，速度不得超过9m/s。\r\n',11,'admin','2012-09-10 23:27:18','2012-11-15 21:16:55','admin'),(438,'第四百二十七条',163,'0001-0001-0002-0008-0004-0012',0,1,1,'提升装置必须装设下列保险装置，并符合下列要求：',10,'admin','2012-09-10 23:27:23','2012-11-15 21:16:55','admin'),(439,'第四百二十八条',163,'0001-0001-0002-0008-0004-0013',0,1,1,'提升绞车必须装设深度指示器、开始减速时能自动示警的警铃与不离开座位即能操纵的常用闸和保险闸，保险闸必须能自动发生制动作用。\r\n常用闸和保险闸共同使用1套闸瓦制动时，操纵和控制机构必须分开。双滚筒提升绞车的2套闸瓦的传动装置必须分开。\r\n对具有2套闸瓦只有1套传动装置的双滚筒绞车，应改为每个滚筒各自有其控制机构的弹簧闸。\r\n提升绞车除设有机械制动闸外，还应设有电气制动装置。\r\n严禁司机离开工作岗位、擅自调整制动闸。\r\n',9,'admin','2012-09-10 23:27:28','2012-11-15 21:16:55','admin'),(440,'第四百二十九条',163,'0001-0001-0002-0008-0004-0014',0,1,1,'保险闸必须采用配重式或弹簧式的制动装置，除可由司机操纵外，还必须能自动抱闸，并同时自动切断提升装置电源。\r\n常用闸必须采用可调节的机械制动装置。\r\n对现用的使用手动式常用闸的绞车，如设有可靠的保险闸时，可继续使用。\r\n用于辅助物料运输的滚筒直径在0.8m及其以下的绞车或提升重量在8t以下的凿井用稳车，可用手动闸。\r\n',8,'admin','2012-09-10 23:27:35','2012-11-15 21:16:55','admin'),(441,'第四百三十条',163,'0001-0001-0002-0008-0004-0015',0,1,1,'开凿立井时，悬挂吊盘、水泵和其他设备的稳车，必须装设可靠的制动装置和防逆转装置，并设有电气闭锁。',7,'admin','2012-09-10 23:27:40','2012-11-15 21:16:55','admin'),(442,'第四百三十一条',163,'0001-0001-0002-0008-0004-0016',0,1,1,'保险闸或保险闸第一级由保护回路断电时起至闸瓦接触到闸轮上的空动时间：压缩空气驱动闸瓦式制动闸不得超过0.5s，储能液压驱动闸瓦式制动闸不得超过0.6s，盘式制动闸不得超过0.3s。对斜井提升，为保证上提紧急制动不发生松绳而必须延时制动时，上提空动时间不受此限。盘式制动闸的闸瓦与制动盘之间的间隙应不大于2mm。保险闸施闸时，杠杆和闸瓦不得发生显著的弹性摆动。',6,'admin','2012-09-10 23:27:45','2012-11-15 21:16:55','admin'),(443,'第四百三十二条',163,'0001-0001-0002-0008-0004-0017',0,1,1,'提升绞车的常用闸和保险闸制动时，所产生的力矩与实际提升最大静荷重旋转力矩之比K值不得小于3。对质量模数较小的绞车，上提重载保险闸的制动减速度超过本规程第四百三十三条所规定的限值时，可将保险闸的K值适当降低，但不得小于2。凿井时期，升降物料用的绞车K值不得小于2。\r\n在调整双滚筒绞车滚筒旋转的相对位置时，制动装置在各滚筒闸轮上所发生的力矩，不得小于该滚筒所悬重量（钢丝绳重量与提升容器重量之和）形成的旋转力矩的1.2倍。\r\n计算制动力矩时，闸轮和闸瓦摩擦系数应根据实测确定，一般采用0.30～0.35；常用闸和保险闸的力矩应分别计算。\r\n',5,'admin','2012-09-10 23:27:50','2012-11-15 21:16:55','admin'),(444,'第四百三十四条',163,'0001-0001-0002-0008-0004-0018',0,1,1,'',3,'admin','2012-09-10 23:27:57','2012-11-15 21:16:55','admin'),(445,'第四百三十四条',163,'0001-0001-0002-0008-0004-0019',0,1,1,'主要提升装置必须配有正、副司机，在交接班升降人员的时间内，必须正司机操作，副司机监护。\r\n每班升降人员前，应先开１次空车，检查绞车动作情况；但连续运转时，不受此限。\r\n发生故障，必须立即向矿调度室报告。\r\n',3,'admin','2012-09-10 23:28:03','2012-11-15 21:16:55','admin'),(446,'第四百三十五条',163,'0001-0001-0002-0008-0004-0020',0,1,1,'安装的矿井主要提升装置，必须经验收合格后方可投入使用。投入运行后的设备，必须每年进行１次检查，每３年进行１次测试，认定合格后方可继续使用。\r\n检查验收和测试内容，应包括下列项目：\r\n',2,'admin','2012-09-10 23:28:09','2012-11-15 21:16:55','admin'),(447,'第四百三十七条',164,'0001-0001-0002-0008-0005-0001',0,1,1,'空气压缩机必须有压力表和安全阀。压力表必须定期校准。安全阀和压力调节器必须动作可靠，安全阀动作压力不得超过额定压力的1.1倍。使用油润滑的空气压缩机必须装设断油保护装置或断油信号显示装置。水冷式空气压缩机必须装设断水保护装置或断水信号显示装置。',3,'admin','2012-09-10 23:28:22','2012-11-15 21:17:14','admin'),(448,'第四百三十八条',164,'0001-0001-0002-0008-0005-0002',0,1,1,'空气压缩机的排气温度单缸不得超过190℃、双缸不得超过160℃。必须装设温度保护装置，在超温时能自动切断电源。\r\n空气压缩机吸气口必须设置过滤装置。\r\n空气压缩机必须使用闪点不低于215℃的压缩机油。',2,'admin','2012-09-10 23:28:28','2012-11-15 21:17:23','admin'),(449,'第四百三十九条',164,'0001-0001-0002-0008-0005-0003',0,1,1,'空气压缩机的风包，在地面应设在室外阴凉处，在井下应设在空气流畅的地方。在井下，固定式压缩机和风包应分别设置在2个硐室内。风包内的温度应保持在120℃以下，并装有超温保护装置，在超温时可自动切断电源和报警。\r\n风包上必须装有动作可靠的安全阀和放水阀，并有检查孔。必须定期清除风包内的油垢。新安装或检修后的风包，应用1.5倍空气压缩机工作压力做水压试验。在风包出口管路上必须加装释压阀，释压阀的口径不得小于出风管的直径，释放压力应为空气压缩机最高工作压力的1.25～1.4倍。\r\n',1,'admin','2012-09-10 23:28:33','2012-11-15 21:17:35','admin'),(450,'第四百四十条',165,'0001-0001-0002-0009-0001-0001',0,1,1,'煤矿地面、井下各种电气设备、电力和通信系统的设计、安装、验收、运行、检修、试验以及安全等工作，可参照有关部门的规程执行；遇有与本规程相抵触的，应按本规程执行。',13,'admin','2012-09-10 23:28:49','2012-11-15 21:21:02','admin'),(451,'第四百四十一条',165,'0001-0001-0002-0009-0001-0002',0,1,1,'矿井应有两回路电源线路。当任一回路发生故障停止供电时，另一回路应能担负矿井全部负荷。年产60000t以下（不含60000t）的矿井采用单回路供电时，必须有备用电源。备用电源的容量必须满足通风、排水、提升等要求，并保证主要通风机等在10min内可靠启动和运行。备用电源应有专人负责管理和维护，每10天至少进行一次启动和运行试验，试验期间不得影响矿井通风等，试验记录要存档备查。\r\n矿井的两回路电源线路上都不得分接任何负荷。\r\n正常情况下，矿井电源应采用分列运行方式。若一回路运行，另一回路必须带电备用，以保证供电的连续性和可靠性。带电备用电源的变压器宜热备用；若冷备用，必须保证备用电源能及时投入正常运行，保证主要通风机等在10min内可靠启动和运行。\r\n10kV及其以下的矿井架空电源线路不得共杆架设。\r\n矿井电源线路上严禁装设负荷定量器。\r\n',14,'admin','2012-09-10 23:28:55','2012-11-15 21:21:02','admin'),(452,'第四百四十二条',165,'0001-0001-0002-0009-0001-0003',0,1,1,'对井下变（配）电所〔含井下各水平中央变（配）电所和采区变（配）电所〕、主排水泵房和下山开采的采区排水泵房供电的线路，不得少于两回路。当任一回路停止供电时，其余回路应能担负全部负荷。向局部通风机供电的井下变（配）电所应采用分列运行方式。\r\n主要通风机、提升人员的立井绞车、抽放瓦斯泵等主要设备房，应各有两回路直接由变（配）电所馈出的供电线路；受条件限制时，其中的一回路可引自上述同种设备房的配电装置。向煤（岩）与瓦斯（二氧化碳）突出矿井自救系统供风的压风机、井下移动瓦斯抽放泵应各有两回路直接由变（配）电所馈出的供电线路。\r\n本条上述供电线路应来自各自的变压器和母线段，线路上不应分接任何负荷。\r\n本条上述设备的控制回路和辅助设备，必须有与主要设备同等可靠的备用电源。”\r\n',12,'admin','2012-09-10 23:29:08','2012-11-15 21:21:02','admin'),(454,'第四百四十三条',165,'0001-0001-0002-0009-0001-0004',0,1,1,'严禁井下配电变压器中性点直接接地。\r\n严禁由地面中性点直接接地的变压器或发电机直接向井下供电。\r\n',10,'admin','2012-09-10 23:29:59','2012-11-15 21:21:02','admin'),(455,'第四百四十四条',165,'0001-0001-0002-0009-0001-0005',0,1,1,'选用的井下电气设备，必须符合表10的要求。\r\n普通型携带式电气测量仪表，必须在瓦斯浓度1.0%以下的地点使用，并实时监测使用环境的瓦斯浓度。\r\n',9,'admin','2012-09-10 23:30:09','2012-11-15 21:21:02','admin'),(456,'第四百四十五条',165,'0001-0001-0002-0009-0001-0006',0,1,1,'井下不得带电检修、搬迁电气设备、电缆和电线。\r\n检修或搬迁前，必须切断电源，检查瓦斯，在其巷道风流中瓦斯浓度低于1.0%时，再用与电源电压相适应的验电笔检验；检验无电后，方可进行导体对地放电。控制设备内部安有放电装置的，不受此限。所有开关的闭锁装置必须能可靠地防止擅自送电，防止擅自开盖操作，开关把手在切断电源时必须闭锁，并悬挂“有人工作，不准送电”字样的警示牌，只有执行这项工作的人员才有权取下此牌送电。\r\n',8,'admin','2012-09-10 23:30:15','2012-11-15 21:21:02','admin'),(457,'第四百四十六条',165,'0001-0001-0002-0009-0001-0007',0,1,1,'操作井下电气设备应遵守下列规定：\r\n（一）非专职人员或非值班电气人员不得擅自操作电气设备。\r\n（二）操作高压电气设备主回路时，操作人员必须戴绝缘手套，并穿电工绝缘靴或站在绝缘台上。\r\n（三）手持式电气设备的操作手柄和工作中必须接触的部分必须有良好绝缘。\r\n',7,'admin','2012-09-10 23:30:21','2012-11-15 21:21:02','admin'),(458,'第四百四十七条',165,'0001-0001-0002-0009-0001-0008',0,1,1,'容易碰到的、裸露的带电体及机械外露的转动和传动部分必须加装护罩或遮栏等防护设施。',7,'admin','2012-09-10 23:30:26','2012-11-15 21:21:02','admin'),(459,'第四百四十八条',165,'0001-0001-0002-0009-0001-0009',0,1,1,'井下各级配电电压和各种电气设备的额定电压等级，应符合下列要求：（一）高压，不超过10000V。（二）低压，不超过1140V。（三）照明、信号、电话和手持式电气设备的供电额定电压，不超过127V。（四）远距离控制线路的额定电压，不超过36V。采区电气设备使用3300V供电时，必须制定专门的安全措施。',5,'admin','2012-09-10 23:30:33','2012-11-15 21:21:02','admin'),(460,'第四百四十九条',165,'0001-0001-0002-0009-0001-00010',0,1,1,'井下低压配电系统同时存在2种或2种以上电压时，低压电气设备上应明显地标出其电压额定值。',4,'admin','2012-09-10 23:30:40','2012-11-15 21:21:02','admin'),(461,'第四百五十条',165,'0001-0001-0002-0009-0001-0011',0,1,1,'矿井必须备有井上、下配电系统图，井下电气设备布置示意图和电力、电话、信号、电机车等线路平面敷设示意图，并随着情况变化定期填绘。图中应注明：\r\n（一）电动机、变压器、配电设备、信号装置、通信装置等装设地点。\r\n（二）每一设备的型号、容量、电压、电流种类及其他技术性能。\r\n（三）馈出线的短路、过负荷保护的整定值，熔断器熔体的额定电流值以及被保护干线和支线最远点两相短路电流值。\r\n（四）线路电缆的用途、型号、电压、截面和长度。\r\n（五）保护接地装置的安设地点。\r\n',3,'admin','2012-09-10 23:30:46','2012-11-15 21:21:02','admin'),(462,'第四百五十一条',165,'0001-0001-0002-0009-0001-0012',0,1,1,'电气设备不应超过额定值运行。\r\n井下防爆电气设备变更额定值使用和进行技术改造时，必须经国家授权的矿用产品质量监督检验部门检验合格后，方可投入运行。\r\n',2,'admin','2012-09-10 23:30:51','2012-11-15 21:21:02','admin'),(463,'第四百五十三条',166,'0001-0001-0002-0009-0002-0001',0,1,1,'井下电力网的短路电流不得超过其控制用的断路器在井下使用的开断能力，并应校验电缆的热稳定性。\r\n非煤矿用高压油断路器用于井下时，其使用的开断电流不应超过额定值的1/2。\r\n',7,'admin','2012-09-10 23:31:09','2012-11-15 21:22:00','admin'),(464,'第四百五十四条',166,'0001-0001-0002-0009-0002-0002',0,1,1,'硐室外严禁使用油浸式低压电气设备。\r\n40kW及以上的电动机，应采用真空电磁起动器控制。\r\n',6,'admin','2012-09-10 23:31:14','2012-11-15 21:22:07','admin'),(465,'第四百五十五条',166,'0001-0001-0002-0009-0002-0003',0,1,1,'井下高压电动机、动力变压器的高压控制设备，应具有短路、过负荷、接地和欠压释放保护。井下由采区变电所、移动变电站或配电点引出的馈电线上，应装设短路、过负荷和漏电保护装置。低压电动机的控制设备，应具备短路、过负荷、单相断线、漏电闭锁保护装置及远程控制装置。',5,'admin','2012-09-10 23:31:18','2012-11-15 21:22:15','admin'),(466,'第四百五十六条',166,'0001-0001-0002-0009-0002-0004',0,1,1,'井下配电网路（变压器馈出线路、电动机等）均应装设过流、短路保护装置；必须用该配电网路的最大三相短路电流校验开关设备的分断能力和动、热稳定性以及电缆的热稳定性。必须正确选择熔断器的熔体。\r\n必须用最小两相短路电流校验保护装置的可靠动作系数。保护装置必须保证配电网路中最大容量的电气设备或同时工作成组的电气设备能够起动。\r\n',4,'admin','2012-09-10 23:31:24','2012-11-15 21:22:22','admin'),(467,'第四百五十七条',166,'0001-0001-0002-0009-0002-0005',0,1,1,'矿井高压电网，必须采取措施限制单相接地电容电流不超过20A。\r\n地面变电所和井下中央变电所的高压馈电线上，必须装设有选择性的单相接地保护装置；供移动变电站的高压馈电线上，必须装设有选择性的动作于跳闸的单相接地保护装置。\r\n井下低压馈电线上，必须装设检漏保护装置或有选择性的漏电保护装置，保证自动切断漏电的馈电线路。\r\n每天必须对低压检漏装置的运行情况进行1次跳闸试验。\r\n煤电钻必须使用设有检漏、漏电闭锁、短路、过负荷、断相、远距离起动和停止煤电钻功能的综合保护装置。每班使用前，必须对煤电钻综合保护装置进行1次跳闸试验。\r\n',3,'admin','2012-09-10 23:31:30','2012-11-15 21:23:31','admin'),(468,'第四百五十八条',166,'0001-0001-0002-0009-0002-0006',0,1,1,'直接向井下供电的高压馈电线上，严禁装设自动重合闸。手动合闸时，必须事先同井下联系。井下低压馈电线上有可靠的漏电、短路检测闭锁装置时，可采用瞬间1次自动复电系统。',2,'admin','2012-09-10 23:31:40','2012-11-15 21:23:39','admin'),(469,'第四百五十九条',166,'0001-0001-0002-0009-0002-0007',0,1,1,'井上、下必须装设防雷电装置，并遵守下列规定：\r\n（一）经由地面架空线路引入井下的供电线路和电机车架线，必须在入井处装设防雷电装置。\r\n（二）由地面直接入井的轨道及露天架空引入（出）的管路，必须在井口附近将金属体进行不少于2处的良好的集中接地。\r\n（三）通信线路必须在入井处装设熔断器和防雷电装置。\r\n',1,'admin','2012-09-10 23:31:46','2012-11-15 21:23:47','admin'),(470,'第四百六十条',167,'0001-0001-0002-0009-0003-0001',0,1,1,'永久性井下中央变电所和井底车场内的其他机电设备硐室，应砌碹或用其他可靠的方式支护。采区变电所应用不燃性材料支护。\r\n硐室必须装设向外开的防火铁门。铁门全部敞开时，不得妨碍运输。铁门上应装设便于关严的通风孔。装有铁门时，门内可加设向外开的铁栅栏门，但不得妨碍铁门的开闭。\r\n从硐室出口防火铁门起5m内的巷道，应砌碹或用其他不燃性材料支护。硐室内必须设置足够数量的扑灭电气火灾的灭火器材。\r\n井下中央变电所和主要排水泵房的地面标高，应分别比其出口与井底车场或大巷连接处的底板标高高出0.5m。\r\n',6,'admin','2012-09-10 23:31:57','2012-11-15 21:23:58','admin'),(471,'第四百六十一条',167,'0001-0001-0002-0009-0003-0002',0,1,1,'采掘工作面配电点的位置和空间必须能满足设备检修和巷道运输、矿车通过及其他设备安装的要求，并用不燃性材料支护。',5,'admin','2012-09-10 23:32:03','2012-11-15 21:24:06','admin'),(472,'第四百六十二条',167,'0001-0001-0002-0009-0003-0003',0,1,1,'变电硐室长度超过6m时，必须在硐室的两端各设1个出口。',4,'admin','2012-09-10 23:32:08','2012-11-15 21:24:12','admin'),(473,'第四百六十三条',167,'0001-0001-0002-0009-0003-0004',0,1,1,'硐室内各种设备与墙壁之间应留出0.5m以上的通道，各种设备相互之间，应留出0.8m以上的通道。对不需从两侧或后面进行检修的设备，可不留通道。',3,'admin','2012-09-10 23:32:13','2012-11-15 21:24:20','admin'),(474,'第四百六十四条',167,'0001-0001-0002-0009-0003-0005',0,1,1,'带油的电气设备必须设在机电设备硐室内。严禁设集油坑。\r\n硐室不应有滴水。硐室的过道应保持畅通，严禁存放无关的设备和物件。带油的电气设备溢油或漏油时，必须立即处理。\r\n',2,'admin','2012-09-10 23:32:19','2012-11-15 21:24:26','admin'),(475,'第四百六十五条',167,'0001-0001-0002-0009-0003-0006',0,1,1,'硐室入口处必须悬挂“非工作人员禁止入内”字样的警示牌。硐室内必须悬挂与实际相符的供电系统图。硐室内有高压电气设备时，入口处和硐室内必须在明显地点悬挂“高压危险”字样的警示牌。\r\n采区变电所应设专人值班。无人值班的变电硐室必须关门加锁，并有值班人员巡回检查。\r\n硐室内的设备，必须分别编号，标明用途，并有停送电的标志。\r\n',1,'admin','2012-09-10 23:32:25','2012-11-15 21:24:33','admin'),(476,'第四百六十六条',168,'0001-0001-0002-0009-0004-0001',0,1,1,'在总回风巷和专用回风巷中不应敷设电缆。在机械提升的进风的倾斜井巷（不包括输送机上、下山）和使用木支架的立井井筒中敷设电缆时，必须有可靠的安全措施。\r\n溜放煤、矸、材料的溜道中严禁敷设电缆。\r\n',7,'admin','2012-09-10 23:32:38','2012-11-15 21:24:43','admin'),(477,'第四百六十七条',168,'0001-0001-0002-0009-0004-0002',0,1,1,'井下电缆的选用应遵守下列规定：',6,'admin','2012-09-10 23:32:43','2012-11-15 21:24:56','admin'),(478,'第四百六十八条',168,'0001-0001-0002-0009-0004-0003',0,1,1,'敷设电缆（与手持式或移动式设备连接的电缆除外）应遵守下列规定：',5,'admin','2012-09-10 23:32:48','2012-11-15 21:25:03','admin'),(479,'第四百六十九条',168,'0001-0001-0002-0009-0004-0004',0,1,1,'电缆不应悬挂在风管或水管上，不得遭受淋水。电缆上严禁悬挂任何物件。电缆与压风管、供水管在巷道同一侧敷设时，必须敷设在管子上方，并保持0.3m以上的距离。在有瓦斯抽放管路的巷道内，电缆（包括通信、信号电缆）必须与瓦斯抽放管路分挂在巷道两侧。盘圈或盘“8”字形的电缆不得带电，但给采、掘机组供电的电缆不受此限。\r\n井筒和巷道内的通信和信号电缆应与电力电缆分挂在井巷的两侧，如果受条件所限：在井筒内，应敷设在距电力电缆0.3m以外的地方；在巷道内，应敷设在电力电缆上方0.1m以上的地方。\r\n高、低压电力电缆敷设在巷道同一侧时，高、低压电缆之间的距离应大于0.1m。高压电缆之间、低压电缆之间的距离不得小于50mm。\r\n井下巷道内的电缆，沿线每隔一定距离、拐弯或分支点以及连接不同直径电缆的接线盒两端、穿墙电缆的墙的两边都应设置注有编号、用途、电压和截面的标志牌。\r\n',4,'admin','2012-09-10 23:32:55','2012-11-15 21:25:30','admin'),(480,'第四百七十条',168,'0001-0001-0002-0009-0004-0005',0,1,1,'立井井筒中所用的电缆中间不得有接头；因井筒太深需设接头时，应将接头设在中间水平巷道内。\r\n运行中因故需要增设接头而又无中间水平巷道可利用时，可在井筒中设置接线盒，接线盒应放置在托架上，不应使接头承力。',3,'admin','2012-09-10 23:33:00','2012-11-15 21:25:37','admin'),(481,'第四百七十一条',168,'0001-0001-0002-0009-0004-0006',0,1,1,'电缆穿过墙壁部分应用套管保护，并严密封堵管口。',2,'admin','2012-09-10 23:33:06','2012-11-15 21:25:44','admin'),(482,'第四百七十二条',168,'0001-0001-0002-0009-0004-0007',0,1,1,'电缆的连接应符合下列要求：',1,'admin','2012-09-10 23:33:12','2012-11-15 21:25:51','admin'),(483,'第四百七十三条',169,'0001-0001-0002-0009-0005-0001',0,1,1,'井下下列地点必须有足够的照明：',9,'admin','2012-09-10 23:33:22','2012-11-15 21:26:01','admin'),(484,'第四百七十四条',169,'0001-0001-0002-0009-0005-0002',0,1,1,'严禁用电机车架空线作照明电源。',8,'admin','2012-09-10 23:33:33','2012-11-15 21:26:08','admin'),(485,'第四百七十五条',169,'0001-0001-0002-0009-0005-0003',0,1,1,'矿灯的管理和使用应遵守下列规定：\r\n（一）矿井完好的矿灯总数，至少应比经常用灯的总人数多10%。\r\n（二）矿灯应集中统一管理。每盏矿灯必须编号，经常使用矿灯的人员必须专人专灯。\r\n（三）矿灯应保持完好，出现电池漏液、亮度不够、电线破损、灯锁失效、灯头密封不严、灯头圈松动、玻璃破裂等情况时，严禁发放。发出的矿灯，最低应能连续正常使用11h。\r\n（四）严禁使用矿灯人员拆开、敲打、撞击矿灯。人员出井后（地面领用矿灯人员，在下班后），必须立即将矿灯交还灯房。\r\n（五）在每次换班2h内，灯房人员必须把没有还灯人员的名单报告矿调度室。\r\n（六）矿灯必须装有可靠的短路保护装置。高瓦斯矿井应装有短路保护器。\r\n',7,'admin','2012-09-10 23:33:38','2012-11-15 21:26:16','admin'),(486,'第四百七十六条',169,'0001-0001-0002-0009-0005-0004',0,1,1,'矿灯房应符合下列要求：\r\n（一）应用不燃性材料建筑。\r\n（二）取暖应用蒸汽或热水管式设备。个别情况下，采用火炉取暖时，火炉间应有单独的间隔和出口。\r\n（三）应有良好的通风装置，灯房和仓库内严禁烟火，并备有灭火器材。\r\n充电装置应有可靠的充电稳压装置。\r\n',6,'admin','2012-09-10 23:33:48','2012-11-15 21:26:24','admin'),(487,'第四百七十七条',169,'0001-0001-0002-0009-0005-0005',0,1,1,'准备和装添电液必须使用专用器具。工作人员必须戴防护眼镜、口罩和橡胶手套，系橡胶围裙，穿胶鞋。\r\n调合和贮存电液，必须使用有盖的瓷质、玻璃质等容器。调合酸性电液时，必须将硫酸徐徐倒入水中，严禁向硫酸中倒水。\r\n房间内必须备有中和电液用的溶液。',5,'admin','2012-09-10 23:33:54','2012-11-15 21:26:32','admin'),(488,'第四百七十八条',169,'0001-0001-0002-0009-0005-0006',0,1,1,'主副井绞车房、井底车场、运输调度室、采区变电所、上下山绞车房、水泵房、带式输送机集中控制硐室等主要机电设备硐室和采掘工作面，应安装电话。井下主要水泵房、井下中央变电所、矿井地面变电所和地面通风机房的电话，应能与矿调度室直接联系。\r\n井下电话线路严禁利用大地作回路。\r\n',4,'admin','2012-09-10 23:34:00','2012-11-15 21:26:39','admin'),(489,'第四百七十九条',169,'0001-0001-0002-0009-0005-0007',0,1,1,'电气信号应符合下列要求：\r\n（一）矿井中的电气信号，除信号集中闭塞外应能同时发声和发光。重要信号装置附近，应标明信号的种类和用途。\r\n（二）升降人员和主要井口绞车的信号装置的直接供电线路上，严禁分接其他负荷。\r\n',3,'admin','2012-09-10 23:34:05','2012-11-15 21:26:47','admin'),(490,'第四百八十条',169,'0001-0001-0002-0009-0005-0008',0,1,1,'井下照明和信号装置，应采用具有短路、过载和漏电保护的照明信号综合保护装置配电。',2,'admin','2012-09-10 23:34:11','2012-11-15 21:26:56','admin'),(491,'第四百八十一条',169,'0001-0001-0002-0009-0005-0009',0,1,1,'井下防爆型的通信、信号和控制等装置，应优先采用本质安全型。',1,'admin','2012-09-10 23:34:15','2012-11-15 21:27:04','admin'),(492,'第四百八十二条',170,'0001-0001-0002-0009-0006-0001',0,1,1,'电压在36V以上和由于绝缘损坏可能带有危险电压的电气设备的金属外壳、构架，铠装电缆的钢带（或钢丝）、铅皮或屏蔽护套等必须有保护接地。',6,'admin','2012-09-10 23:34:28','2012-11-15 21:27:14','admin'),(493,'第四百八十三条',170,'0001-0001-0002-0009-0006-0002',0,1,1,'接地网上任一保护接地点的接地电阻值不得超过2Ω。每一移动式和手持式电气设备至局部接地极之间的保护接地用的电缆芯线和接地连接导线的电阻值，不得超过1Ω。',5,'admin','2012-09-10 23:34:37','2012-11-15 21:27:21','admin'),(494,'第四百八十四条',170,'0001-0001-0002-0009-0006-0003',0,1,1,'所有电气设备的保护接地装置（包括电缆的铠装、铅皮、接地芯线）和局部接地装置，应与主接地极连接成1个总接地网。\r\n主接地极应在主、副水仓中各埋设1块。主接地极应用耐腐蚀的钢板制成，其面积不得小于0.75m2、厚度不得小于5mm。\r\n在钻孔中敷设的电缆不能与主接地极连接时，应单独形成一分区接地网，其接地电阻值不得超过2Ω。',4,'admin','2012-09-10 23:34:43','2012-11-15 21:27:28','admin'),(495,'第四百八十五条',170,'0001-0001-0002-0009-0006-0004',0,1,1,'下列地点应装设局部接地极：',3,'admin','2012-09-10 23:34:52','2012-11-15 21:27:37','admin'),(496,'第四百八十六条',170,'0001-0001-0002-0009-0006-0005',0,1,1,'连接主接地极的接地母线，应采用截面不小于50mm2的铜线，或截面不小于100mm2的镀锌铁线，或厚度不小于4mm、截面不小于100mm2的扁钢。\r\n电气设备的外壳与接地母线或局部接地极的连接，电缆连接装置两头的铠装、铅皮的连接，应采用截面不小于25mm2的铜线，或截面不小于50mm2的镀锌铁线，或厚度不小于4mm、截面不小于50mm2的扁钢。\r\n',2,'admin','2012-09-10 23:34:59','2012-11-15 21:27:51','admin'),(497,'第四百八十七条',170,'0001-0001-0002-0009-0006-0006',0,1,1,'橡套电缆的接地芯线，除用作监测接地回路外，不得兼作他用。',1,'admin','2012-09-10 23:35:04','2012-11-15 21:27:58','admin'),(498,'第四百八十八条',171,'0001-0001-0002-0009-0007-0001',0,1,1,'电气设备的检查、维护和调整，必须由电气维修工进行。高压电气设备的修理和调整工作，应有工作票和施工措施。\r\n高压停、送电的操作，可根据书面申请或其他可靠的联系方式，得到批准后，由专责电工执行。\r\n采区电工，在特殊情况下，可对采区变电所内高压电气设备进行停、送电的操作，但不得擅自打开电气设备进行修理。\r\n',4,'admin','2012-09-10 23:35:16','2012-11-15 21:28:08','admin'),(499,'第四百八十九条',171,'0001-0001-0002-0009-0007-0002',0,1,1,'井下防爆电气设备的运行、维护和修理，必须符合防爆性能的各项技术要求。防爆性能遭受破坏的电气设备，必须立即处理或更换，严禁继续使用。',3,'admin','2012-09-10 23:35:22','2012-11-15 21:28:15','admin'),(500,'第四百九十条',171,'0001-0001-0002-0009-0007-0003',0,1,1,'矿井应按表11的规定对电气设备和电缆进行检查、调整。\r\n表11  电气设备和电缆的检查、调整规定\r\n检查、调整项目	检查周期	备    注\r\n使用中的防爆电气设备的防爆性能检查	每月1次	每日应由分片负责电工检查1次外部\r\n配电系统继电保护装置检查整定	每6个月1次	负荷变化时应及时整定\r\n高压电缆的泄漏和耐压试验	每年1次	\r\n主要电气设备绝缘电阻的检查	每6个月不少于1次	\r\n固定敷设电缆的绝缘和外部检查	每季1次	每周应由专责电工检查1次外部和悬挂情况\r\n移动式电气设备的橡套电缆绝缘检查	每月1次	每班由当班司机或专责电工检查1次外皮有无破损\r\n接地电网接地电阻值测定	每季1次	\r\n新安装的电气设备绝缘电阻和接地电阻的测定		投入运行以前\r\n检查和调整结果应记入专用的记录簿内。检查和调整中发现的问题，应指派专人限期处理。\r\n',2,'admin','2012-09-10 23:35:28','2012-11-15 21:28:23','admin'),(501,'第四百九十一条',171,'0001-0001-0002-0009-0007-0004',0,1,1,'电气设备使用的绝缘油的物理、化学性能检测和电气耐压试验，每年应进行1次，但对操作频繁的电气设备使用的绝缘油，应每6个月进行1次耐压试验。\r\n油断路器经3次切断短路故障后，其绝缘油应加试1次耐压试验，并检查有无游离碳。\r\n不符合标准的绝缘油必须及时处理或更换。油浸电气设备的绝缘油量应定期检查，并保持规定油量。\r\n更换和试验矿用设备绝缘油应有记录。\r\n',1,'admin','2012-09-10 23:35:36','2012-11-15 21:28:31','admin'),(502,'第四百九十三条',69,'0001-0001-0002-00010-0001-0002',0,1,1,'所有煤矿必须有矿山救护队为其服务。煤矿企业应设立矿山救护队，不具备单独设立矿山救护队条件的煤矿企业，应指定兼职救援人员，并与就近的救护队签订救护协议或联合建立矿山救护队；否则，不得生产。',8,'admin','2012-09-10 23:35:58','2012-11-14 20:29:40','admin'),(503,'第四百九十四条',69,'0001-0001-0002-00010-0001-0003',0,1,1,'矿山救护队必须经国家煤矿安全监察局进行资质认证，取得合格证后方可从事矿山救护工作。',7,'admin','2012-09-10 23:36:06','2012-11-14 20:29:57','admin'),(504,'第四百九十五条',69,'0001-0001-0002-00010-0001-0004',0,1,1,'任何人不得调动矿山救护队、救护装备和救护车辆从事与矿山救护无关的工作。',6,'admin','2012-09-10 23:36:12','2012-11-14 20:30:17','admin'),(505,'第四百九十六条',69,'0001-0001-0002-00010-0001-0005',0,1,1,'矿山救护队每月至少进行1次佩戴氧气呼吸器的训练，每次佩戴氧气呼吸器的时间不少于3h；每季至少进行1次高温浓烟演习训练。',5,'admin','2012-09-10 23:36:17','2012-11-14 20:30:28','admin'),(506,'第四百九十七条',69,'0001-0001-0002-00010-0001-0006',0,1,1,'矿山救护队必须备有服务矿井的通风系统图和灾害预防和处理计划等技术资料。\r\n矿山救护队应根据服务矿井主要灾害类型制定预处理方案，并进行训练演习。\r\n',4,'admin','2012-09-10 23:36:22','2012-11-14 20:30:39','admin'),(507,'第四百九十八条',69,'0001-0001-0002-00010-0001-0007',0,1,1,'制定需矿山救护队执行的排放瓦斯、启封火区、震动爆破、反风演习等安全技术工作的安全措施时，矿山救护队必须参加。\r\n矿山救护队从事井下安全技术工作时，必须携带氧气呼吸器及相关仪器装备，并按规定佩用氧气呼吸器。\r\n',4,'admin','2012-09-10 23:36:27','2012-11-14 20:31:03','admin'),(508,'第四百九十九条',69,'0001-0001-0002-00010-0001-0008',0,1,1,'矿山救护大队应由不少于2个中队组成，是本矿区的救护指挥中心和演习训练、培训中心。\r\n矿山救护中队应由不少于3个救护小队组成。救护中队每天应有2个小队分别值班、待机。\r\n救护小队应由不少于9人组成。\r\n煤矿企业可根据需要建立辅助救护队，业务上受矿山救护队指导。\r\n',2,'admin','2012-09-10 23:36:32','2012-11-14 20:31:15','admin'),(509,'第五百条',69,'0001-0001-0002-00010-0001-0009',0,1,1,'矿井发生重、特大事故时，应设立医疗站，对遇险人员进行急救，并做好记录。',1,'admin','2012-09-10 23:36:40','2012-11-14 20:31:25','admin'),(510,'第五百零一条',172,'0001-0001-0002-00010-0002-0001',0,1,1,'矿山救护队大、中队长应由熟悉矿山救护业务，具有相应的煤矿专业知识，从事煤矿生产、安全、技术管理工作5年以上和矿山救护工作3年以上的人员担任。',4,'admin','2012-09-10 23:36:49','2012-11-14 20:32:11','admin'),(511,'第五百零二条',172,'0001-0001-0002-00010-0002-0002',0,1,1,'矿山救护大队指挥员年龄不应超过55岁，矿山救护中队指挥员不应超过45岁，救护队员不应超过40岁，其中35岁以下队员应保持在2/3以上。指战员每年应进行1次身体检查。对身体不合格或超龄人员应及时调整。',3,'admin','2012-09-10 23:36:56','2012-11-14 20:32:23','admin'),(512,'第五百零三条',172,'0001-0001-0002-00010-0002-0003',0,1,1,'新招收的矿山救护队员，应具有初中以上文化程度，年龄在25周岁以下，从事井下工作1年以上。\r\n新矿山救护队员必须经过3个月的基础培训，再经过3个月的编队实习，并综合考评合格后，才能成为正式矿山救护队员。\r\n新招收的辅助救护队员必须经过45天的救护知识基础培训，经考试合格后，才能成为正式辅助救护队员。\r\n',2,'admin','2012-09-10 23:37:01','2012-11-14 20:32:34','admin'),(513,'第五百零四条',172,'0001-0001-0002-00010-0002-0004',0,1,1,'矿山救护队员、辅助救护队员，每年必须接受2周的再培训和知识更新教育。　',1,'admin','2012-09-10 23:37:06','2012-11-14 20:32:42','admin'),(514,'第五百零五条',173,'0001-0001-0002-00010-0003-0001',0,1,1,'',4,'admin','2012-09-10 23:37:19','2012-09-10 23:37:47','admin'),(515,'第五百零六条',173,'0001-0001-0002-00010-0003-0002',0,1,1,'矿山救护队技术装备必须专人管理，定期检查维护；矿山救护车必须专车专用，并定期保养维护，保持战备状态。\r\n氧气呼吸器连续3个月没有使用的，清净罐内的二氧化碳吸收剂必须更换。矿山救护队所使用的氢氧化钙及氧气，必须按规定进行化验。氧气纯度不得低于98%，其他要求应符合医用氧气的标准。二氧化碳吸收剂必须每季度化验1次，确保二氧化碳吸收率不低于30%，二氧化碳含量不大于4%，水分保持在15%～21%之间。严禁使用不符合标准的氢氧化钙和氧气。氧气瓶必须符合国家压力容器规定标准，每3年必须进行除锈清洗、水压试验，达不到标准的不得使用。\r\n',4,'admin','2012-09-10 23:37:29','2012-11-14 20:33:09','admin'),(516,'第五百零七条',173,'0001-0001-0002-00010-0003-0003',0,1,1,'高倍数泡沫灭火机、惰性气体发生装置等矿山救护大型设备，应每季检查、演习1次。',3,'admin','2012-09-10 23:37:35','2012-11-14 20:33:21','admin'),(517,'第五百零八条',173,'0001-0001-0002-00010-0003-0004',0,1,1,'氧气充填泵在20MPa压力情况下不得漏油、漏气、漏水。充填室内储存的大氧气瓶不得少于5个，其压力在10MPa以上，空氧气瓶和充满的氧气瓶应分别存放。\r\n新购进或经水压试验后的氧气瓶，必须进行2次充、放氧气后，方可使用。\r\n',2,'admin','2012-09-10 23:37:42','2012-11-14 20:33:33','admin'),(518,'第五百零九条',173,'0001-0001-0002-00010-0003-0005',0,1,1,'矿山救护队应具有分析化验矿井灾区空气成分的能力。\r\n矿山救护队应自备矿灯充电室，并指定专人管理，保证救灾用灯。\r\n',1,'admin','2012-09-10 23:37:47','2012-11-14 20:33:44','admin'),(519,'第五百一十条',174,'0001-0001-0002-00010-0004-0001',0,1,1,'矿井发生重大事故后，必须立即成立抢救指挥部并设立地面基地。矿山救护队队长为抢救指挥部成员。',4,'admin','2012-09-10 23:37:56','2012-11-14 20:34:05','admin'),(520,'第五百一十一条',174,'0001-0001-0002-00010-0004-0002',0,1,1,'矿山救护队队长对矿山救护队抢救工作具体负责。如与其他矿山救护队联合作战时，应成立矿山救护联合作战部，由服务于发生事故的煤矿企业的矿山救护队队长担任作战部指挥，协调各矿山救护队战斗行动。',3,'admin','2012-09-10 23:38:01','2012-11-14 20:34:15','admin'),(521,'第五百一十二条',174,'0001-0001-0002-00010-0004-0003',0,1,1,'矿井发生火、瓦斯、煤尘等重大事故后，必须首先组织矿山救护队进行侦察，探明灾区情况。抢救指挥部应根据灾害性质、发生地点、波及范围、人员分布、救灾的人力和物力，制定抢救方案。',2,'admin','2012-09-10 23:38:07','2012-11-14 20:34:35','admin'),(522,'第五百一十三条',174,'0001-0001-0002-00010-0004-0004',0,1,1,'为保证重大事故井下抢救工作的顺利进行，应在靠近灾区的安全地点设立井下基地。井下基地指挥由指挥部选派具有救护知识的人员担任。\r\n井下基地应有矿山救护队指挥员、待机小队和急救员值班，并设有通往指挥部和灾区的电话，备有必要的救护装备和器材。\r\n井下基地电话应专人看守并做好记录，井下基地指挥员应经常与抢救指挥部、灾区工作的救护小队保持联系。\r\n井下救灾过程中应由专人检测风流和有害气体浓度；灾情突然发生变化时，井下基地指挥员应采取应急措施，并及时向指挥部报告。\r\n',1,'admin','2012-09-10 23:38:14','2012-11-14 20:34:45','admin'),(523,'第五百一十四条',175,'0001-0001-0002-00010-0005-0001',0,1,1,'矿山救护队值班员必须24h值班。\r\n值班员接听事故电话时，应在问清和记录事故地点、时间、类别、遇险遇难人员数量、通知人姓名及单位后，立即发出警报，并向指挥员报告。\r\n听到警报后，矿山救护队必须在1min内出动；待机小队立即转入值班。发生的事故为火灾、瓦斯或煤尘爆炸及煤（岩）与瓦斯（二氧化碳）突出事故时，待机小队应与值班小队一起出发。\r\n',20,'admin','2012-09-10 23:38:23','2012-11-14 20:35:06','admin'),(524,'第五百一十五条',175,'0001-0001-0002-00010-0005-0002',0,1,1,'矿山救护队到达事故矿井后，指挥员应立即赶到抢救指挥部；救护队员必须按事故类别整理携带装备，做好抢救准备。\r\n指挥员接受任务后，必须立即向救护小队下达任务，并说明事故情况、救灾重点、行动计划、行动路线、安全措施和注意事项。\r\n严禁使用混合小队。',19,'admin','2012-09-10 23:38:30','2012-11-14 20:35:19','admin'),(525,'第五百一十六条',175,'0001-0001-0002-00010-0005-0003',0,1,1,'进入灾区的救护小队队员不得少于6人，进入前必须检查氧气呼吸器，氧气压力不得低于18MPa，并按规定佩戴和使用。\r\n救护小队必须携带1台全面罩氧气呼吸器和不低于18MPa压力的2个备用氧气瓶，以及氧气呼吸器工具和备件袋。\r\n如不能确认井筒和井底车场无有害气体，救护队员必须在地面将氧气呼吸器佩戴好。\r\n',18,'admin','2012-09-10 23:38:37','2012-11-14 20:35:33','admin'),(526,'第五百一十七条',175,'0001-0001-0002-00010-0005-0004',0,1,1,'在窒息区或有中毒危险区工作时，救护小队长应使队员保持在彼此能看到或听到信号的范围内；任何情况下严禁指战员单独行动，严禁通过口具讲话或摘掉口具讲话；救护小队长应经常观察队员呼吸器的氧气压力，并根据氧气压力最低的1名队员确定整个小队的返回时间。',17,'admin','2012-09-10 23:38:42','2012-11-14 20:35:45','admin'),(527,'第五百一十八条',175,'0001-0001-0002-00010-0005-0005',0,1,1,'进入灾区从事救护工作时，任何情况下氧气呼吸器都必须保留不低于5MPa的压力；发现有队员身体不适或氧气呼吸器发生故障难以排除时，全小队必须立即撤出。',16,'admin','2012-09-10 23:38:48','2012-11-14 20:35:55','admin'),(528,'第五百一十九条',175,'0001-0001-0002-00010-0005-0006',0,1,1,'矿山救护队员在灾区工作1个呼吸器班后，应至少休息8h，才能重新佩戴氧气呼吸器工作。',15,'admin','2012-09-10 23:38:53','2012-11-14 20:36:05','admin'),(529,'第五百二十条',175,'0001-0001-0002-00010-0005-0007',0,1,1,'矿山救护队指挥员应亲自组织和参加侦察工作。在布置侦察任务时，必须向所有队员说明所了解的各种情况，应做到侦察任务清楚、行进路线明确，小队的行进方向、时间应标在图纸上。',14,'admin','2012-09-10 23:38:58','2012-11-14 20:36:15','admin'),(530,'第五百二十一条',175,'0001-0001-0002-00010-0005-0008',0,1,1,'',12,'admin','2012-09-10 23:39:04','2012-09-10 23:40:11','admin'),(531,'第五百二十二条',175,'0001-0001-0002-00010-0005-0009',0,1,1,'处理矿井火灾事故时应遵循下列原则：\r\n（一）控制烟雾的蔓延，不致危及井下人员安全。\r\n（二）防止火灾扩大。\r\n（三）防止引起瓦斯、煤尘爆炸，防止火风压引起风流逆转而造成危害。\r\n（四）保证救灾人员的安全，并有利于抢救遇险人员。\r\n（五）创造有利的灭火条件。\r\n',12,'admin','2012-09-10 23:39:11','2012-11-14 20:36:35','admin'),(532,'第五百二十三条',175,'0001-0001-0002-00010-0005-00010',0,1,1,'进风井口、井筒、井底车场、主要进风巷和硐室发生火灾时，为抢救井下工作人员，应进行全矿井反风。指挥部下达反风命令前，必须将火源进风侧的人员撤出，并采取阻止火灾蔓延的措施。\r\n采取风流短路措施时，必须将受影响区域内的人员全部撤出。\r\n多台主要通风机联合通风的矿井反风时，要保证非事故区域的主要通风机先反风，事故区域的主要通风机后反风。\r\n',11,'admin','2012-09-10 23:39:18','2012-11-14 20:36:46','admin'),(533,'第五百二十四条',175,'0001-0001-0002-00010-0005-0011',0,1,1,'处理火灾事故过程中，必须指定专人检查瓦斯和煤尘，观测灾区气体和风流变化。当瓦斯浓度达到2.0%以上、并继续增加有爆炸危险时，矿山救护队必须将全部人员立即撤到安全地点，然后采取措施，排除爆炸危险。\r\n灭火工作必须从火源进风侧进行。用水灭火时，水流应从火源外围喷射，逐步逼向火源的中心；必须有充足的风量和畅通的回风巷，防止水煤气爆炸。\r\n',10,'admin','2012-09-10 23:39:22','2012-11-14 20:36:58','admin'),(534,'第五百二十五条',175,'0001-0001-0002-00010-0005-0012',0,1,1,'扑灭上、下山巷道火灾时，必须采取防止火风压造成风流逆转的措施。',9,'admin','2012-09-10 23:39:30','2012-11-14 20:37:09','admin'),(535,'第五百二十六条',175,'0001-0001-0002-00010-0005-0013',0,1,1,'扑灭硐室火灾时，应遵守下列规定：\r\n（一）爆炸材料库着火时，应首先将雷管运出，然后将其他爆炸材料运出；因高温运不出时，应关闭防火门，退至安全地点。\r\n（二）绞车房着火时，应将火源下方的矿车固定，防止烧断钢丝绳造成跑车伤人。\r\n（三）蓄电池电机车库着火时，必须切断电源，采取措施，防止氢气爆炸。\r\n',8,'admin','2012-09-10 23:39:34','2012-11-14 20:37:19','admin'),(536,'第五百二十七条',175,'0001-0001-0002-00010-0005-0014',0,1,1,'矿山救护队处理掘进工作面火灾时，应保持原有的通风状态，进行侦察后再采取措施。',7,'admin','2012-09-10 23:39:40','2012-11-14 20:37:28','admin'),(537,'第五百二十八条',175,'0001-0001-0002-00010-0005-0015',0,1,1,'采用隔绝法封闭火区救灾时，应遵循下列原则：\r\n（一）在保证安全的情况下，尽量缩小封闭范围。\r\n（二）有瓦斯、煤尘爆炸危险时，应设置防爆墙，在防爆墙的掩护下建立永久密闭墙。\r\n（三）需要封闭多条巷道时，应根据具体情况确定封闭顺序。\r\n（四）火区临时封闭后，人员应立即撤出危险区。进入检查或加固密闭墙时，要在确保安全的情况下进行。\r\n（五）密闭的火区中发生爆炸密闭墙被破坏时，严禁派救护队恢复密闭墙或探险，应在较远的安全地点重新建造密闭。\r\n',6,'admin','2012-09-10 23:39:45','2012-11-14 20:37:38','admin'),(538,'第五百二十九条',175,'0001-0001-0002-00010-0005-0016',0,1,1,'矿山救护队在高温区进行救护工作时，救护队员进入高温区的时间不得超过表15规定。',5,'admin','2012-09-10 23:39:51','2012-11-14 20:37:53','admin'),(539,'第五百三十条',175,'0001-0001-0002-00010-0005-0017',0,1,1,'处理爆炸事故时，救护小队进入灾区必须遵守下列规定：\r\n表15  救护队员进入高温灾区的最长时间值\r\n温度/℃	40	45	50	55	60\r\n进入时间/min	25	20	15	10	5\r\n一）进入前切断灾区电源。\r\n（二）检查灾区内各种有害气体的浓度、温度及通风设施破坏情况，发现有再次爆炸危险时，必须立即撤到安全地点。\r\n（三）穿过支架被破坏的巷道时，要架好临时支架。\r\n（四）通过支护不好的地点时，救护队员要保持一定的距离按顺序通过。\r\n（五）进入灾区行动要谨慎，防止碰撞产生火花，引起爆炸。\r\n（六）确知人员已经牺牲时，必须先恢复灾区通风，再进行处理。\r\n',4,'admin','2012-09-10 23:39:56','2012-11-14 20:38:06','admin'),(540,'第五百三十一条',175,'0001-0001-0002-00010-0005-0018',0,1,1,'发生煤（岩）与瓦斯突出事故，不得停风和反风，防止风流紊乱扩大灾情。如通风系统及设施被破坏，应设置风障、临时风门及安装局部通风机恢复通风。\r\n恢复突出区通风时，应以最短的路线将瓦斯引入回风巷。回风井口50m范围内不得有火源，并设专人监视。\r\n是否停电应根据井下实际情况决定。\r\n',3,'admin','2012-09-10 23:40:01','2012-11-14 20:38:16','admin'),(541,'第五百三十二条',175,'0001-0001-0002-00010-0005-0019',0,1,1,'处理煤（岩）与二氧化碳突出事故时，除执行本规程第五百三十一条外，还必须加大灾区风量，迅速抢救遇险人员。矿山救护队进入灾区时要戴好防护眼镜。',2,'admin','2012-09-10 23:40:06','2012-11-14 20:38:25','admin'),(542,'第五百三十三条',175,'0001-0001-0002-00010-0005-0020',0,1,1,'处理水灾事故时，矿山救护队到达事故矿井后，要了解灾区情况、水源、事故前人员分布、矿井具有生存条件的地点及其进入的通道等，并根据被堵人员所在地点的空间、氧气、瓦斯浓度以及救出被困人员所需的大致时间制定相应救灾方案。',1,'admin','2012-09-10 23:40:11','2012-11-14 20:38:34','admin'),(543,'第五百四十八条',123,'0001-0001-0003-0002-0001-0001',0,1,1,'挖掘机采装的台阶高度应符合下列规定：\r\n（一）不需爆破的岩土台阶高度不得大于最大挖掘高度。\r\n（二）需爆破的坚硬煤、岩台阶，爆破后爆堆高度不得大于最大挖掘高度的1.1～1.2倍，台阶顶部不得有悬浮大块。\r\n（三）上装车台阶高度不得大于最大卸载高度与运输容器高度及卸载安全高度之和的差。\r\n',3,'admin','2012-09-10 23:42:34','2012-11-14 20:39:16','admin'),(544,'第五百四十九条',123,'0001-0001-0003-0002-0001-0002',0,1,1,'露天采场最终边坡的台阶坡面角和边坡角，必须符合最终边坡设计要求。',2,'admin','2012-09-10 23:42:42','2012-11-14 20:39:25','admin'),(545,'第五百五十条',123,'0001-0001-0003-0002-0001-0003',0,1,1,'最小工作平盘宽度，必须保证采掘、运输设备的安全运行和供电线路、电信线路、供水管路、排水沟等的正常布置。',1,'admin','2012-09-10 23:42:47','2012-11-14 20:39:32','admin'),(546,'第五百五十一条',124,'0001-0001-0003-0002-0002-0001',0,1,1,'穿孔作业必须按穿孔参数设计要求进行，并配备除尘设施。',4,'admin','2012-09-10 23:42:58','2012-11-14 20:39:42','admin'),(547,'第五百五十二条',124,'0001-0001-0003-0002-0002-0002',0,1,1,'穿孔机在有老空的工作面穿孔时，必须制定安全措施，并在专业人员指挥下进行。',3,'admin','2012-09-10 23:43:03','2012-11-14 20:39:52','admin'),(548,'第五百五十三条',124,'0001-0001-0003-0002-0002-0003',0,1,1,'穿孔机进行穿孔作业和走行时，履带边缘与坡顶线的距离不得小于表16规定。\r\n表16  穿孔机作业和走行安全距离\r\n台阶高度/m	＜4	4～10	＞10\r\n安全距离/m	1～2	2～2.5	2.5～3.5\r\n穿凿边行孔时，穿孔机应垂直于台阶坡顶线或调角布置（最小夹角不小于45°）。在有顺层滑坡危险区，必须压碴穿孔。\r\n',2,'admin','2012-09-10 23:43:08','2012-11-14 20:40:06','admin'),(549,'第五百五十四条',124,'0001-0001-0003-0002-0002-0004',0,1,1,'穿孔机在高压线下通过时，钻架顶端与高压线的最小垂直距离不得小于1m；在作业和行走时，钻架与高压线的水平距离不得小于2m。走行时必须保证安全，必要时事先落好钻架。',1,'admin','2012-09-10 23:43:13','2012-11-14 20:40:14','admin'),(550,'第五百五十五条',125,'0001-0001-0003-0002-0003-0001',0,1,1,'',19,'admin','2012-09-10 23:43:27','2012-09-10 23:45:41','admin'),(551,'第五百五十六条',125,'0001-0001-0003-0002-0003-0002',0,1,1,'',18,'admin','2012-09-10 23:43:32','2012-09-10 23:45:41','admin'),(552,'第五百五十七条',125,'0001-0001-0003-0002-0003-0003',0,1,1,'',17,'admin','2012-09-10 23:43:37','2012-09-10 23:45:41','admin'),(553,'第五百五十八条',125,'0001-0001-0003-0002-0003-0004',0,1,1,'',16,'admin','2012-09-10 23:43:43','2012-09-10 23:45:41','admin'),(554,'第五百五十九条',125,'0001-0001-0003-0002-0003-0005',0,1,1,'',15,'admin','2012-09-10 23:44:06','2012-09-10 23:45:41','admin'),(555,'第五百六十条',125,'0001-0001-0003-0002-0003-0006',0,1,1,'',14,'admin','2012-09-10 23:44:15','2012-09-10 23:45:41','admin'),(556,'第五百六十一条',125,'0001-0001-0003-0002-0003-0007',0,1,1,'',13,'admin','2012-09-10 23:44:21','2012-09-10 23:45:41','admin'),(557,'第五百六十二条',125,'0001-0001-0003-0002-0003-0008',0,1,1,'',12,'admin','2012-09-10 23:44:27','2012-09-10 23:45:41','admin'),(558,'第五百六十三条',125,'0001-0001-0003-0002-0003-0009',0,1,1,'',11,'admin','2012-09-10 23:44:32','2012-09-10 23:45:41','admin'),(559,'第五百六十四条',125,'0001-0001-0003-0002-0003-00010',0,1,1,'',10,'admin','2012-09-10 23:44:37','2012-09-10 23:45:41','admin'),(560,'第五百六十五条',125,'0001-0001-0003-0002-0003-0011',0,1,1,'',9,'admin','2012-09-10 23:44:42','2012-09-10 23:45:41','admin'),(561,'第五百六十六条',125,'0001-0001-0003-0002-0003-0012',0,1,1,'',8,'admin','2012-09-10 23:44:48','2012-09-10 23:45:41','admin'),(562,'第五百六十七条',125,'0001-0001-0003-0002-0003-0013',0,1,1,'',7,'admin','2012-09-10 23:44:54','2012-09-10 23:45:41','admin'),(563,'第五百六十八条',125,'0001-0001-0003-0002-0003-0014',0,1,1,'',6,'admin','2012-09-10 23:45:00','2012-09-10 23:45:41','admin'),(564,'第五百六十九条',125,'0001-0001-0003-0002-0003-0015',0,1,1,'',5,'admin','2012-09-10 23:45:13','2012-09-10 23:45:41','admin'),(565,'第五百七十条',125,'0001-0001-0003-0002-0003-0016',0,1,1,'',4,'admin','2012-09-10 23:45:19','2012-09-10 23:45:41','admin'),(566,'第五百七十一条',125,'0001-0001-0003-0002-0003-0017',0,1,1,'',3,'admin','2012-09-10 23:45:24','2012-09-10 23:45:41','admin'),(567,'第五百七十二条',125,'0001-0001-0003-0002-0003-0018',0,1,1,'',2,'admin','2012-09-10 23:45:29','2012-09-10 23:45:41','admin'),(568,'第五百七十三条',125,'0001-0001-0003-0002-0003-0019',0,1,1,'',1,'admin','2012-09-10 23:45:34','2012-09-10 23:45:41','admin'),(569,'第五百七十四条',125,'0001-0001-0003-0002-0003-0020',0,1,1,'',0,'admin','2012-09-10 23:45:41',NULL,NULL),(570,'第五百七十五条',126,'0001-0001-0003-0002-0004-0001',0,1,1,'',12,'admin','2012-09-10 23:46:02','2012-09-10 23:47:12','admin'),(571,'第五百七十六条',126,'0001-0001-0003-0002-0004-0002',0,1,1,'',11,'admin','2012-09-10 23:46:08','2012-09-10 23:47:12','admin'),(572,'第五百七十七条',126,'0001-0001-0003-0002-0004-0003',0,1,1,'',10,'admin','2012-09-10 23:46:14','2012-09-10 23:47:12','admin'),(573,'第五百七十八条',126,'0001-0001-0003-0002-0004-0004',0,1,1,'',9,'admin','2012-09-10 23:46:19','2012