insert  into `t_system_company`(`id`,`company_name`,`folder`,`email`,`phone_number`,`status`,`version`,`create_by`,`create_time`,`last_modify_by`,`last_modify_time`) values (0,'默认','default','darkmi@126.com','13811215910','NORMAL',0,'admin','2012-04-25 16:42:29',NULL,NULL),(1,'北京矿业集团','beijingkuangyejituan','22@126.com','13811215910','NORMAL',0,'admin','2012-04-25 11:29:45',NULL,NULL),(2,'河北矿业集团','hebeikuangyejituan','11@126.com','13811215910',NULL,0,'admin','2012-04-26 19:23:56',NULL,NULL);

/*Table structure for table `t_system_config` */

DROP TABLE IF EXISTS `t_system_config`;

CREATE TABLE `t_system_config` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `name` varchar(50) DEFAULT NULL,
