insert  into `t_specification_chapter`(`id`,`name`,`parent_id`,`tree_index`,`sort`,`is_leaf`,`state`,`content`,`version`,`create_by`,`create_time`,`last_modify_time`,`last_modify_by`) values (1,'全部分类',NULL,'0001',0,0,NULL,'',0,'admin','2012-06-13 17:09:06','2012-06-13 17:09:50','admin'),(2,'煤矿安全规程',1,'0001-0001',0,0,NULL,'',5,'admin','2012-06-13 17:09:06','2012-06-13 17:09:50','admin'),(3,'煤矿安全技术操作规程',1,'0001-0002',0,0,NULL,'',0,'admin','2012-06-13 17:09:06','2012-06-13 17:09:50','admin'),(4,'第一编 总则',2,'0001-0001-0001',0,0,NULL,'',14,'admin','2012-06-13 17:09:06','2012-08-10 16:20:43','系统'),(5,'第二编 井工部分',2,'0001-0001-0002',0,0,NULL,'',10,'admin','2012-06-13 17:09:06','2012-08-10 16:20:43','系统'),(6,'第三编 露天部分',2,'0001-0001-0003',0,0,NULL,'',10,'admin','2012-06-13 17:09:06','2012-08-10 16:20:43','系统'),(7,'第四编 职业危害',2,'0001-0001-0004',0,0,NULL,'',10,'admin','2012-06-13 17:09:06','2012-08-10 16:20:43','系统'),(8,'（2006）',2,'0001-0001-0005',0,1,NULL,'',10,'admin','2012-06-13 17:09:06','2012-08-10 16:20:43','系统'),(9,'（2009）',2,'0001-0001-0006',0,1,NULL,'',10,'admin','2012-06-13 17:09:06','2012-08-10 16:20:43','系统'),(10,'（2010）',2,'0001-0001-0007',0,1,NULL,'',10,'admin','2012-06-13 17:09:06','2012-08-10 16:20:43','系统'),(11,'第一条',4,'0001-0001-0001-0001',0,1,NULL,'为保障煤矿安全生产和职工人身安全，防止煤矿事故，根据《煤炭法》、《矿山安全法》和《煤矿安全监察条例》，制定本规程。',9,'admin','2012-06-13 17:09:06','2012-08-10 16:28:29','系统'),(12,'第二条',4,'0001-0001-0001-0002',0,1,NULL,'在中华人民共和国领域从事煤炭生产和煤矿建设活动，必须遵守本规程。',8,'admin','2012-06-13 17:09:06','2012-08-10 16:28:29','系统'),(13,'第三条',4,'0001-0001-0001-0003',0,1,NULL,'煤矿企业必须遵守国家有关安全生产的法律、法规、规章、规程、标准和技术规范。\r\n煤矿企业必须建立、健全各级领导安全生产责任制、职能机构安全生产责任制、岗位人员安全生产责任制。\r\n煤矿企业应建立、健全安全目标管理制度、安全奖惩制度、安全技术措施审批制度、安全隐患排查制度、安全检查制度、安全办公会议等制度。\r\n煤矿企业必须建立各种设备、设施检查维修制度，定期进行检查维修，并做好记录。\r\n',8,'admin','2012-06-13 17:09:06','2012-08-10 16:28:29','系统'),(14,'第四条',4,'0001-0001-0001-0004',0,1,NULL,'煤矿企业必须设置安全生产机构，配备适应工作需要的安全生产人员和装备。',8,'admin','2012-06-13 17:09:06','2012-08-10 16:28:29','系统'),(15,'第五条',4,'0001-0001-0001-0005',0,1,NULL,'煤矿安全工作必须实行群众监督。煤矿企业必须支持群众安全监督组织的活动，发挥职工群众安全监督作用。\r\n职工有权制止违章作业，拒绝违章指挥；当工作地点出现险情时，有权立即停止作业，撤到安全地点；当险情没有得到处理不能保证人身安全时，有权拒绝作业。\r\n',8,'admin','2012-06-13 17:09:06','2012-08-10 16:28:29','系统'),(16,'第六条',4,'0001-0001-0001-0006',0,1,NULL,'煤矿企业必须对职工进行安全培训。未经安全培训的，不得上岗作业。\r\n矿务局（公司）局长（经理）、矿长必须具备安全专业知识，具有领导安全生产和处理煤矿事故的能力，并经依法培训合格，取得安全任职资格证书。\r\n特种作业人员必须按国家有关规定培训合格，取得操作资格证书。',8,'admin','2012-06-13 17:09:06','2012-08-10 16:28:29','系统'),(17,'第七条',4,'0001-0001-0001-0007',0,1,NULL,'煤矿使用的涉及安全生产的产品，必须取得煤矿矿用产品安全标志。未取得煤矿矿用产品安全标志的，不得使用。\r\n试验涉及安全生产的新技术、新工艺、新设备、新材料前，必须经过论证、安全性能检验和鉴定，并制定安全措施。\r\n',8,'admin','2012-06-13 17:09:06','2012-08-10 16:28:29','系统'),(18,'第八条',4,'0001-0001-0001-0008',0,1,NULL,'煤矿企业在编制生产建设长远发展规划和年度生产建设计划时，必须编制安全技术发展规划和安全技术措施计划。安全技术措施所需费用、材料和设备等必须列入企业财务、供应计划。',8,'admin','2012-06-13 17:09:06','2012-08-10 16:28:29','系统'),(19,'第九条',4,'0001-0001-0001-0009',0,1,NULL,'煤矿企业必须编制年度灾害预防和处理计划，并根据具体情况及时修改。灾害预防和处理计划由矿长负责组织实施。\r\n煤矿企业每年必须至少组织1次矿井救灾演习。',8,'admin','2012-06-13 17:09:06','2012-08-10 16:28:29','系统'),(20,'第十条',4,'0001-0001-0001-0010',0,1,NULL,'入井人员必须戴安全帽、随身携带自救器和矿灯，严禁携带烟草和点火物品，严禁穿化纤衣服，入井前严禁喝酒。\r\n煤矿企业必须建立入井检身制度和出入井人员清点制度。',8,'admin','2012-06-13 17:09:06','2012-08-10 16:28:29','系统'),(21,'第十一条',4,'0001-0001-0001-0011',0,1,NULL,'煤矿企业应有创伤急救系统为其服务。创伤急救系统应配备救护车辆、急救器材、急救装备和药品等。',8,'admin','2012-06-13 17:09:06','2012-08-10 16:28:29','系统'),(22,'第十二条',4,'0001-0001-0001-0012',0,1,NULL,'井工煤矿必须及时填绘反映实际情况的下列图纸：\r\n（一）矿井地质和水文地质图。\r\n（二）井上、下对照图。\r\n（三）巷道布置图。\r\n（四）采掘工程平面图。\r\n（五）通风系统图。\r\n（六）井下运输系统图。\r\n（七）安全监测装备布置图。\r\n（八）排水、防尘、防火注浆、压风、充填、抽放瓦斯等管路系统图。\r\n（九）井下通信系统图。\r\n（十）井上、下配电系统图和井下电气设备布置图。\r\n（十一）井下避灾路线图。',8,'admin','2012-06-13 17:09:06','2012-08-10 16:28:29','系统'),(23,'第十三条',4,'0001-0001-0001-0013',0,1,NULL,'露天煤矿必须及时填绘反映实际情况的下列图纸：\r\n（一）地形地质图。\r\n（二）工程地质平面图、断面图，综合水文地质平面图。\r\n（三）采剥工程平面图、断面图。\r\n（四）排土工程平面图。\r\n（五）运输系统图。\r\n（六）输配电系统图。\r\n（七）通信系统图。\r\n（八）防排水系统及排水设备布置图。\r\n（九）边坡监测系统平面图、断面图。\r\n（十）井工老空与露天矿平面对照图。',8,'admin','2012-06-13 17:09:06','2012-08-10 16:28:29','系统'),(24,'第十四条',4,'0001-0001-0001-0014',0,1,NULL,'煤矿发生事故后，煤矿企业主要负责人和技术负责人必须立即采取措施组织抢救，矿长负责抢救指挥，并按有关规定及时上报。',8,'admin','2012-06-13 17:09:06','2012-08-10 16:28:29','系统'),(25,'第一章',5,'0001-0001-0002-0001',0,0,NULL,NULL,9,'admin','2012-06-13 17:09:06','2012-09-01 17:34:26','admin'),(26,'第一节',25,'0001-0001-0002-0001-0001',0,0,NULL,NULL,6,'admin','2012-06-13 17:09:06','2012-09-01 17:36:54','admin'),(27,'第十五条',26,'0001-0001-0002-0001-0001-0001',0,1,NULL,'单项工程、单位工程开工前，必须编制施工组织设计和作业规程，并组织每个工作人员学习。',0,'admin','2012-06-13 17:09:06','2012-06-13 17:09:50','admin'),(28,'第十六条',26,'0001-0001-0002-0001-0001-0002',0,1,NULL,'开凿平硐、斜井和立井时，自井口到坚硬岩层之间的井巷必须砌碹，并向坚硬岩层内至少延深5m。\r\n在山坡下开凿斜井和平硐时，井口顶、侧必须构筑挡墙和防洪水沟。',0,'admin','2012-06-13 17:09:06','2012-06-13 17:09:50','admin'),(29,'第十七条',26,'0001-0001-0002-0001-0001-0001',0,1,NULL,'掘进井巷和硐室时，必须采取湿式钻眼、冲洗井壁巷帮、水炮泥、爆破喷雾、装岩（煤）洒水和净化风流等综合防尘措施。\r\n冻结法凿井和在遇水膨胀的岩层中掘进不能采用湿式钻眼时，可采用干式钻眼，但必须采取捕尘措施，并使用个体防尘保护用品。',0,'admin','2012-06-13 17:09:06','2012-06-13 17:09:50','admin'),(30,'第十八条',26,'0001-0001-0002-0001-0001-0001',0,1,NULL,'每个生产矿井必须至少有2个能行人的通达地面的安全出口，各个出口间的距离不得小于30m。\r\n采用中央式通风系统的新建和改扩建矿井，设计中应规定井田边界附近的安全出口。当井田一翼走向较长、矿井发生灾害不能保证人员安全撤出时，必须掘出井田边界附近的安全出口。\r\n井下每一个水平到上一个水平和各个采区都必须至少有2个便于行人的安全出口，并与通达地面的安全出口相连接。未建成2个安全出口的水平或采区严禁生产。\r\n井巷交岔点，必须设置路标，标明所在地点，指明通往安全出口的方向。井下工作人员必须熟悉通往安全出口的路线。\r\n',0,'admin','2012-06-13 17:09:06','2012-06-13 17:09:50','admin'),(31,'第十九条',26,'0001-0001-0002-0001-0001-0001',0,1,NULL,'对于通达地面的安全出口和2个水平之间的安全出口，倾角等于或小于45°时，必须设置人行道，并根据倾角大小和实际需要设置扶手、台阶或梯道。倾角大于45°时，必须设置梯道间或梯子间，斜井梯道间必须分段错开设置，每段斜长不得大于10m；立井梯子间中的梯子角度不得大于80°，相邻2个平台的垂直距离不得大于8m。\r\n安全出口应经常清理、维护，保持畅通。',0,'admin','2012-06-13 17:09:06','2012-06-13 17:09:50','admin'),(32,'第二十条',26,'0001-0001-0002-0001-0001-0001',0,1,NULL,'主要绞车道不得兼作人行道。提升量不大，保证行车时不行人的，不受此限。',0,'admin','2012-06-13 17:09:06','2012-06-13 17:09:50','admin'),(33,'第二十一条',26,'0001-0001-0002-0001-0001-0001',0,1,NULL,'巷道净断面必须满足行人、运输、通风和安全设施及设备安装、检修、施工的需要，并符合下列要求：\r\n（一）主要运输巷和主要风巷的净高，自轨面起不得低于2m。架线电机车运输巷的净高必须符合本规程第三百五十六条和第三百五十七条的有关要求。\r\n（二）采区（包括盘区，以下各条同）内的上山、下山和平巷的净高不得低于2m，薄煤层内的不得低于1.8m。\r\n采煤工作面运输巷、回风巷及采区内的溜煤眼等的净断面或净高，由煤矿企业统一规定。\r\n巷道净断面的设计，必须按支护最大允许变形后的断面计算。\r\n',0,'admin','2012-06-13 17:09:06','2012-06-13 17:09:50','admin'),(34,'第二十二条',26,'0001-0001-0002-0001-0001-0001',0,1,NULL,'运输巷两侧（包括管、线、电缆）与运输设备最突出部分之间的距离，应符合下列要求：\r\n（一）新建矿井、生产矿井新掘运输巷的一侧，从巷道道碴面起1.6m的高度内，必须留有宽0.8m（综合机械化采煤矿井为1m）以上的人行道，管道吊挂高度不得低于1.8m；巷道另一侧的宽度不得小于0.3m（综合机械化采煤矿井为0.5m）。巷道内安设输送机时，输送机与巷帮支护的距离不得小于0.5m；输送机机头和机尾处与巷帮支护的距离应满足设备检查和维修的需要，并不得小于0.7m。巷道内移动变电站或平板车上综采设备的最突出部分，与巷帮支护的距离不得小于0.3m。\r\n（二）生产矿井已有巷道人行道的宽度不符合本条第一款第（一）项的要求时，必须在巷道的一侧设置躲避硐，2个躲避硐之间的距离不得超过40m。躲避硐宽度不得小于1.2m，深度不得小于0.7m，高度不得小于1.8m，躲避硐内严禁堆积物料。\r\n（三）在人车停车地点的巷道上下人侧，从巷道道碴面起1.6m的高度内，必须留有宽1m以上的人行道，管道吊挂高度不得低于1.8m。\r\n',0,'admin','2012-06-13 17:09:06','2012-06-13 17:09:50','admin'),(35,'第二十三条',26,'0001-0001-0002-0001-0001-0001',0,1,NULL,'在双轨运输巷中，2列列车最突出部分之间的距离，对开时不得小于0.2m，采区装载点不得小于0.7m，矿车摘挂钩地点不得小于1m。车辆最突出部分与巷道两侧距离，必须符合本规程第二十二条的要求。\r\n',0,'admin','2012-06-13 17:09:06','2012-06-13 17:09:50','admin'),(36,'第二十四条',26,'0001-0001-0002-0001-0001-0001',0,1,NULL,'采区结束回撤设备时，必须编制专门措施，加强通风、瓦斯、防火管理。',0,'admin','2012-06-13 17:09:06','2012-06-13 17:09:50','admin'),(37,'第二节',25,'0001-0001-0002-0001-0002',0,0,NULL,NULL,7,'admin','2012-06-13 17:09:06','2012-09-01 17:36:54','admin'),(38,'第二十五条',37,'0001-0001-0002-0001-0002-0001',0,1,NULL,'凿井期间，井口工作范围必须用栅栏围住，人员进出地点必须安装栅栏门；井口必须设置封口盘和井盖门，井盖门的两端必须安装栅栏，封口盘和井盖门必须坚固严密，并采用不燃性材料。\r\n',4,'admin','2012-06-13 17:09:06','2012-09-01 17:38:48','admin'),(39,'第二十六条',37,'0001-0001-0002-0001-0002-0002',0,1,NULL,'采用普通凿井法施工时，立井的永久或临时支护到井筒工作面的距离及防止片帮的措施必须根据岩性、水文地质条件和施工工艺在作业规程中明确规定。\r\n',4,'admin','2012-06-13 17:09:06','2012-09-01 17:38:48','admin'),(40,'第二十七条',37,'0001-0001-0002-0001-0002-0003',0,1,NULL,'立井井筒穿过表土层、砂层、松软岩层或煤层时，必须有专门措施。采用井圈或其他临时支护时，临时支护必须安全可靠、紧靠工作面，并及时进行永久支护。在建立永久支护前，每班应派专人观测地面沉降和临时支护后面的井帮变化情况；发现危险预兆时，必须立即停止工作，撤出人员，进行处理。\r\n',4,'admin','2012-06-13 17:09:06','2012-09-01 17:38:48','admin'),(41,'第二十八条',37,'0001-0001-0002-0001-0002-0004',0,1,NULL,'立井永久支护的质量必须符合设计要求。岩帮与支护之间必须填满灌实。井壁出水时必须采取导水或堵水等措施。\r\n',4,'admin','2012-06-13 17:09:06','2012-09-01 17:38:48','admin'),(42,'第二十九条',37,'0001-0001-0002-0001-0002-0005',0,1,NULL,'采用钻井法开凿立井井筒必须遵守下列规定：\r\n（一）钻井的设计与施工最终位置必须通过风化带，并向不透水的稳定基岩至少延深5m。\r\n（二）钻井期间，采用封口平台时，必须将井口封盖严密；采用井口梁时，必须有可靠的防坠措施。\r\n（三）钻井过程中，护壁泥浆的各项参数必须定时测定，发现问题立即调整。井筒内的泥浆面，必须保持高于地下静止水位。\r\n（四）钻井时必须测定井筒的偏斜度。偏斜超过规定时，必须及时纠正。井筒偏斜度及测点的间距必须在施工组织设计中明确规定。钻井完毕后，必须绘制井筒的纵横剖面图，井筒中心线和截面必须符合设计要求。\r\n（五）预制井壁的质量，必须逐节检查鉴定。井壁连接部位必须有可靠的防蚀、防水措施，合格后方可下沉井壁。\r\n（六）井壁下沉完成后，必须检查井壁偏斜度，只有符合要求后方可进行壁后充填，壁后充填必须密实。充填材料必须经过试验，满足强度和凝固时间的要求，并保证能够置换出泥浆。开凿沉井井壁的底部或开掘马头门之前，必须检查破壁处及其上方15～30m范围内壁后的充填质量，发现不合格时，必须采取可靠的补救措施。\r\n（七）开凿沉井井壁的底部和开掘马头门采用爆破作业时，必须制定安全措施。\r\n',4,'admin','2012-06-13 17:09:06','2012-09-01 17:38:48','admin'),(43,'第三十条',37,'0001-0001-0002-0001-0002-0006',0,1,NULL,'采用冻结法开凿立井井筒应遵守下列规定：\r\n（一）冻结深度应穿过风化带延深至稳定的基岩10m以上。基岩段涌水较大时，应加深冻结深度。\r\n（二）钻进冻结孔时，必须测定钻孔的方向和偏斜度，测斜的最大间隔不得超过30m，并绘制冻结孔实际偏斜平面位置图，偏斜度超过规定时，必须及时纠正。因钻孔偏斜影响冻结效果时，必须补孔。\r\n（三）地质检查钻孔不得打在冻结的井筒内。水文观测钻孔偏斜不得超出井筒，深度不得超过冻结段下部隔水层。\r\n（四）冻结管应采用无缝钢管焊接或螺纹连接，冻结管下入钻孔后应进行试漏，发现异常时，必须及时处理。\r\n（五）开始冻结后，必须经常观察水文观测孔的水位变化。只有在水文孔冒水7天、水量正常，确认冻结壁已交圈后，方可进行试挖。冻结和开凿过程中，要经常检查盐水温度和流量、井帮温度和位移，以及井帮和工作面渗漏盐水等情况。检查应有详细记录，发现异常，必须及时处理。\r\n（六）开凿表土层冻结段时，可以采用爆破作业，但必须制定安全技术措施。\r\n（七）掘进施工过程中，必须有防止冻结壁变形、片帮、掉石、断管等安全措施。\r\n（八）生根壁座应设在含水较少的稳定坚硬的基岩中。\r\n（九）只有在永久井壁施工全部完成后，方可停止冻结。\r\n（十）梁窝的设计和施工必须有防止漏水的措施。\r\n（十一）不论冻结管能否提拔回收，对全孔必须及时用水泥砂浆或混凝土全部充满填实。\r\n冻结站必须用不燃性材料建筑，并应有通风装置。应经常测定站内空气中氨气，氨的浓度不得超过0.004％。站内严禁烟火，并必须备有急救和消防器材。\r\n氨瓶和氨罐必须经过试验，合格后方准使用；在运输、使用和存放期间，应有安全措施。\r\n',4,'admin','2012-06-13 17:09:06','2012-09-01 17:38:48','admin'),(44,'第三十一条',37,'0001-0001-0002-0001-0002-0007',0,1,NULL,'立井井筒穿过含水岩层或破碎带，采用地面或工作面预注浆法进行堵水或加固时，应遵守下列规定：\r\n（一）注浆施工前，必须编制注浆工程设计。\r\n（二）注浆段长度必须大于注浆的含水岩层的厚度，并深入不透水岩层或硬岩层5～10m。井底的设计位置在注浆的含水岩层内时，注浆深度必须大于井深10m。\r\n（三）地面预注浆的钻孔，每钻进40m必须测斜1次，钻孔偏斜率不得超过0.5％。\r\n（四）注浆前，必须进行注浆泵和输送管路系统的耐压试验。试验压力必须达到最大注浆压力的1.5倍，试验时间不得小于15min，无异常情况后，方可使用。\r\n（五）注浆过程中，注浆压力突然上升时，必须停止注浆泵运转，卸压后方可处理。\r\n（六）每次注浆后，应至少停歇30min，方可提拔止浆塞，以防高压浆顶出钻杆。\r\n（七）冬季注浆施工时，注浆站和地面输浆管路，必须采取防冻措施。\r\n（八）井筒工作面预注浆前，在注浆的含水岩层上方，必须按设计要求设置止浆岩帽或混凝土止浆垫。含水岩层厚度大，需采用分段注浆和掘砌时，对每一注浆段，必须按设计要求设置止浆岩帽或混凝土止浆垫。岩帽和混凝土止浆垫的结构形式和厚度应根据最大注浆压力、岩石性质和工作条件确定。混凝土止浆垫由井壁支承时，应对井壁强度进行验算。\r\n（九）孔口管必须按设计孔位埋设牢固，并安设高压阀门。注浆前，必须对止浆垫和孔口管进行耐压试验，试验压力必须大于注浆压力1MPa。\r\n（十）钻注浆孔时，钻机必须安设牢固。取芯钻进时，应使用能够防止顶出钻具的钻头；无芯钻进时，可使用三翼钻头，以防承压水顶出钻具。\r\n（十一）井内应设吊泵，及时排除井底积水。当钻进注浆孔时，如井筒涌水量接近吊泵额定排水能力，必须停止钻进，提取钻具，关闭高压阀门，及时注浆。\r\n（十二）注浆站设在地面时，井上、下必须有可靠的通信联系。\r\n（十三）制浆和注浆的工作人员，应佩戴防护眼镜和口罩，水泥搅拌房内应采取防尘措施。\r\n（十四）注浆结束后，必须检查注浆效果，合格后，方可开凿井筒。\r\n',4,'admin','2012-06-13 17:09:06','2012-09-01 17:38:48','admin'),(45,'第三十二条',37,'0001-0001-0002-0001-0002-0008',0,1,NULL,'立井井筒漏水量每小时超过6m3或漏水中含砂，采用井壁注浆堵水时，必须编制施工组织设计并遵守下列规定：\r\n（一）井壁必须有承受最大注浆压力的强度。\r\n（二）井筒在流砂层部位时，注浆孔深度必须小于井壁厚度200mm。井筒采用双层井壁支护时，注浆孔应穿过内壁进入外壁100mm。当井壁破裂必须采用破壁注浆时，必须制定专门措施。\r\n（三）注浆管必须固结在井壁中，并装有阀门。钻孔可能发生涌砂时，应采取套管法或其他安全措施。采用套管法注浆时，必须对套管的固结强度进行耐压试验，只有达到注浆终压力后，方可使用。\r\n（四）在罐笼顶上进行钻孔注浆作业时，必须安设工作盘和注浆管路安全阀，作业人员必须佩带保险带，并在井口设专职值班人员。\r\n（五）井上、下都必须有可靠的通信设施，升降注浆作业吊盘或工作盘时，必须得到值班人员的允许。\r\n（六）井筒内进行钻孔注浆作业时，井底不得有人。注浆中必须观察井壁，发现问题必须停止作业，及时处理。\r\n（七）钻孔时应经常检查孔内涌水量和含砂量。涌水量较大或涌水中含砂时，必须停止钻进，及时注浆；钻孔中无水时，必须及时严密封孔。\r\n（八）注浆管露出井壁的管端与提升容器之间的间隙，必须符合本规程第三百八十七条的有关规定。\r\n',4,'admin','2012-06-13 17:09:06','2012-09-01 17:38:48','admin'),(46,'第三十三条',37,'0001-0001-0002-0001-0002-0009',0,1,NULL,'开凿或延深立井的施工组织设计中，必须有吊盘、保护盘以及凿岩、抓岩、出矸等设备的设置、运行、维修的安全措施。',4,'admin','2012-06-13 17:09:06','2012-09-01 17:38:48','admin'),(47,'第三十四条',37,'0001-0001-0002-0001-0002-0010',0,1,NULL,'开凿或延深立井时，井筒内必须设有在提升设备发生故障时专供人员出井的安全设施。',4,'admin','2012-06-13 17:09:06','2012-09-01 17:38:48','admin'),(49,'第三十六条',37,'0001-0001-0002-0001-0002-0012',0,1,NULL,'开凿或延深立井时，井筒内每个工作地点必须设置独立的信号装置。掘进和砌壁平行作业时，从吊盘和掘进工作面所发出的信号，必须有明显的区别。\r\n井内和井口的信号必须由专职信号工发送。除紧急停车外，严禁不经过井口信号工直接从井内向绞车房发送信号。井内作业人员必须熟悉并会发送信号。\r\n井口、井底信号工应在吊罐提起适当高度后，先发暂停信号，进行稳罐；待吊罐稳定，清理罐底附着物后，才能发出下降或提升信号。信号工必须目接、目送吊罐安全通过责任段。\r\n',4,'admin','2012-06-13 17:09:06','2012-09-01 17:38:48','admin'),(50,'第三十七条',37,'0001-0001-0002-0001-0002-0013',0,1,NULL,'安装井架或井架上的设备时必须盖严井口。装备井筒与安装井架及井架上的设备平行作业时，井口掩盖装置必须坚固可靠，能承受井架上坠落物的冲击。',4,'admin','2012-06-13 17:09:06','2012-09-01 17:38:48','admin'),(51,'第三十八条',37,'0001-0001-0002-0001-0002-0014',0,1,NULL,'延深立井井筒时，必须用坚固的保险盘或留保护岩柱与上部生产水平隔开。只有在井筒装备完毕、井筒与井底车场连接处的开凿和支护完成，制定安全措施后，方可拆除保险盘或掘凿保护岩柱。',4,'admin','2012-06-13 17:09:06','2012-09-01 17:38:48','admin'),(52,'第三十九条',37,'0001-0001-0002-0001-0002-0015',0,1,NULL,'采用反向凿井法掘凿暗立井或竖煤仓应遵守下列规定：\r\n（一）用木垛盘支护时，必须及时支护。爆破前最末一道木垛盘与工作面的距离不得超过1.6m。木垛盘的基墩必须牢固可靠。行人、运料眼与溜矸眼之间，必须用木板隔开。在人行眼内必须有木梯和护头板，护头板的间距最大不得超过3m，护头板上的矸石必须及时清理。爆破前，必须将人行眼和运料眼盖严。爆破后，首先通风，吹散炮烟，之后方可进入检查，检查人员不得少于2人。经过检查，确认通风、信号正常，人行间、隔板、护头板、顶板、井帮等无危险情况后，方可进行作业。\r\n（二）采用吊罐法施工时，绳孔偏斜率不得超过0.5％，绞车房与出矸水平之间，必须装设2套信号装置，其中1套必须设在吊罐内。爆破前必须摘下吊罐，放置在巷道内安全地点，将提升钢丝绳提到安全位置。爆破后必须指定专人检查提升钢丝绳和吊具，如有损坏，修复后方可使用。吊罐内有人作业时，严禁在吊罐下方进行工作或通行。\r\n（三）采用反井钻机施工时，在扩孔期间，严禁人员在孔的下方停留、通行或观察。扩孔完毕，必须在孔的外围设置栅栏，防止人员进入。\r\n（四）扩井时，必须有防止人员坠落的安全措施。爆破前必须拆除爆破孔底以下0.3m范围内的木垛盘。\r\n溜矸眼内的矸石必须经常放出，防止卡眼，但不得放空。严禁站在溜矸眼的矸石上作业。\r\n',4,'admin','2012-06-13 17:09:06','2012-09-01 17:38:48','admin'),(53,'第四十条',37,'0001-0001-0002-0001-0002-0016',0,1,NULL,'冬季或用冻结法开凿立井时，必须有防冻、清除冰凌的措施。',4,'admin','2012-06-13 17:09:06','2012-09-01 17:38:48','admin'),(54,'第四十一条',37,'0001-0001-0002-0001-0002-0017',0,1,NULL,'掘进工作面严禁空顶作业。靠近掘进工作面10m内的支护，在爆破前必须加固。爆破崩倒、崩坏的支架必须先行修复，之后方可进入工作面作业。修复支架时必须先检查顶、帮，并由外向里逐架进行。\r\n在松软的煤、岩层或流砂性地层中及地质破碎带掘进巷道时，必须采取前探支护或其他措施。\r\n在坚硬和稳定的煤、岩层中，确定巷道不设支护时，必须制定安全措施。\r\n',4,'admin','2012-06-13 17:09:06','2012-09-01 17:38:48','admin'),(55,'第四十二条',37,'0001-0001-0002-0001-0002-0018',0,1,NULL,'支架间应设牢固的撑木或拉杆。可缩性金属支架应用金属支拉杆，并用机械或力矩扳手拧紧卡缆。支架与顶帮之间的空隙必须塞紧、背实。巷道砌碹时，碹体与顶帮之间必须用不燃物充满填实；巷道冒顶空顶部分，可用支护材料接顶，但在碹拱上部必须充填不燃物垫层，其厚度不得小于0.5m。',4,'admin','2012-06-13 17:09:06','2012-09-01 17:38:48','admin'),(56,'第四十三条',37,'0001-0001-0002-0001-0002-0019',0,1,NULL,'更换巷道支护时，在拆除原有支护前，应先加固临近支护，拆除原有支护后，必须及时除掉顶帮活矸和架设永久支护，必要时还应采取临时支护措施。在倾斜巷道中，必须有防止矸石、物料滚落和支架歪倒的安全措施。',4,'admin','2012-06-13 17:09:06','2012-09-01 17:38:48','admin'),(57,'第四十四条',37,'0001-0001-0002-0001-0002-0020',0,1,NULL,'采用锚杆、锚喷等支护形式时，应遵守下列规定：\r\n（一）锚杆、锚喷等支护的端头与掘进工作面的距离，锚杆的形式、规格、安装角度，混凝土标号、喷体厚度，挂网所采用金属网的规格以及围岩涌水的处理等，必须在施工组织设计或作业规程中规定。\r\n（二）采用钻爆法掘进的岩石巷道，必须采用光面爆破。\r\n（三）打锚杆眼前，必须首先敲帮问顶，将活矸处理掉，在确保安全的条件下，方可作业。\r\n（四）使用锚固剂固定锚杆时，应将孔壁冲洗干净，砂浆锚杆必须灌满填实。\r\n（五）软岩使用锚杆支护时，必须全长锚固。\r\n（六）采用人工上料喷射机喷射混凝土、砂浆时，必须采用潮料，并使用除尘机对上料口、余气口除尘。喷射前，必须冲洗岩帮。喷射后应有养护措施。作业人员必须佩戴劳动保护用品。\r\n（七）锚杆必须按规定做拉力试验。煤巷还必须进行顶板离层监测，并用记录牌板显示。对喷体必须做厚度和强度检查，并有检查和试验记录。在井下做锚固力试验时，必须有安全措施。\r\n（八）锚杆必须用机械或力矩扳手拧紧，确保锚杆的托板紧贴巷壁。\r\n（九）岩帮的涌水地点，必须处理。\r\n（十）处理堵塞的喷射管路时，喷枪口的前方及其附近严禁有其他人员。\r\n',4,'admin','2012-06-13 17:09:06','2012-09-01 17:38:48','admin'),(59,'第二章',5,'0001-0001-0002-0002',0,1,1,'111',8,'admin','2012-09-01 16:26:34','2012-09-01 17:34:26','admin'),(60,'第一节1',59,'0001-0001-0002-0002-0001',0,1,1,'111',3,'admin','2012-09-01 16:26:50','2012-09-01 17:47:48','admin'),(61,'第三章',5,'0001-0001-0002-0003',0,1,1,'',7,'admin','2012-09-01 17:33:23','2012-09-01 17:34:26','admin'),(62,'第四章',5,'0001-0001-0002-0004',0,1,1,'',6,'admin','2012-09-01 17:33:33','2012-09-01 17:34:26','admin'),(63,'第五章',5,'0001-0001-0002-0005',0,1,1,'',5,'admin','2012-09-01 17:33:43','2012-09-01 17:34:26','admin'),(64,'第六章',5,'0001-0001-0002-0006',0,1,1,'',5,'admin','2012-09-01 17:33:50','2012-09-01 17:34:26','admin'),(65,'第七章',5,'0001-0001-0002-0007',0,1,1,'',3,'admin','2012-09-01 17:34:00','2012-09-01 17:34:26','admin'),(66,'第八章',5,'0001-0001-0002-0008',0,1,1,'',2,'admin','2012-09-01 17:34:13','2012-09-01 17:34:26','admin'),(67,'第九章',5,'0001-0001-0002-0009',0,1,1,'',1,'admin','2012-09-01 17:34:19','2012-09-01 17:34:26','admin'),(68,'第十章',5,'0001-0001-0002-00010',0,1,1,'',0,'admin','2012-09-01 17:34:26',NULL,NULL),(69,'第一节',68,'0001-0001-0002-00010-0001',0,1,1,'',4,'admin','2012-09-01 17:34:56','2012-09-01 18:40:05','admin'),(70,'第四百九十二条',69,'0001-0001-0002-00010-0001-0001',0,1,1,'矿山救护队是处理矿井火、瓦斯、煤尘、水、顶板等灾害的专业队伍；矿山救护队员是煤矿井下一线特种作业人员。',8,'admin','2012-09-01 17:35:19','2012-09-10 23:36:40','admin'),(71,'第三节',25,'0001-0001-0002-0001-0003',0,1,1,'',5,'admin','2012-09-01 17:36:21','2012-09-01 17:36:54','admin'),(72,'第四节',25,'0001-0001-0002-0001-0004',0,1,1,'',4,'admin','2012-09-01 17:36:29','2012-09-01 17:36:54','admin'),(73,'第五节',25,'0001-0001-0002-0001-0005',0,1,1,'',3,'admin','2012-09-01 17:36:36','2012-09-01 17:36:54','admin'),(74,'第六节',25,'0001-0001-0002-0001-0006',0,1,1,'',2,'admin','2012-09-01 17:36:42','2012-09-01 17:36:54','admin'),(75,'第七节',25,'0001-0001-0002-0001-0007',0,1,1,'',1,'admin','2012-09-01 17:36:50','2012-09-01 17:36:54','admin'),(76,'第八节',25,'0001-0001-0002-0001-0008',0,1,1,'',0,'admin','2012-09-01 17:36:54',NULL,NULL),(77,'第四十五条',37,'0001-0001-0002-0001-0002-0020',0,1,1,'掘进巷道在揭露老空前，必须制定探查老空的安全措施，包括接近老空时必须预留的煤（岩）柱厚度和探明水、火、瓦斯等内容。必须根据探明的情况采取措施，进行处理。\r\n在揭露老空时，必须将人员撤至安全地点。只有经过检查，证明老空内的水、瓦斯和其他有害气体等无危险后，方可恢复工作。\r\n',2,'admin','2012-09-01 17:38:16','2012-09-01 17:38:48','admin'),(78,'第四十六条',37,'0001-0001-0002-0001-0002-0021',0,1,1,'开凿或延深斜井、下山时，必须在斜井、下山的上口设置防止跑车装置，在掘进工作面的上方设置坚固的跑车防护装置。跑车防护装置与掘进工作面的距离必须在施工组织设计或作业规程中规定。\r\n斜井（巷）施工期间兼作行人道时，必须每隔40m设置躲避硐并设红灯。设有躲避硐的一侧必须有畅通的人行道。上下人员必须走人行道。行车时红灯亮，行人立即进入躲避硐；红灯熄灭后，方可行走。\r\n',1,'admin','2012-09-01 17:38:33','2012-09-01 17:38:48','admin'),(79,'第四十七条',37,'0001-0001-0002-0001-0002-0022',0,1,1,'由下向上掘进25°以上的倾斜巷道时，必须将溜煤（矸）道与人行道分开，防止煤（矸）滑落伤人。人行道应设扶手、梯子和信号装置。斜巷与上部巷道贯通时，必须有安全措施。',0,'admin','2012-09-01 17:38:48',NULL,NULL),(80,'第四十八条',71,'0001-0001-0002-0001-0003-0001',0,1,1,'采区开采前必须按照生产布局合理的要求编制采区设计，并严格按照采区设计组织施工。\r\n一个采区内同一煤层的一翼最多只能布置1个回采工作面和2个掘进工作面同时作业。\r\n一个采区内同一煤层双翼开采或多煤层开采的，该采区最多只能布置2个回采工作面和4个掘进工作面同时作业。\r\n严禁在采煤工作面范围内再布置另一采煤工作面同时作业。\r\n采掘过程中严禁任意扩大和缩小设计规定的煤柱。采空区内不得遗留未经设计规定的煤柱。\r\n严禁破坏工业场地、矿界、防水和井巷等的安全煤柱\r\n突出矿井、高瓦斯矿井、低瓦斯矿井高瓦斯区域的采煤工作面，不得采用前进式采煤方法。\r\n',17,'admin','2012-09-01 17:39:14','2012-09-01 17:43:46','admin'),(81,'第四十九条',71,'0001-0001-0002-0001-0003-0002',0,1,1,'采煤工作面回采前必须编制作业规程。情况发生变化时，必须及时修改作业规程或补充安全措施。',16,'admin','2012-09-01 17:39:28','2012-09-01 17:43:46','admin'),(82,'第五十条',71,'0001-0001-0002-0001-0003-0003',0,1,1,'采煤工作面必须保持至少2个畅通的安全出口，一个通到回风巷道，另一个通到进风巷道。\r\n开采三角煤、残留煤柱，不能保持2个安全出口时，必须制订安全措施，报企业主要负责人审批。\r\n采煤工作面所有安全出口与巷道连接处超前压力影响范围内必须加强支护，且加强支护的巷道长度不得小于20m；综合机械化采煤工作面，此范围内的巷道高度不得低于1.8m，其他采煤工作面，此范围内的巷道高度不得低于1.6m。安全出口和与之相连接的巷道必须设专人维护，发生支架断梁折柱、巷道底鼓变形时，必须及时更换、清挖。\r\n',15,'admin','2012-09-01 17:39:44','2012-09-01 17:43:46','admin'),(83,'第五十一条',71,'0001-0001-0002-0001-0003-0004',0,1,1,'采煤工作面的伞檐不得超过作业规程的规定，不得任意丢失顶煤和底煤。工作面的浮煤应清理干净。支架、输送机和充填垛都应保持直线。',14,'admin','2012-09-01 17:39:59','2012-09-01 17:43:46','admin'),(84,'第五十二条',71,'0001-0001-0002-0001-0003-0005',0,1,1,'台阶采煤工作面必须设置安全脚手板、护身板和溜煤板。倒台阶采煤工作面，还必须在台阶的底脚加设保护台板。\r\n阶檐的宽度、台阶面长度和下部超前小眼的个数，必须在作业规程中规定。\r\n',13,'admin','2012-09-01 17:40:14','2012-09-01 17:43:46','admin'),(85,'第五十三条',71,'0001-0001-0002-0001-0003-0006',0,1,1,'采煤工作面必须经常存有一定数量的备用支护材料。使用摩擦式金属支柱或单体液压支柱的工作面，必须备有坑木，其数量、规格、存放地点和管理方法必须在作业规程中规定。\r\n采煤工作面严禁使用折损的坑木、损坏的金属顶梁、失效的摩擦式金属支柱和失效的单体液压支柱。\r\n在同一采煤工作面中，不得使用不同类型和不同性能的支柱。在地质条件复杂的采煤工作面中必须使用不同类型的支柱时，必须制定安全措施。\r\n摩擦式金属支柱和单体液压支柱入井前必须逐根进行压力试验。\r\n对摩擦式金属支柱、金属顶梁和单体液压支柱，在采煤工作面回采结束后或使用时间超过8个月后，必须进行检修。检修好的支柱，还必须进行压力试验，合格后方可使用。\r\n',12,'admin','2012-09-01 17:40:31','2012-09-01 17:43:46','admin'),(86,'第五十四条',71,'0001-0001-0002-0001-0003-0007',0,1,1,'采煤工作面必须按作业规程的规定及时支护，严禁空顶作业。所有支架必须架设牢固，并有防倒柱措施。严禁在浮煤或浮矸上架设支架。使用摩擦式金属支柱时，必须使用液压升柱器架设，初撑力不得小于50kN；单体液压支柱的初撑力，柱径为100mm的不得小于90kN，柱径为80mm的不得小于60kN。对于软岩条件下初撑力确实达不到要求的，在制定措施、满足安全的条件下，必须经企业技术负责人审批。严禁在控顶区域内提前摘柱。碰倒或损坏、失效的支柱，必须立即恢复或更换。移动输送机机头、机尾需要拆除附近的支架时，必须先架好临时支架。\r\n采煤工作面遇顶底板松软或破碎、过断层、过老空、过煤柱或冒顶区以及托伪顶开采时，必须制定安全措施。\r\n',11,'admin','2012-09-01 17:40:49','2012-09-01 17:43:46','admin'),(87,'第五十五条',71,'0001-0001-0002-0001-0003-0008',0,1,1,'严格执行敲帮问顶制度。\r\n开工前，班组长必须对工作面安全情况进行全面检查，确认无危险后，方准人员进入工作面。\r\n',10,'admin','2012-09-01 17:41:07','2012-09-01 17:43:46','admin'),(88,'第五十六条',71,'0001-0001-0002-0001-0003-0009',0,1,1,'采煤工作面必须及时回柱放顶或充填，控顶距离超过作业规程规定时，禁止采煤。用垮落法控制顶板，回柱后顶板不垮落、悬顶距离超过作业规程的规定时，必须停止采煤，采取人工强制放顶或其他措施进行处理。',9,'admin','2012-09-01 17:41:19','2012-09-01 17:43:46','admin'),(89,'第五十七条',71,'0001-0001-0002-0001-0003-00010',0,1,1,'用垮落法控制顶板时，回柱放顶的方法和安全措施，放顶与爆破、机械落煤等工序平行作业的安全距离，放顶区内支架、木柱、木垛的回收方法，必须在作业规程中明确规定。\r\n采煤工作面初次放顶及收尾时，必须制定安全措施。\r\n放顶人员必须站在支架完整，无崩绳、崩柱、甩钩、断绳抽人等危险的安全地点工作。回柱放顶前，必须对放顶的安全工作进行全面检查，清理好退路。回柱放顶时，必须指定有经验的人员观察顶板。\r\n',8,'admin','2012-09-01 17:41:33','2012-09-01 17:43:46','admin'),(90,'第五十八条',71,'0001-0001-0002-0001-0003-0011',0,1,1,'采煤工作面采用密集支柱切顶时，两段密集支柱之间必须留有宽0.5m以上的出口，出口间的距离和新密集支柱超前的距离必须在作业规程中明确规定。采煤工作面采用无密集支柱切顶时，必须有防止工作面冒顶和矸石窜入工作面的措施。',7,'admin','2012-09-01 17:42:05','2012-09-01 17:43:46','admin'),(91,'第五十九条',71,'0001-0001-0002-0001-0003-0012',0,1,1,'采用人工假顶分层垮落法开采的采煤工作面，人工假顶必须铺设好，搭接严密；采用金属网或矿用塑料网假顶时，必须把网连结好。\r\n确认垮落的顶板岩石能够胶结形成再生顶板时，可不铺设人工假顶。\r\n采用分层垮落法开采时，必须向采空区注水或注浆。注水或注浆的具体要求，应在作业规程中明确规定。\r\n',6,'admin','2012-09-01 17:42:21','2012-09-01 17:43:46','admin'),(92,'第六十条',71,'0001-0001-0002-0001-0003-0013',0,1,1,'用水砂充填法控制顶板时，采空区和三角点必须充填满。充填地点的下方，严禁人员通行或停留。注砂井和充填地点之间，应保持用电话联络，联络中断时，必须立即停止注砂。\r\n清理因跑砂堵塞的倾斜井巷前，必须制定安全措施。\r\n',5,'admin','2012-09-01 17:42:36','2012-09-01 17:43:46','admin'),(93,'第六十一条',71,'0001-0001-0002-0001-0003-0014',0,1,1,'用带状充填法控制顶板时，必须在垒砌石垛带之前清扫底板上的浮煤，石垛带必须砌接到顶，顶板下和垛墙上的缝隙应用石块塞紧。需从2个石垛中间采取矸石时，必须首先将顶板的活矸用长柄工具处理掉，设置临时支护，并与采煤工作面相接，采矸人员应在临时支护保护下进行工作，并有人观察顶板。',4,'admin','2012-09-01 17:42:50','2012-09-01 17:43:46','admin'),(94,'第六十二条',71,'0001-0001-0002-0001-0003-0015',0,1,1,'开采近距离煤层，上一煤层采用刀柱法、条带法或带状充填法控制顶板，下一煤层采用垮落法控制顶板时，必须制定控制顶板的安全措施。',3,'admin','2012-09-01 17:43:02','2012-09-01 17:43:46','admin'),(95,'第六十三条',71,'0001-0001-0002-0001-0003-0016',0,1,1,'长壁式采煤工作面分上下面同时回采时，上下面的错距应根据煤层倾角、矿山压力、支护形式、通风、瓦斯、自然发火、涌水等情况，在作业规程中明确规定。',2,'admin','2012-09-01 17:43:14','2012-09-01 17:43:46','admin'),(96,'第六十四条',71,'0001-0001-0002-0001-0003-0017',0,1,1,'采用倾斜分层垮落法回采时，下一分层的采煤工作必须在上一分层顶板垮落的稳定区域下面进行。上下分层的回采间隔时间不应过长，以防假顶腐朽。\r\n采用水平分层垮落法回采时，上一分层的采煤工作面超前下一分层采煤工作面的距离，应在作业规程中规定。\r\n',1,'admin','2012-09-01 17:43:30','2012-09-01 17:43:46','admin'),(97,'第六十五条',71,'0001-0001-0002-0001-0003-0018',0,1,1,'采用掩护支架开采急倾斜煤层时，支架的角度、结构，支架垫层数和厚度以及点柱的支设角度、排列方式和密度，必须在作业规程中规定。\r\n生产中遇有断梁、支架悬空、窜矸等情况时，必须及时处理。支架沿走向弯曲、歪斜及角度超过作业规程规定时，在下一次放架过程中，必须进行调整。应经常检查支架上的螺栓和附件，如有松动，必须及时拧紧。\r\n正倾斜掩护支架的每个回采带的两端，必须设置人行眼，并用木板隔出溜煤眼。伪倾斜掩护支架工作面上下2个出口的要求和工作面的伪倾角，超前溜煤眼的规格、间距和施工方式必须在作业规程中规定。\r\n掩护支架接近平巷时，应缩短每次下放支架的距离，并减少同时爆破的炮眼数目和装药量。掩护支架过平巷时，应加强溜煤眼与平巷连接处的支护或架设木垛。\r\n',1,'admin','2012-09-01 17:43:46','2012-09-01 17:44:07','admin'),(98,'第二节',59,'0001-0001-0002-0002-0002',0,1,1,'',1,'admin','2012-09-01 17:47:42','2012-09-01 17:47:48','admin'),(99,'第三节',59,'0001-0001-0002-0002-0003',0,1,1,'',0,'admin','2012-09-01 17:47:48',NULL,NULL),(100,'第一章',7,'0001-0001-0004-0001',0,1,1,'',1,'admin','2012-09-01 17:52:32','2012-09-01 17:52:39','admin'),(101,'第二章',7,'0001-0001-0004-0002',0,1,1,'',0,'admin','2012-09-01 17:52:39',NULL,NULL),(102,'第一章',6,'0001-0001-0003-0001',0,1,1,'',7,'admin','2012-09-01 17:54:54','2012-09-01 17:55:37','admin'),(103,'第二章',6,'0001-0001-0003-0002',0,1,1,'',6,'admin','2012-09-01 17:55:00','2012-09-01 17:55:37','admin'),(104,'第三章',6,'0001-0001-0003-0003',0,1,1,'',5,'admin','2012-09-01 17:55:07','2012-09-01 17:55:37','admin'),(105,'第四章',6,'0001-0001-0003-0004',0,1,1,'',4,'admin','2012-09-01 17:55:12','2012-09-01 17:55:37','admin'),(106,'第五章',6,'0001-0001-0003-0005',0,1,1,'',3,'admin','2012-09-01 17:55:18','2012-09-01 17:55:37','admin'),(107,'第六章',6,'0001-0001-0003-0006',0,1,1,'',2,'admin','2012-09-01 17:55:24','2012-09-01 17:55:37','admin'),(108,'第七章',6,'0001-0001-0003-0007',0,1,1,'',1,'admin','2012-09-01 17:55:31','2012-09-01 17:55:37','admin'),(109,'第八章',6,'0001-0001-0003-0008',0,1,1,'',0,'admin','2012-09-01 17:55:37',NULL,NULL),(110,'第七百三十八条',100,'0001-0001-0004-0001-0001',0,1,1,'煤矿企业必须加强职业危害的防治与管理，做好作业场所的职业卫生和劳动保护工作。采取有效措施控制尘、毒危害，保证作业场所符合国家职业卫生标准。',5,'admin','2012-09-01 17:57:49','2012-09-01 17:59:25','admin'),(111,'第七百三十九条',100,'0001-0001-0004-0001-0002',0,1,1,'作业场所空气中粉尘（总粉尘、呼吸性粉尘）浓度应符合表26要求。',4,'admin','2012-09-01 17:58:14','2012-09-01 17:59:25','admin'),(112,'第七百四十条',100,'0001-0001-0004-0001-0003',0,1,1,'煤矿企业必须按国家规定对生产性粉尘进行监测，并遵守下列规定：\r\n（一）总粉尘：\r\n1．作业场所的粉尘浓度，井下每月测定2次，地面及露天煤矿每月测定1次；\r\n2．粉尘分散度，每6个月测定1次。\r\n（二）呼吸性粉尘：\r\n1．工班个体呼吸性粉尘监测，采、掘（剥）工作面每3个月测定1次，其他工作面或作业场所每6个月测定1次。每个采样工种分2个班次连续采样，1个班次内至少采集2个有效样品，先后采集的有效样品不得少于4个；\r\n2．定点呼吸性粉尘监测每月测定1次。\r\n（三）粉尘中游离SiO2含量，每6个月测定1次，在变更工作面时也必须测定1次；各接尘作业场所每次测定的有效样品数不得少于3个。\r\n（四）开采深度大于200m的露天煤矿，在气压较低的季节应适当增加测定次数。\r\n',3,'admin','2012-09-01 17:58:38','2012-09-01 17:59:25','admin'),(113,'第七百四十一条',100,'0001-0001-0004-0001-0004',0,1,1,'作业场所的噪声，不应超过85dB（A）。大于85dB（A）时，需配备个人防护用品；大于或等于90dB（A）时，还应采取降低作业场所噪声的措施。',2,'admin','2012-09-01 17:58:50','2012-09-01 17:59:25','admin'),(114,'第七百四十二条',100,'0001-0001-0004-0001-0005',0,1,1,'矿区水源和供水工程应保证矿区工业用水量，其水质应符合国家卫生标准。',1,'admin','2012-09-01 17:59:07','2012-09-01 17:59:25','admin'),(115,'第七百四十三条',100,'0001-0001-0004-0001-0006',0,1,1,'煤矿企业必须按国家规定对生产性毒物、有害物理因素等进行定期监测，并遵守下列规定：\r\n（一）三硝基甲苯（生产车间）作业点，每月测定1次。\r\n（二）铅、苯、汞及其他有毒物质，每3个月测定1次，已达到职业卫生标准的可6个月测定1次。\r\n（三）噪声、放射线及其他物理因素每年至少测定1次。\r\n监测结果必须建档，并报有关单位。\r\n',0,'admin','2012-09-01 17:59:25',NULL,NULL),(116,'第七百四十四条',101,'0001-0001-0004-0002-0001',0,1,1,'煤矿企业必须按国家有关法律、法规的规定，对新入矿工人进行职业健康检查，并建立健康档案；对接尘工人的职业健康检查必须拍照胸大片。\r\n煤矿企业应按照国家法律、法规和卫生行政主管部门的规定定期对接触粉尘、毒物及有害物理因素等的作业人员进行职业健康检查。对检查出的职业病患者，煤矿企业必须按国家规定及时给以治疗、疗养和调离有害作业岗位，并做好健康监护及职业病报告工作。\r\n查体时间间隔必须符合下列要求：\r\n（一）对在岗接触粉尘作业工人，岩石掘进工种每2～3年拍片检查1次；混合工种每3～4年拍片检查1次；纯采煤工种每4～5年拍片检查1次。\r\n（二）对离岗工人必须进行离岗的职业性健康检查。\r\n（三）对接触毒物、放射线的人员每年检查1次。\r\n职业性健康检查、职业病诊断、职业病治疗应由取得相应资格的职业卫生机构承担。',6,'admin','2012-09-01 17:59:45','2012-09-01 18:01:07','admin'),(117,'第七百四十五条',101,'0001-0001-0004-0002-0002',0,1,1,'Ⅰ期尘肺患者每年复查1次。\r\n疑似尘肺患者（0+）：岩石掘进工种每年拍片复查1次、混合工种每2年拍片复查1次、纯采煤工种每3年拍片复查1次。\r\n',5,'admin','2012-09-01 18:00:00','2012-09-01 18:01:07','admin'),(118,'第七百四十六条',101,'0001-0001-0004-0002-0003',0,1,1,'有下列病症之一的，不得从事接尘作业：\r\n（一）活动性肺结核病及肺外结核病。\r\n（二）严重的上呼吸道或支气管疾病。\r\n（三）显著影响肺功能的肺脏或胸膜病变。\r\n（四）心、血管器质性疾病。\r\n（五）经医疗鉴定，不适于从事粉尘作业的其他疾病。\r\n',4,'admin','2012-09-01 18:00:13','2012-09-01 18:01:07','admin'),(119,'第七百四十七条',101,'0001-0001-0004-0002-0004',0,1,1,'有下列病症之一的，不得从事井下工作：\r\n（一）本规程第七百四十六条所列病症之一的。\r\n（二）风湿病（反复活动）。\r\n（三）严重的皮肤病。\r\n（四）经医疗鉴定，不适于从事井下工作的其他疾病。',3,'admin','2012-09-01 18:00:27','2012-09-01 18:01:07','admin'),(120,'第七百四十八条',101,'0001-0001-0004-0002-0005',0,1,1,'癫痫病和精神分裂症患者严禁从事煤矿生产工作。',2,'admin','2012-09-01 18:00:39','2012-09-01 18:01:07','admin'),(121,'第七百四十九条',101,'0001-0001-0004-0002-0006',0,1,1,'患有高血压、心脏病、深度近视等病症以及其他不适应高空（2m以上）作业者，不得从事高空作业。',1,'admin','2012-09-01 18:00:51','2012-09-01 18:01:07','admin'),(122,'第七百五十条',101,'0001-0001-0004-0002-0007',0,1,1,'粉尘、毒物及有害物理因素超过国家职业卫生标准的作业场所，除采取防治措施外，作业人员必须佩戴防尘或防毒等个体劳动防护用品。',0,'admin','2012-09-01 18:01:07',NULL,NULL),(123,'第一节',103,'0001-0001-0003-0002-0001',0,1,1,'',3,'admin','2012-09-01 18:03:04','2012-09-01 18:03:30','admin'),(124,'第二节',103,'0001-0001-0003-0002-0002',0,1,1,'',2,'admin','2012-09-01 18:03:18','2012-09-01 18:03:30','admin'),(125,'第三节',103,'0001-0001-0003-0002-0003',0,1,1,'',1,'admin','2012-09-01 18:03:23','2012-09-01 18:03:30','admin'),(126,'第四节',103,'0001-0001-0003-0002-0004',0,1,1,'',0,'admin','2012-09-01 18:03:30',NULL,NULL),(127,'第一节',104,'0001-0001-0003-0003-0001',0,1,1,'',2,'admin','2012-09-01 18:03:42','2012-09-01 18:03:55','admin'),(128,'第二节',104,'0001-0001-0003-0003-0002',0,1,1,'',1,'admin','2012-09-01 18:03:50','2012-09-01 18:03:55','admin'),(129,'第三节',104,'0001-0001-0003-0003-0003',0,1,1,'',0,'admin','2012-09-01 18:03:55',NULL,NULL),(130,'第一节',107,'0001-0001-0003-0006-0001',0,1,1,'',1,'admin','2012-09-01 18:04:08','2012-09-01 18:04:14','admin'),(131,'第二节',107,'0001-0001-0003-0006-0002',0,1,1,'',0,'admin','2012-09-01 18:04:14',NULL,NULL),(132,'第一节',108,'0001-0001-0003-0007-0001',0,1,1,'',7,'admin','2012-09-01 18:05:05','2012-09-01 18:05:39','admin'),(133,'第二节',108,'0001-0001-0003-0007-0002',0,1,1,'',6,'admin','2012-09-01 18:05:09','2012-09-01 18:05:39','admin'),(134,'第三节',108,'0001-0001-0003-0007-0003',0,1,1,'',5,'admin','2012-09-01 18:05:15','2012-09-01 18:05:39','admin'),(135,'第四节',108,'0001-0001-0003-0007-0004',0,1,1,'',4,'admin','2012-09-01 18:05:19','2012-09-01 18:05:39','admin'),(136,'第五节',108,'0001-0001-0003-0007-0005',0,1,1,'',3,'admin','2012-09-01 18:05:23','2012-09-01 18:05:39','admin')